RETURN "<div style='color: " & $textColour & "; cursor: pointer; padding-top: 8px; font-size: 11pt' data-clickid=""" & $reportName & """>" 
& $content 
& 
IF( 
  $isFavorited = TRUE, 
  " Dashboard report",
  " Add to dashboard"
) & "</div>"

FALSE
/*OR(
	AND(
		[Navigation] = "Dashboard", 
		ARRAY_INDEXOF([Current User Dashboard Preference], "All Audits") > 0
	), 
