& $content 
& 
IF( 
  $isFavorited = TRUE, 
  " Dashboard report",
  " Add to dashboard"
) & "</div>"

LET $highOrExtremeRisks  := 
HASH_GET(
	HASH_GET([Aggregate Results], "2"),
	"Aggregate"
);
LET $totalRisks := 
HASH_GET(
	HASH_GET([Aggregate Results], "1"),
	"Aggregate"
);

LET $result := IF(N($totalRisks) > 0, $highOrExtremeRisks / $totalRisks * 100, 0);

RETURN NUMBERFORMAT($result, "0.0") & "%"

LET $target:= "Risk Summary";

RETURN
OR(
  AND(
	[Navigation] = "Dashboard",
	ARRAY_INDEXOF([Current User Dashboard Preference], $target) > 0
  ),
  AND(
	[Navigation] = "Reports",
	[Report Type] = "Premade reports"
  )
)

