& 
IF( 
  $isFavorited = TRUE, 
  " Dashboard report",
  " Add to dashboard"
) & "</div>"

LET $data := HASH_GET([Aggregate Results], "Most Assigned Status");
LET $keys := HASH_KEYS($data); 
LET $data :=  ARRAY_SELECT($keys, $(i, item) HASH_GET($data, $item));

LET $rows := 
ARRAY_SELECT(
  $data, $(i, item) 
  (
	LET $url := HASH_GET($item, "URL"); 
	LET $status := HASH_GET($item, "Status"); 
	LET $count := HASH_GET($item, "Aggregate");

	RETURN 
	ARRAY(
	  HASH("value", $status, "url", $url),
	  HASH("value", $count, "url", $url)
	)
  )
);

LET $columns := 
ARRAY(
  HASH("heading", "Status", "width", 560), 
  HASH("heading", "#", "width", 560)
); 

RETURN
HASH(
    "columns", $columns,
    "rows", $rows
)

FALSE
