  $isFavorited = TRUE, 
  "<span style=""margin-bottom: 10px;"">" & HASH_GET([Icons], "unfilledStar") & "</span>",
  "<span style=""margin-bottom: 10px;"">" & HASH_GET([Icons], "filledStar") & "</span>"
