) & "</div>"

LET $data := HASH_GET([Aggregate Results], "Most Assigned Role");
LET $keys := HASH_KEYS($data); 
LET $data :=  ARRAY_SELECT($keys, $(i, item) HASH_GET($data, $item));

LET $rows := 
ARRAY_SELECT(
  $data, $(i, item) 
  (
	LET $url := HASH_GET($item, "URL"); 
	LET $role := HASH_GET($item, "Risk Owner"); 
	LET $name := COALESCE(IF($role = NULL(), "Unassigned", EBMS_GET_ROLE_DISPLAYNAME($role)), $role); 

	LET $count := HASH_GET($item, "Aggregate");

	RETURN 
	ARRAY(
	  HASH("value", $name, "url", $url),
	  HASH("value", $count, "url", $url)
	)
  )
);

LET $columns := 
ARRAY(
  HASH("heading", "Risk Owner", "width", 560), 
  HASH("heading", "#", "width", 560)
); 

RETURN
HASH(
    "columns", $columns,
    "rows", $rows
)

OR(
	AND(
		[Navigation] = "Dashboard", 
		ARRAY_INDEXOF([Current User Dashboard Preference], "Risk Owner Summary") > 0
	), 
	AND(
		[Navigation] = "Reports",
		[Report Type] = "Premade reports"
