IF( 
	$isFavorited = TRUE, 
	"#2f4f4f",

	"black"
);


RETURN "<div style='color: " & $textColour & "; cursor: pointer; padding-top: 8px; font-size: 11pt' data-clickid=""" & $reportName & """>" 
& $content 
& 
IF( 
  $isFavorited = TRUE, 
  " Dashboard report",
  " Add to dashboard"
) & "</div>"

LET $totalW := 50 * 20 - 2;
LET $totalH := 30 * 10 - 8; 
LET $headingHeight := 24;
LET $fontsize := "13px";
LET $onlyShowOpen := TRUE; 


LET $consequences := ARRAY_DISTINCT(ARRAY_INDEX(ARRAY_TRANSPOSE(ARRAY_INDEX([Risk Data], 1)), 2));
LET $likelihoods  := ARRAY_REVERSE(ARRAY_DISTINCT(ARRAY_INDEX(ARRAY_TRANSPOSE(ARRAY_INDEX([Risk Data], 1)), 1)));
LET $textColours := SPLIT(ARRAY_INDEX(ARRAY_INDEX([Risk Data], 2), 3), ", ");
LET $colours := SPLIT(ARRAY_INDEX(ARRAY_INDEX([Risk Data], 2), 2), ", ");
LET $ratings := SPLIT(ARRAY_INDEX(ARRAY_INDEX([Risk Data], 2), 1), ", ");

LET $ratingtoColourMap :=
HASH(
	ARRAY_INDEX($ratings, 1), 1,
	ARRAY_INDEX($ratings, 2), 2,
	ARRAY_INDEX($ratings, 3), 3,
	ARRAY_INDEX($ratings, 4), 4
);

LET $map := ARRAY_INDEX([Risk Data], 1);

// calculate actual col/row width/height
LET $w := $totalW / (ARRAY_LENGTH($consequences) + 1);
LET $h := ($totalH - $headingHeight) / (ARRAY_LENGTH($likelihoods));

LET $filter := "1 = 1";

// load risk info
LET $risks      := 
DB_LOOKUPALL(
	ARRAY("Likelihood", "Consequence", "RecordID", "Status"), 
	"Risk", 
	"[LineNumber] FAST_EQUAL_TO 1 AND " & $filter
); 

LET $riskGroups := ARRAY_GROUP($risks, $(item) ARRAY(ARRAY_INDEX($item, 1), ARRAY_INDEX($item, 2)));
LET $riskMap    := ARRAY_TO_HASH($riskGroups, $(i, item) ARRAY_INDEX($item, 1), $(i, item) ARRAY_INDEX($item, 2));

LET $consequenceRow :=
"<tr>" &
"<td style=""width:" & $w & "px;height:" & $headingHeight & "px""></td>" & 
ARRAY_JOIN(
	ARRAY_SELECT(
	$consequences, $(col, consequence) (
	  "<td style=""text-align: center; font-size: " & $fontsize & "; width:" & $w & "px"">" & HTMLENCODE($consequence) & "</td>")
	)
) & 
"</tr>";

RETURN 
"<table style=""border-collapse:collapse"" cellpadding=""0"" cellspacing=""0"">" & 
	ARRAY_JOIN(ARRAY_SELECT(
		$likelihoods,
		$(row, likelihood) (
			"<tr>" & 
				"<td style=""padding-right: 10px; font-size: " & $fontsize & "; text-align: right; height:" & $h & "px"">" &
					$likelihood & 
				"</td>" & 
			ARRAY_JOIN(ARRAY_SELECT(
				$consequences,
				$(col, consequence) (
					LET $key   := ARRAY($likelihood, $consequence);
					LET $matches := ARRAY_WHERE($map, $(i, item) ARRAY(ARRAY_INDEX($item, 1), ARRAY_INDEX($item, 2)) = $key);
					LET $riskRating := ARRAY_INDEX(ARRAY_FIRST($matches), 3);
					LET $riskScore := ARRAY_INDEX(ARRAY_FIRST($matches), 4);
					LET $colorIndex := HASH_GET($ratingtoColourMap, $riskRating);
					LET $color := ARRAY_INDEX($colours, $colorIndex);
					LET $textColor := ARRAY_INDEX($textColours, $colorIndex);

					//LET $fontsize:= IF($onlyShowOpen == TRUE, "18px", "9px");
					LET $style := "display: inline-block; width: 49%; cursor: pointer; font-size:" & $fontsize & ";";

					RETURN "<td style=""text-align: center; border: 0px; background-color: " & $color & """>" & (
						LET $risksMatchingKey := HASH_GET($riskMap, $key);
						LET $matches := ARRAY_LENGTH($risksMatchingKey);
						LET $open := 
						ARRAY_LENGTH(
							ARRAY_WHERE(
								$risksMatchingKey, $(i, item) 
								AND(
									ARRAY_INDEX($item, 4) <> "Cancelled",
									ARRAY_INDEX($item, 4) <> "Resolved",
									ARRAY_INDEX($item, 4) <> "Closed"
								) 
								//ARRAY_INDEX($item, 4) = "Open"
							)
						);

						LET $resolved  := ARRAY_LENGTH(ARRAY_WHERE($risksMatchingKey, $(i, item) ARRAY_INDEX($item, 4) = "Resolved"));

						LET $separator := IF($onlyShowOpen == TRUE, "", "border-right: 1px solid white;");
						LET $content := IF($onlyShowOpen == TRUE, "", "<span>Open</span>");


						//LET $openDiv := "<div data-clickid=""" & ARRAY_FIRST($key) & "|" & ARRAY_LAST($key) & ";"" style=""" & $style & $separator & """><div><a style=""color: white;"">" & IF($open > 0 , $open) & "</a></div>" & $content & "</div>";
						LET $openDiv := 
					    "<div data-clickid=""" & ARRAY_FIRST($key) & "|" & ARRAY_LAST($key) & """; style=""" & $style & """>" & 
					  		 "<div>" & 
							 IF(
								$open > 0, 
					  			"<a style=""cursor: pointer; text-decoration: none; font-size: " & $fontsize & "; color: " & $textColor & ";"">" & 
									$open & 
								"</a>"
							) &
					  		"</div>" & $content & 
					    "</div>";

						LET $resolvedDiv := 
						"<div data-clickid=""" & ARRAY_FIRST($key) & " " & ARRAY_LAST($key) & """; style=""" & $style & """><div><a style=""color: white;"">" & $resolved & "</a></div><span>Resolved</span></div>";

						RETURN 
						IF(
							AND(
								$onlyShowOpen = TRUE,
								$open >= 0
							),
							$openDiv, 

							OR(
								$open > 0, 
								$resolved > 0
							), 
							$openDiv & $resolvedDiv,


							""
						)

					) 
					& "</td>"
				)
			)) & "</tr>"
		)
	)) &
	$consequenceRow & 
	"</table>"

