FieldEntry.Formula:
LET $dashboardSettings := 
HASH(
  "Dashboard Preference Name", "Risk Dashboard", 
  "Settings EForm", "Settings - Risk", 
  "Module Name", "Risk"
); 

//------------------------ No changes below this line

LET $moduleSettings := 
(
  LET $columns := 
  ARRAY(
    "Primary Entity Name", 
    "Module Name",
	"Add New Visible in Report Tiles",

    //Custom link data 
    "Custom Link Text", 
    "Custom Link URL", 
    "Custom Link Visibility",

    //Report links
    "Report Name", 
    "Report EFormType", 
    "Report Profile Name", 
    "Report Type",
    "Report Enabled",
    "Report Visibility", 

    //Action Data
	"Action Name", 
	"Can Use Action",
	"Action Navigation",
	"Dashboard Action Type",
	"Dashboard Action EFormType",
	"Dashboard Action RecordID",
	"Dashboard Action Open Mode"

  );

  LET $data := 
  DB_LOOKUPALL(
  	$columns, 
  	HASH_GET($dashboardSettings, "Settings EForm"), 
  );

  LET $hashed := 
  INDEXES_TO_KEYS(
    ARRAY_TRANSPOSE($data), 
    $columns
  );

  RETURN $hashed
);

LET $globalSettings := 
(
  LET $columns := 
  ARRAY(
    "Module Name", 
    "Module Settings EForm",
    "Module Home Page", 
    "Module Dashboard Name",
    "Module Access"
  ); 

  LET $modules := 
  DB_LOOKUPALL(
    $columns, 
    "Global Settings", 
    "[Show on Main Entry] = TRUE AND [RecordID] = 1" & 
	" AND [Module Name] IS NOT EMPTY AND [Module Type] = 'Module'" & 
	" AND [Module Settings EForm] <> " & DB_ENCODECONSTANT(HASH_GET($dashboardSettings, "Settings EForm"))
  ); 

  LET $data :=
  ARRAY_SELECT(
  	$modules, $(m, module)
    INDEXES_TO_KEYS(
	  $module, 
	  $columns
	)
  );

  LET $result := 
  ARRAY_WHERE(
  	$data, $(i, item) 
  	NOT(ISBLANK(HASH_GET($item, "Module Name") <> NULL))
  );

  RETURN $result
); 

LET $currentUser := EBMS_GET_CURRENT_USER();

RETURN 
HASH_MERGE(
  $dashboardSettings, 
  HASH("Module Settings", $moduleSettings),
  HASH("Global Settings", $globalSettings), 
  HASH("Current User", $currentUser)
)

LET $preference := 
EBMS_GET_PREFERENCE(
  HASH_GET(
	[Dashboard Settings], 
    "Dashboard Preference Name"
  )
);

RETURN 
IF(
  $preference = NULL, 
  ARRAY("All Records"), 

  //Here you can set default preferences for people in different roles
  //user in role x 
  //preference for user in role x

  $preference
)

LET $currentUser := HASH_GET([Dashboard Settings], "Current User"); 

LET $currentRoles := EBMS_GET_ANCESTOR_ROLES($currentUser);

RETURN 
INDEXES_TO_KEYS(
  ARRAY_SELECT($currentRoles, $(i, item) TRUE),
  $currentRoles
)

LET $modules:= HASH_GET([Dashboard Settings], "Global Settings");

LET $data :=
ARRAY_SELECT(
	$modules, $(m, module)

	LET $moduleName 	:= 	HASH_GET($module, "Module Name"); 
	LET $moduleSettings :=	HASH_GET($module, "Module Settings EForm"); 
	LET $moduleEForm 	:=	HASH_GET($module, "Module Home Page"); 
	LET $displayText 	:=	HASH_GET($module, "Module Name"); //"Module Dashboard Name"); 

	LET $accessList := HASH_GET($module, "Module Access");


	LET $hasAccess :=
	ARRAY_LENGTH(
		ARRAY_WHERE(
			$accessList, $(i, item) 
			ARRAY_LENGTH(
				ARRAY_WHERE(
					$item, $(i, item) 
					HASH_GET([User to Group Mappings], $item)= TRUE
				)
			) > 0
		)
	) > 0;

	RETURN 
	IF( 
		$hasAccess == TRUE, 
		IF( 
			$moduleEForm == [EFormType], 
			$displayText,
			HASH(
				"text", 	$displayText, 
				"value", 	$moduleEForm
			)
		)
	)
);

LET $result := 
ARRAY_WHERE(
	$data, $(i, item) 
	$item<> NULL
);
RETURN $result

LET $showPerLine := 3;

LET $keys := 
ARRAY(
  "Custom Link Text", 
  "Custom Link URL", 
  "Custom Link Visibility"
); 

LET $data :=
ARRAY_TRANSPOSE(
  ARRAY_SELECT(
    $keys, $(k, key) 
    HASH_GET(HASH_GET([Dashboard Settings], "Module Settings"), $key)
  )
); 

LET $filtered := 
ARRAY_WHERE(
  $data, $(i, item)
  AND(
    ARRAY_INDEX($item, ARRAY_INDEXOF($keys, "Custom Link Text")) <> NULL
  )
); 

LET $filtered := 
ARRAY_WHERE(
	$filtered, $(i, item) 
	ARRAY_LENGTH(
		ARRAY_WHERE(
			ARRAY_INDEX($item, 3), $(i, item)
			HASH_GET([User to Group Mappings], T($item))
		)
	) > 0
);

LET $set := 
ARRAY_SELECT(
	$filtered, $(i, item) 
	ARRAY_TAKE($item, 2)
);

LET $lines := CEILING(ARRAY_LENGTH($set) / $showPerLine );

LET $result :=  
ARRAY_SELECT(
	RANGE(0, $lines), $(l, line)
	ARRAY_TAKE(ARRAY_SKIP($set, $showPerLine * ($l - 1)), $showPerLine)
);

RETURN 
$result

LET $data := HASH_GET([Dashboard Settings], "Module Settings");  
LET $currentUser := HASH_GET([Dashboard Settings], "Current User"); 

LET $columns := 
ARRAY(
  "Action Name", 
  "Can Use Action",
  "Action Navigation",
  "Dashboard Action Type",
  "Dashboard Action EFormType",
  "Dashboard Action RecordID",
  "Dashboard Action Open Mode"
);

LET $data := 
ARRAY_TRANSPOSE(
  ARRAY_SELECT(
    $columns, $(i, column) 
    HASH_GET($data, $column)
  )
); 

LET $data := 
ARRAY_WHERE(
  $data, $(i, item) 
  ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Action Name")) <> NULL
);

LET $filtered := 
ARRAY_WHERE(
	$data, $(i, item) 
	ARRAY_LENGTH(
		ARRAY_WHERE(
			ARRAY_INDEX($item, 2), $(i, item) 
			HASH_GET([User to Group Mappings], T($item))
		)
	) > 0
);

LET $clean := 
ARRAY_SELECT(
	$filtered, $(i,item) 
	HASH(
		"Action", 			ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Action Name")),
		"URL", 				ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Action Navigation")),
		"Navigation Type", 	ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Dashboard Action Type")),
		"EForm Target", 	ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Dashboard Action EFormType")),
		"RecordID",			ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Dashboard Action RecordID")),
		"Open Mode", 		ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Dashboard Action Open Mode"))
	)
);

RETURN $clean

LET $data := HASH_GET([Dashboard Settings], "Module Settings");  

LET $columns := 
ARRAY(
  "Report Name", 
  "Report EFormType", 
  "Report Profile Name", 
  "Report Type",
  "Report Enabled",
  "Report Visibility" //this must be last in the set
);

LET $data := 
ARRAY_TRANSPOSE(
  ARRAY_SELECT(
    $columns, $(i, column) 
    HASH_GET($data, $column)
  )
); 


LET $data := 
ARRAY_WHERE(
  $data, $(i, item) 
  AND(
	ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Report Enabled")) = TRUE, 
	OR(
      ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Report Type")) = "Link and Export", 
	  ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Report Type")) = "Chart",
	  ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Report Type")) = "EForm"
	)
  )
);

LET $filtered := 
ARRAY_WHERE(
	$data, $(i, item) 
	ARRAY_LENGTH(
		ARRAY_WHERE(
			ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Report Visibility")), $(i, item) 
			HASH_GET([User to Group Mappings], T($item))
		)
	) > 0
);

LET $result := 
ARRAY_SELECT(
  $filtered, $(i, item) 
  INDEXES_TO_KEYS(
    $item,
	$columns
  )
);

LET $grouped :=  
HASH(
  "Link and Export", ARRAY_WHERE($result, $(i, item) HASH_GET($item, "Report Type") = "Link and Export"),
  "Visual Report", ARRAY_WHERE($result, $(i, item) HASH_GET($item, "Report Type") = "Chart"),
  "EForm Report" , ARRAY_WHERE($result, $(i, item) HASH_GET($item, "Report Type") = "EForm")
);

RETURN $grouped

