LET $multiline := DB_LOOKUPALL( ARRAY( "Likelihood", "Consequence", "Risk Rating", "Risk Score" ), "Risk Mappings", "[RecordID] = 1" );
LET $lineOne := DB_LOOKUPALL( ARRAY( "Risk Ratings", "Risk Colours", "Risk Text Colours" ), "Risk Mappings", "[RecordID] = 1 AND [LineNumber] FAST_EQUAL_TO 1" );
RETURN ARRAY_CONCAT( ARRAY($multiline), $lineOne )

LET $startOfThisMonth := DATE(YEAR(NOW()), MONTH(NOW())); LET $startOfLastMonth := DATEADD("month", -1,$startOfThisMonth); LET $startOfNextMonth := DATEADD("month", 1,$startOfThisMonth);
LET $riskProfile := "Summary"; LET $auditProfile := "Summary"; LET $treatmentProfile := "Summary";
RETURN ARRAY( HASH( "Report ID", "1", "Description", "All Risks", "Format", "number", "Form", "Risk", "Profile", $riskProfile, "Aggregate", "COUNT([RecordID])", "Filters", ARRAY( ARRAY("LineNumber", "=", 1) ) ), HASH( "Report ID", "2", "Description", "High or Extreme Risks", "Format", "number", "Form", "Risk", "Profile", $riskProfile, "Aggregate", "COUNT([RecordID])", "Filters", ARRAY( ARRAY("LineNumber", "=", 1, " AND "), ARRAY("Risk Rating", "IN", ARRAY("High", "Extreme"))
) ), HASH( "Report ID", "3", "Description", "Risks Added in the Last 30 Days", "Format", "number", "Form", "Risk", "Profile", $riskProfile, "Aggregate", "COUNT([RecordID])", "Filters", ARRAY( ARRAY("LineNumber", "=", 1, " AND "), ARRAY("DateCreated", ">=", DATEADD("day", -30, NOW()))
) ), HASH( "Report ID", "4", "Description", "All Audits In Progress", "Format", "number", "Form", "Audit Schedule", "Profile", $riskProfile, "Aggregate", "COUNT([RecordID])", "Filters", ARRAY( ARRAY("LineNumber", "=", 1, " AND "), ARRAY("Status", "IN", ARRAY("In Progress"))
) ), HASH( "Report ID", "5", "Description", "Audits - High or Extreme Severity", "Format", "number", "Form", "Audit Schedule", "Profile", $riskProfile, "Aggregate", "COUNT([RecordID])", "Filters", ARRAY( ARRAY("LineNumber", "=", 1, " AND "), ARRAY("Risk Rating", "IN", ARRAY("High", "Extreme")) ) ), HASH( "Report ID", "6", "Description", "Audits Planned in next 30 days", "Format", "number", "Form", "Audit Schedule", "Profile", $riskProfile, "Aggregate", "COUNT([RecordID])", "Filters", ARRAY( ARRAY("LineNumber", "=", 1, " AND "), ARRAY("Planned Start Date", ">=", NOW(), " AND "), ARRAY("Planned Start Date", "<=", DATEADD("day", 30, NOW())) ) ), HASH( "Report ID", "7", "Description", "All Treatments In Progress", "Format", "number", "Form", "Treatment", "Profile", $riskProfile, "Aggregate", "COUNT([RecordID])", "Filters", ARRAY( ARRAY("LineNumber", "=", 1, " AND "), ARRAY("Status", "IN", ARRAY("In Progress")) ) ), HASH( "Report ID", "8", "Description", "Treatments Due in next 30 days", "Format", "number", "Form", "Treatment", "Profile", $riskProfile, "Aggregate", "COUNT([RecordID])", "Filters", ARRAY( ARRAY("LineNumber", "=", 1, " AND "), ARRAY("Treatment Due Date", ">=", NOW(), " AND "), ARRAY("Treatment Due Date", "<=", DATEADD("day", 30, NOW())) ) ), HASH( "Report ID", "9", "Description", "Treatments Completed in last 30 days", "Format", "number", "Form", "Treatment", "Profile", $riskProfile, "Aggregate", "COUNT([RecordID])", "Filters", ARRAY( ARRAY("LineNumber", "=", 1, " AND "), ARRAY("Completion Date", ">=", NOW(), " AND "), ARRAY("Completion Date", "<=", DATEADD("day", 30, NOW())) ) ) )

LET $run := TRUE; 

//CUSTOM REPORTS //
LET $customReportSets:= 
HASH(
	"Most Assigned Status", 	HASH("Type", "Status", 			"Form", "Risk",	"Target Field", "[Status]", 			"Profile", "Details",		"Filter", "[LineNumber] = 1"),
	"Most Assigned Role", 		HASH("Type", "Risk Owner", 		"Form", "Risk",	"Target Field", "[Responsible Person]", 		"Profile", "Details",		"Filter", "[LineNumber] = 1"),
    "Most Assigned Register", 	HASH("Type", "Risk Rating", 	"Form", "Risk",	"Target Field", "[Risk Rating]", 		"Profile", "Details",		"Filter", "[LineNumber] = 1"),

	"Audit - Most Assigned Status", 		HASH("Type", "Status", 			"Form", "Audit Schedule",	"Target Field", "[Status]", 			"Profile", "Summary",		"Filter", "[LineNumber] = 1"),
	"Audit - Most Assigned Audit Type", 	HASH("Type", "Audit Type", 		"Form", "Audit Schedule",	"Target Field", "[Audit Type]", 		"Profile", "Summary",		"Filter", "[LineNumber] = 1"),
    "Audit - Most Assigned Risk Rating", 	HASH("Type", "Risk Rating", 	"Form", "Audit Schedule",	"Target Field", "[Risk Rating]", 		"Profile", "Summary",		"Filter", "[LineNumber] = 1"),

	"Treatment - Most Assigned Status", 	HASH("Type", "Status", 			"Form", "Treatment",			"Target Field", "[Status]", 			"Profile", "Summary",		"Filter", "[LineNumber] = 1"),
	"Treatment - Most Assigned Role", 		HASH("Type", "Treatment Owner", 		"Form", "Treatment",	"Target Field", "[Treatment Owner]", 	"Profile", "Summary",		"Filter", "[LineNumber] = 1"),
    "Treatment - Most Assigned Register", 	HASH("Type", "Treatment Decision", 	"Form", "Treatment",		"Target Field", "[Treatment Decision]", "Profile", "Summary",		"Filter", "[LineNumber] = 1")
);

LET $valueField := NULL;//"Total Risk Entry Value";

LET $customReports := 
ARRAY_SELECT(
	HASH_KEYS($customReportSets), $(i, item) 
	(

		LET $targetField := HASH_GET(HASH_GET($customReportSets, $item), "Target Field");
		LET $filter := HASH_GET(HASH_GET($customReportSets, $item), "Filter");
		LET $type := HASH_GET(HASH_GET($customReportSets, $item), "Type");
		LET $profile  := HASH_GET(HASH_GET($customReportSets, $item), "Profile");
		LET $form := HASH_GET(HASH_GET($customReportSets, $item), "Form");

		LET $data := 
		IF( 
		  $run = TRUE, 
		  DB_REPORT(
			ARRAY(
				$targetField, 
				"COUNT([RecordID])"//, 
				//"SUM([" & $valueField & "])"
			), 
			$form, 
			"[LineNumber] FAST_EQUAL_TO 1 AND " & $filter
		));

		LET $sorted :=
		ARRAY_SORTDESC(
			$data, $(item) 
			ARRAY_INDEX($item, 2)
		);

		LET $individualHash := 
		ARRAY_SELECT(
			$sorted, $(i, item) 

			LET $formatted := 
			ARRAY(
				ARRAY_INDEX($item, 1),
				NUMBERFORMAT(ARRAY_INDEX($item, 2), "#,##0"),
				"$" & NUMBERFORMAT(ARRAY_INDEX($item, 3), "#,##0"), 
				"TabularReport.aspx?EFormType=" & URLENCODE($form) & "&profile="& URLENCODE($profile) & "&where=" & URLENCODE($filter) & URLENCODE(" AND " & $targetField & " = '" & ARRAY_INDEX($item, 1) & "'")
			); 

			RETURN
			INDEXES_TO_KEYS($formatted, ARRAY($type, "Aggregate", "Value", "URL"))
		);

		RETURN 
		INDEXES_TO_KEYS(
			$individualHash,
			ARRAY_SELECT(RANGE(1, ARRAY_LENGTH($sorted)), $(i, item) $type & "-" & $item)
		)
	)
);

LET $customReports := 
INDEXES_TO_KEYS(
	$customReports, 
	HASH_KEYS($customReportSets)
);
//END CUSTOM REPORTS //


LET $getURL := $(form, aggregate, filters, profile)(
	LET $urlFilterString :=  
	ARRAY_JOIN(
		ARRAY_SELECT(
			$filters, $(i, item)
			IF(	
				EBMS_CONTAINS(ARRAY_INDEX($item, 1), "STEP TO"), 
				DB_ENCODEIDENTIFIER("RecordID") & " " & ">=" & " " & 1, 

				DB_ENCODEIDENTIFIER(ARRAY_INDEX($item, 1))
				& " " & ARRAY_INDEX($item, 2)
				& " " & DB_ENCODECONSTANT(ARRAY_INDEX($item, 3))
			)
			& IF(ARRAY_INDEX($item, 4) <> NULL(), " " & ARRAY_INDEX($item, 4))

		), 
		"" 
	);

	RETURN 
	"TabularReport.aspx?" & 
	"EFormType=" 		& URLENCODE($form) & 
	"&profile="		& URLENCODE($profile) & 
	"&where=" 		& URLENCODE($urlFilterString)
);

LET $getAggregate := $(form, aggregate, filters, format)(
	LET $reportFilterString := 
	ARRAY_JOIN(
		ARRAY_SELECT(
			$filters, $(i, item) 
			IF(	
				EBMS_CONTAINS(ARRAY_INDEX($item, 1), "STEP TO"), 
				ARRAY_INDEX($item, 1) & ARRAY_INDEX($item, 2) & " " & DB_ENCODECONSTANT(ARRAY_INDEX($item, 3)),
				"[" & ARRAY_INDEX($item, 1) & "] " & ARRAY_INDEX($item, 2) & " " & DB_ENCODECONSTANT(ARRAY_INDEX($item, 3))
			)
		),
		" AND "
	);

	LET $data := 
	ARRAY_FIRST(ARRAY_FIRST(
		IF( 
		  $run = TRUE, 
		  DB_REPORT(
			$aggregate, 
			$form, 
			$reportFilterString
		))
	));

	RETURN 
	IF( 
		$format = "dollar", 
			"$" & NUMBERFORMAT($data, "#,##0"),

		$format = "number", 
			NUMBERFORMAT($data, "#,##0"),

		$format = "none", 
			$data, 

		$data
	)
);

LET $results := 
ARRAY_SELECT(
	[Aggregate Requests], $(i, item) 
	ARRAY(
		HASH_GET($item, "Report ID"), 
			HASH(
			"Description", 
			HASH_GET($item, "Description"), 

			"User", 
			HASH_GET($item, "User"), 

			"Status", 
			HASH_GET($item, "Status"), 

			"Aggregate", 
			CALL(
				$getAggregate, 
				HASH_GET($item, "Form"), 
				HASH_GET($item, "Aggregate"), 
				HASH_GET($item, "Filters"),
				HASH_GET($item, "Format")
			),

			"URL", 
			CALL(
				$getURL, 
				HASH_GET($item, "Form"), 
				HASH_GET($item, "Aggregate"), 
				HASH_GET($item, "Filters"),	
				HASH_GET($item, "Profile")
			)	
		)
	)
);

RETURN 
HASH_MERGE(
	ARRAY_TO_HASH(
		$results, 
		$(i, item) ARRAY_INDEX($item, 1),
		$(i, item) ARRAY_INDEX($item, 2)
	), 
	$customReports
)

SetUserPreference.PreferenceValueFormula:
LET $target := $eventargs; //string of a single report name OR an array of report names
LET $preference := EBMS_GET_PREFERENCE(HASH_GET([Dashboard Settings], "Dashboard Preference Name")); 

LET $action := 
IF( 
	ARRAY_INDEXOF($preference, $target) > 0, 
	"Remove", 
	"Add"
);

LET $set := 
IF( 
	$action = "Add", 
	ARRAY_CONCAT(
		$preference, 
		ARRAY($target)
	),
	ARRAY_WHERE($preference, $(i ,item) $item <> $target)
);

RETURN 
IF( 
  AND(
	$preference = NULL, 
	ARRAY_LENGTH($target) > 0
  ), 
  ARRAY_DISTINCT(ARRAY_CONCAT([Current User Dashboard Preference], $target)),
  ARRAY_DISTINCT(ARRAY_WHERE($set, $(i,item) $item <> NULL()))
)

LET $target := $eventargs; //string of a single report name OR an array of report names
LET $preference := EBMS_GET_PREFERENCE(HASH_GET([Dashboard Settings], "Dashboard Preference Name")); 

LET $action := 
IF( 
	ARRAY_INDEXOF($preference, $target) > 0, 
	"Add", 
	"Add"
);

LET $set := 
IF( 
	$action = "Add", 
	ARRAY_CONCAT(
		$preference, 
		ARRAY($target)
	),
	ARRAY_WHERE($preference, $(i ,item) $item <> $target)
);

RETURN 
IF( 
  AND(
	$preference = NULL, 
	ARRAY_LENGTH($target) > 0
  ), 
  ARRAY_DISTINCT(ARRAY_CONCAT([Current User Dashboard Preference], $target)),
  ARRAY_DISTINCT(ARRAY_WHERE($set, $(i,item) $item <> NULL()))
)
