FieldEntry.Formula:
LET $data := ARRAY_DISTINCT([Related Risk ID Array]);
LET $filteredData := ARRAY_WHERE($data, $(i, item)
	AND(
		$item <> [Select Risk ID],
		$item <> NULL()
	)
);

RETURN
ARRAY_CONCAT($filteredData, [Select Risk ID])

LET $thisRequest := HASH(NOW(), [Acknowledgement Recipients]); 

RETURN 
IF(
  [Acknowledgement Previous Request Data] = NULL(),
  $thisRequest,

  HASH_MERGE(
    [Acknowledgement Previous Request Data], 
    $thisRequest
  )
)

LET $addition := 
ARRAY_CONCAT(
  [Document Types], 
  "Mailmerge"
);

LET $removal := 
ARRAY_WHERE(
  [Document Types], 
  $(i, item) $item <> "Mailmerge"
); 

LET $set := 
IF( 
  [Is a Mail Merge Template] = TRUE, 
  $addition, 
  $removal
); 

RETURN
ARRAY_DISTINCT(
  ARRAY_WHERE($set, $(i, item) $item <> NULL)
)

LET $addition := 
ARRAY_CONCAT(
  [Document Types], 
  "Mailmerge"
);

LET $removal := 
ARRAY_WHERE(
  [Document Types], 
  $(i, item) $item <> "Mailmerge"
); 

LET $set := 
IF( 
  [Is a Mail Merge Template] = TRUE, 
  $addition, 
  $removal
); 

RETURN
ARRAY_DISTINCT(
  ARRAY_WHERE($set, $(i, item) $item <> NULL)
)

LET $exclusionList := 
ARRAY(
	"svc-wrike", 
	"Test",
	"CRM Reminders",
	"heidi"
);

LET $everyone :=
IF( 
  [Acknowledge - Everyone], 
  DB_LOOKUPALL(
	"Username", 
	"sys.Roles", 
	"[IsSystem] <> TRUE AND [Active] = TRUE AND [Type] = 'USER'"
  )  
);

LET $everyone := 
ARRAY_WHERE(
  $everyone, $(i, item) 
  AND(
    EBMS_GET_ROLE_EMAIL_ADDRESSES($item) <> NULL(),
    ARRAY_INDEXOF($exclusionList, $item) = NULL()
  )
);

LET $users := 
IF( 
	[Acknowledge - Specific Users], 
	[User for Acknowledgement]
);

LET $groups := 
IF( 
	[Acknowledge - Specific Groups], 
	ARRAY_SELECTMANY(
		[Group for Acknowledgement], $(i, group) 
		DB_LOOKUPALL(
			"Username", 
			"sys.Users", 
			"[Username] IN ROLE " & DB_ENCODECONSTANT($group)
		)
	)
);

LET $departments := 
IF( 
	[Acknowledge - Specific Departments], 
	ARRAY_SELECTMANY(
		[Department for Acknowledgement], $(i, department) 
		DB_LOOKUPALL(
			"Username", 
			"sys.Users", 
			"GET_USER_DEPARTMENT([Username]) = " & DB_ENCODECONSTANT($department)
		)
	)
);

LET $combined := 
IF(
  OR( 
	$everyone = NULL(), 
	$everyone = ARRAY()
  ), 

  ARRAY_CONCAT(
	$users, 
	$groups, 
	$departments
  ), 

  $everyone
);

RETURN 
ARRAY_DISTINCT(ARRAY_WHERE($combined, $(i, item) $item <> NULL()))
