Column.ValidationFormula:
LET $array:= SPLIT($thisValue, ",");


LET $check:= 
ARRAY_SELECT( $array, $(i,item)
	IF(
		ISNULL(REGEX_MATCH($item, "(\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,4}\b)")),
		  FALSE,
		TRUE
	)
);

RETURN IF(
		ISBLANK($thisValue),"Email is required",
		IF(
			ARRAY_INDEXOF($check, FALSE)>0,
			"Please enter valid email address",
			TRUE
		)
	)

LET $array:= SPLIT($thisValue, ",");


LET $check:= 
ARRAY_SELECT( $array, $(i,item)
	IF(
		ISNULL(REGEX_MATCH($item, "(\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,4}\b)")),
		  FALSE,
		TRUE
	)
);

RETURN IF(

			ARRAY_INDEXOF($check, FALSE)>0,
			"Please enter valid email address",
			TRUE
		)

LET $array:= SPLIT($thisValue, ",");


LET $check:= 
ARRAY_SELECT( $array, $(i,item)
	IF(
		ISNULL(REGEX_MATCH($item, "(\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,4}\b)")),
