FieldEntry.Formula:
LET $emails := HASH_GET(ARRAY_FIRST([Temp]), "Result Email Addresses"); 

LET $currentInput := [To];

RETURN 
ARRAY_JOIN(ARRAY_DISTINCT(ARRAY_CONCAT(SPLIT($emails, ", "), SPLIT($currentInput, ", "))), ", ")

LET $emails := HASH_GET(ARRAY_FIRST([Temp]), "Result Email Addresses");

LET $currentInput := [CC];

RETURN 
ARRAY_JOIN(ARRAY_DISTINCT(ARRAY_CONCAT(SPLIT($emails, ", "), SPLIT($currentInput, ", "))), ", ")

LET $emails := HASH_GET(ARRAY_FIRST([Temp]), "Result Email Addresses");

LET $currentInput := [BCC];

RETURN 
ARRAY_JOIN(ARRAY_DISTINCT(ARRAY_CONCAT(SPLIT($emails, ", "), SPLIT($currentInput, ", "))), ", ")

LET $emails := HASH_GET(ARRAY_FIRST([Temp]), "Result Email Addresses");

LET $currentInput := [Reply-to];

RETURN 
ARRAY_JOIN(ARRAY_DISTINCT(ARRAY_CONCAT(SPLIT($emails, ", "), SPLIT($currentInput, ", "))), ", ")

