Column.Formula:
LET $parent := EBMS_GET_PARENT_RECORD(EFormType, RecordID);
LET $parentEFormType := ARRAY_FIRST($parent);
LET $parentRecordID := ARRAY_LAST($parent);

LET $originalEndDate := HASH_GET([Contract Data], "Original End Date");
LET $currentEndDate := HASH_GET([Contract Data], "Current End Date");

LET $endDateofExtensionsBeforeThisExtension := 
DB_LOOKUP(
	"End Date After Exercised", 
	"Extension", 
	"[Extension Number] = " & DB_ENCODECONSTANT([Extension Number] - 1),
	HASH("parent", ARRAY($parentEFormType, $parentRecordID))
);

LET $targetDate := 
IF(
    $endDateofExtensionsBeforeThisExtension = NULL(), 
	IF($currentEndDate <> NULL(), $currentEndDate, $originalEndDate),
	$endDateofExtensionsBeforeThisExtension
);

LET $result :=  
IF( 
	OR(
		[Status] = "Cancelled",
		$parent= NULL()
	),
	NULL(),

	$targetDate
);

RETURN
IF(
  	[End Date Before Exercised] <> NULL(),
  	IF(
  		OR(
			[Status] = "Exercised",
			[Status] = "Submitted"
  		), 
  		[End Date Before Exercised],
  		$result
  	),
  	$result
)

LET $addYears := DATEADD("year", [Extension Years], DATEADD("day", /*-1*/0, TODAY([End Date Before Exercised])));
LET $addMonths := DATEADD("month", [Extension Months], $addYears);
LET $addDays := DATEADD("day", [Extension Days], $addMonths);

RETURN 
IF(
  	AND(
	  	[End Date After Exercised] <> NULL(),
  		OR(
			[Status] = "Exercised",
			[Status] = "Submitted"
  		)
	), 
    [End Date After Exercised],

  	[Status] = "Cancelled", 
    NULL(),

  	GREATEST(
    	TODAY([End Date Before Exercised]), 
    	COALESCE(TODAY($addDays), TODAY([End Date Before Exercised]))
  	)
)

Column.DefaultValueFormula:
LET $cols:= 
ARRAY(
  "Can Add Extension Value"
);
RETURN
INDEXES_TO_KEYS(
	DB_LOOKUP(
		$cols,
		"Settings - Contract", 
		"[LineNumber] = 1"
	),
  $cols
 )

Column.Formula:
LET $data := 
ARRAY(
	ARRAY([Extension Years], IF([Extension Years] > 1, "years", "year")),
	ARRAY([Extension Months], IF([Extension Months] > 1, "months", "month")),
	ARRAY([Extension Days], IF([Extension Days] > 1, "days", "day"))
);

LET $result := 
ARRAY_WHERE(
	$data, $(i, item) 
	ARRAY_FIRST($item) > 0
);

RETURN 
ARRAY_JOIN(
	ARRAY_SELECT(
		$result, $(i, item) 
		ARRAY_JOIN($item, " ")
	), ", "
)

Column.ValidationFormula:
LET $childApproved := 
DB_LOOKUP("RecordID", "Submission", "[LineNumber] FAST_EQUAL_TO 1 AND [Status] = 'Approved'", HASH("parent", ARRAY(EFormType, RecordID))) <> NULL();

LET $thisApproved := [Date Approved] <> NULL();

RETURN 
IF( 
  AND(
	$childApproved = TRUE,
	$thisApproved <> TRUE
  ), 
  "This record has been approved while this page has been open. Please refresh the page to synchronise the status of this record."
)

Column.DefaultValueFormula:
LET $approvalRequired := 
NOT(DB_LOOKUP(
  "Not Required", 
  "Approval Workflow", 
  HASH("Submission Type", "Extension", "LineNumber", 1)
)); 

LET $approvalsFeatureEnabled := 
ARRAY_INDEXOF(
  DB_LOOKUP(
    "Profile Supported Functions", 
    "Settings - Contract", 
    "[RecordID] = 1"
  ), "Approvals"
) > 0;

LET $approvalStepRequired :=
AND(
  $approvalsFeatureEnabled = TRUE, 
  $approvalRequired
); 

RETURN 
IF( 
  $approvalStepRequired, 
    FALSE, 
  TRUE
)

Column.Formula:
LET $current := ARRAY([EFormType], [RecordID]);
LET $descriptionColumns := 
HASH(
	"Contract",  					"Contract Number", 
	"Vendor", 						"Company Name",
	"Procurement", 					"Procurement Number",
	"Project", 						"Project Number",
    "Evaluation", 		    		"Description",
  	"Information Request",  		"File No.", 
	"Subpoena", 					"File No.",
	"Privacy Incident", 			"File No."
); 

LET $altFormName :=
HASH(
	"Insurance",	"Compliance"
);

LET $getLabelText := $(eFormType, recordID)
(
  	LET $targetColumn := HASH_GET($descriptionColumns, $eFormType);
  	RETURN 
  	IF(
		$targetColumn = NULL, NULL,
		LET $isthisRecord := ARRAY($eFormType, $recordID) = $current;
		LET $title := 
		IF( $isthisRecord = TRUE, 
			CURRENT_VALUE($targetColumn),
			ARRAY_FIRST(DB_LOOKUP(
				ARRAY($targetColumn),
				$eFormType, 
				"[RecordID] FAST_EQUAL_TO " & $recordID
			))
		  );
		//shortern the descriptive label/ title
		LET $charCount := LEN($title);
		LET $titleArray := SPLIT($title, " ");
		LET $wordCount := ARRAY_LENGTH($titleArray);

		LET $charLimit := 30; 
		LET $wordLimit := ARRAY_LENGTH( SPLIT( LEFT($title, $charLimit), " ") );

	  RETURN //ARRAY_TAKE($titleArray, $wordLimit)
		IF(
			$wordCount <= $wordLimit, $title,
			ARRAY_JOIN(
				ARRAY_TAKE($titleArray, $wordLimit), " "
			)& " ..."
		)
	  )

); 

LET $getReadAccess := $(eFormType, recordID) (EBMS_GET_EFORMRECORD_PERMISSION($eFormType, $recordID, "read"));

LET $currentDes := CALL($getLabelText, [EFormType], [RecordID]);
LET $currentAccess := FALSE;

RETURN
IF(
	[Parent Structure] <> NULL, [Parent Structure],
  	(

		LET $parent := 
		IF( 
			[RecordID] > 0, 
			(
			LET $record := EBMS_GET_PARENT_RECORD(ARRAY_FIRST($current), ARRAY_INDEX($current, 2));
			RETURN

			IF(
				ARRAY_FIRST($record) == NULL,
				NULL,
				ARRAY_CONCAT( 
					$record, 
					CALL($getLabelText, ARRAY_FIRST($record), ARRAY_INDEX($record, 2)),
					CALL($getReadAccess, ARRAY_FIRST($record), ARRAY_INDEX($record, 2))
				)
			))
		);

		LET $gParent := 
		IF( 
			$parent <> NULL, 
			(
				LET $record := EBMS_GET_PARENT_RECORD(ARRAY_FIRST($parent), ARRAY_INDEX($parent, 2));
				RETURN

				IF( 
					ARRAY_FIRST($record) == NULL, NULL,
					ARRAY_CONCAT( 
						$record, 
						CALL($getLabelText, ARRAY_FIRST($record), ARRAY_INDEX($record, 2)),
						CALL($getReadAccess, ARRAY_FIRST($record), ARRAY_INDEX($record, 2))
					)
				)
			)
		); 

		LET $hash :=  
		HASH_MERGE(
			IF( $parent <> NULL, HASH("parent", 	$parent)),
			IF( $gParent <> NULL, HASH("gparent", 	$gParent))
		);

		LET $parentStructure := 	//2nd instance
		IF( 
			ARRAY_LENGTH(HASH_KEYS($hash)) >0, $hash, NULL
		);

		LET $parentData := 
		ARRAY_WHERE(
			ARRAY(
				$gParent, 
				$parent, 
				ARRAY_CONCAT($current, $currentDes, $currentAccess),
			), $(i,item) $item <> NULL
		);

		LET $parentChain :=
			ARRAY_SELECT(
				$parentData, $(i, item)
				ARRAY_TAKE($item, 2)
		);


		LET $parentLinks :=
		ARRAY_CONCAT(
			ARRAY(
			//Append Home to the front of the breadcrumb chain
			"<a style=""text-decoration-line: none;"" href=" & HTMLENCODE(EBMS_GET_BASE_URL()) & ">" &
				"<span style=""margin-left: 5px; background: #ffffff; color: #4D87B7;"">" & HTMLENCODE("Home") & "</span>" &
			"</a>",
			//gets the active Contract profile to use as the display name (e.g. Advanced, Lite)
			"<a style=""text-decoration-line: none;"" href=" & HTMLENCODE(EBMS_GET_BASE_URL()&"/records/Dashboard%20-%20Contract/new?view=Default") & ">" &
				"<span style=""margin-left: 5px; background: #ffffff; color: #4D87B7;"">" & HTMLENCODE("Contracts") & "</span>" & 
			"</a>"
			),

			ARRAY_SELECT(
				ARRAY_WHERE($parentData, $(p, parent) ARRAY_FIRST($parent) <> "Procurement"), $(i,item)
					LET $eFormType 		:= ARRAY_FIRST($item);
					LET $recordID 		:= ARRAY_INDEX($item, 2);
					LET $descriptiveLabel := ARRAY_INDEX($item, 3);
					LET $hasReadAccess	:= ARRAY_INDEX($item, 4);
					LET $URL	:= "records/" & URLENCODE($eFormType) & "/" & $recordID ;
					LET $showEFormType := $eFormType <> "Vendor";

					LET $labelColour := 
					IF(
						AND($eFormType = [EFormType], $recordID = [RecordID]),
						"background: #ffffff; color: #5D5D5D;",

						$hasReadAccess = TRUE,
						"background: #ffffff; color: #4D87B7;",

						"background: #ffffff; color: #5D5D5D;"
					);

					LET $labeltext := 
					IF(
						$showEFormType == TRUE,
						" " & COALESCE(HASH_GET($altFormName, $eFormType), $eFormType)
					) &
					IF(NOT(ISBLANK($descriptiveLabel)),
					" " & $descriptiveLabel);

					LET $label :=  
					"<span style=""margin-left: 5px;" & $labelColour & """>" &
					HTMLENCODE($labeltext) & 
					"</span>";

				RETURN
				IF( 
					$hasReadAccess == TRUE, 
					"<a style=""text-decoration-line: none;"" href=" & HTMLENCODE($URL) & ">" & $label & "</a>",
					$label 
				)
			)
		);

		LET $separator :=   "<svg style=""vertical-align: bottom; margin-left: 5px; height: 16px;"" xmlns=""http://www.w3.org/2000/svg"" viewBox=""0 0 256 512""><path fill=""#5D5D5D"" d=""M246.6 278.6c12.5-12.5 12.5-32.8 0-45.3l-128-128c-9.2-9.2-22.9-11.9-34.9-6.9s-19.8 16.6-19.8 29.6l0 256c0 12.9 7.8 24.6 19.8 29.6s25.7 2.2 34.9-6.9l128-128z""/></svg>";
		LET $breadcrumb := "<div style=""height: 23px; padding-left: 5px; font-size: 10pt; color: #5D5D5D; "">You are here:" & ARRAY_JOIN($parentLinks, $separator) & "</div>";

		RETURN 

		HASH(
			"Parent Structure",	$parentStructure,
			"Parent Data",		$parentData,
			"Parent Chain",		$parentChain,
			"Breadcrumb",		$breadcrumb
		)
	)
)

LET $columns := ARRAY( "Original End Date", "Current End Date", "Vendor Access Enabled", "Enabled Vendor Actions" );
LET $data := DB_LOOKUP( $columns, "Contract", HASH("RecordID", [Contract RecordID]) );
RETURN INDEXES_TO_KEYS($data, $columns)

Binding.Expression:
LET $type := 
IF( 
	[Source Request for Information RecordID] <> NULL(), "Request for Information",
	[Source Work Order RecordID] <> NULL(), "Work Order"
);


LET $sourceRecordID := 
COALESCE(
	[Source Work Order RecordID], 
	[Source Request for Information RecordID]
);

LET $link := "records/" & HTMLENCODE($type) & "/" & $sourceRecordID;

RETURN "<div style=""line-height: 20px; text-align: center"">This record was raised in relation to <a href=""" & HTMLENCODE($link) & """>" & $type & " ID:" & $sourceRecordID & "</a></div>"

FieldEntry.Formula:
DATEDIFF_PARTS(
  //[End Date After Exercised],
  DATEADD("day",/*1*/0, TODAY([End Date After Exercised])), 
  TODAY([End Date Before Exercised])
)

Binding.Expression:
LET $id := "Duration";

LET $error := ARRAY_FIRST(ARRAY_WHERE([Submit Check], $(i,item) HASH_GET($item, "Error ID") = $id));

RETURN HASH_GET($error, "Message")

LET $id := "Duration";

RETURN 

AND(
  	ARRAY_LENGTH(ARRAY_WHERE([Submit Check], $(i,item) HASH_GET($item, "Error ID") = $id)) > 0,
  	[Navigation] = "Details"
)

LET $id := "Value";

LET $error := ARRAY_FIRST(ARRAY_WHERE([Submit Check], $(i,item) HASH_GET($item, "Error ID") = $id));

RETURN HASH_GET($error, "Message")

LET $id := "Value";

RETURN 

AND(
	ARRAY_LENGTH(ARRAY_WHERE([Submit Check], $(i,item) HASH_GET($item, "Error ID") = $id)) > 0,
  	[Navigation] = "Details"
)

LET $years := [Extension Years];
LET $months := [Extension Months];
LET $days := [Extension Days];

RETURN

IF($years > 0, T($years) & IF($years > 1, " years", " year")) &
IF($months > 0, IF($years > 0, ", ") & T($months) & IF($months > 1, " months", " month")) &
IF($days > 0, IF(OR($years > 0, $months > 0), ", ") &T($days) & IF($days > 1, " days", " day"))

LET $years := [Extension Years];
LET $months := [Extension Months];
LET $days := [Extension Days];

RETURN

IF($years > 0, T($years) & IF($years > 1, " years", " year")) &
IF($months > 0, IF($years > 0, ", ") & T($months) & IF($months > 1, " months", " month")) &
IF($days > 0, IF(OR($years > 0, $months > 0), ", ") &T($days) & IF($days > 1, " days", " day"))

FieldEntry.Formula:
LET $extensionRecordID := ARRAY_INDEX([Extension RecordIDs], $count);
LET $contractRecordID :=  ARRAY_INDEX([Contract RecordIDs], $count);

RETURN
ARRAY_CONCAT(
	ARRAY(
		ARRAY(
			ARRAY("Extension", $extensionRecordID),
			HASH_MERGE(
				HASH(
					"Extension Exercised", TRUE
				),
				HASH("Status", "Exercised", "Expiry Date", NULL())
			)
		)
	),
	ARRAY(
		ARRAY(
			ARRAY("Contract", $contractRecordID),
			HASH("Temp", NULL())
		)
	)
)

TriggerCustomEvent.EventArgumentsFormula:
LET $targetType := "Extension";
LET $mostRecentSubmission := DB_LOOKUP( ARRAY("RecordID", "Round"), "Submission", "[Type] = " & DB_ENCODECONSTANT($targetType), HASH("sort", "[Round] DESC", "ancestor", ARRAY(EFormType, RecordID)) );
RETURN HASH( "Submission Type", $targetType, "Previous Submission Round", COALESCE(ARRAY_LAST($mostRecentSubmission), 0), "Previous Submission RecordID", ARRAY_FIRST($mostRecentSubmission), "Parent Record", ARRAY([EFormType], [RecordID]), "Review Record", ARRAY([EFormType], [RecordID]), "Submitter Notes", NULL )

TriggerCustomEvent.TargetRecordsFormula:
LET $hash := HASH("parent", ARRAY("Contract", [Contract RecordID]));

LET $recordIDs :=
DB_LOOKUPALL(
	"RecordID", 
	"Extension", 
	"[LineNumber] = 1",
	HASH_MERGE(HASH("sort", "[Extension Number] ASC"), $hash)
);

RETURN 
ARRAY_SELECT(
  $recordIDs, $(i, item)
  ARRAY("Extension", $item)
)

FieldEntry.Formula:
LET $columns := ARRAY( "Original End Date", "Current End Date", "Vendor Access Enabled", "Enabled Vendor Actions" );
LET $data := DB_LOOKUP( $columns, "Contract", HASH("RecordID", [Contract RecordID]) );
RETURN INDEXES_TO_KEYS($data, $columns)

LET $hash := HASH("parent", ARRAY("Contract", [Contract RecordID]));
LET $exercisedExtensions := ARRAY_FIRST( ARRAY_FIRST( DB_REPORT( "COUNT([RecordID])", "Extension", "[LineNumber] = 1" & " AND [RecordID] <> " & DB_ENCODECONSTANT([RecordID]) & " AND [Status] = 'Exercised' AND [Date Approved] < " & DB_ENCODECONSTANT(COALESCE([Date Approved], NOW())), $hash ) ));
LET $extensionsSubmittedBeforeThis := ARRAY_FIRST( ARRAY_FIRST( DB_REPORT( "COUNT([RecordID])", "Extension", "[LineNumber] = 1" & " AND [RecordID] <> " & DB_ENCODECONSTANT([RecordID]) & " AND [Status] <> 'Exercised'" & " AND [DateCreated] < " & DB_ENCODECONSTANT(N([DateCreated])), $hash ) ));
LET $result := IF( [Status] = "Exercised", $exercisedExtensions + 1, IFERROR($exercisedExtensions + $extensionsSubmittedBeforeThis + 1, 1) );
RETURN $result

TriggerCustomEvent.TargetRecordsFormula:
LET $hash := 
IF( 
	ARRAY_FIRST(EBMS_GET_PARENT_RECORD(EFormType, RecordID)) == "Multiple Award Contract", 
	HASH("parent", ARRAY("Multiple Award Contract", [Multiple Award RecordID])),
	HASH("parent", ARRAY("Contract", [Contract RecordID]))
);

LET $recordIDs :=
DB_LOOKUPALL(
	"RecordID", 
	"Extension", 
	"[LineNumber] = 1",
	HASH_MERGE(HASH("sort", "[Extension Number] ASC"), $hash)
);

LET $result := 
ARRAY_SELECT(
  $recordIDs, $(i, item)
  ARRAY("Extension", $item)
);

RETURN 
ARRAY_CONCAT(
  $result, $result
)

LET $hash := IF( ARRAY_FIRST(EBMS_GET_PARENT_RECORD(EFormType, RecordID)) == "Multiple Award Contract", HASH("parent", ARRAY("Multiple Award Contract", [Multiple Award RecordID])), HASH("parent", ARRAY("Contract", [Contract RecordID])) );
LET $recordIDs := DB_LOOKUPALL( "RecordID", "Extension", "[LineNumber] = 1 AND [Status] <> 'Exercised'", HASH_MERGE(HASH("sort", "[Extension Number] ASC"), $hash) );
RETURN ARRAY_SELECT( $recordIDs, $(i, item) ARRAY("Extension", $item) )

FieldEntry.Formula:
LET $validationData :=

ARRAY(
	IF([End Date After Exercised] = [End Date Before Exercised], HASH("Error ID", "Duration", "Message", "Please specify the duration of this extension")),
	IF([End Date After Exercised] < [End Date Before Exercised], HASH("Error ID", "Duration", "Message", "The end date of the extension must be greater than the current end date on the contract")),
	//IF(AND(HASH_GET([SETTINGS], "Can Add Extension Value") == TRUE, [Extension Value] = 0), HASH("Error ID", "Value", "Message", "Please specify the value of this extension")),
);

RETURN

ARRAY_WHERE($validationData, $(i,item) $item <> NULL)

Navigate.UrlTemplate:
{
LET $recordID := 
DB_LOOKUP(
	"RecordID",
	"Submission",
	"[Status] IN ('Draft', 'Submitted') ",
	HASH("parent", ARRAY(EFormType, RecordID))
);

RETURN EBMS_GET_EFORMRECORD_URL("Submission", $recordID)
}
