Column.Formula:
LET $line:= 
ARRAY_INDEXOF(
  EACH [Module Name], 
  [Enabled Module Name]#[LineNumber]
);

RETURN 
IF(
  $line > 0, 
  [Module Access]#$line
)

Column.DefaultValueFormula:
LET $currentUser := EBMS_GET_CURRENT_USER(); 

LET $currentRoles := EBMS_GET_ANCESTOR_ROLES($currentUser);

LET $userMapping := 
INDEXES_TO_KEYS(
  ARRAY_SELECT($currentRoles, $(i, item) TRUE),
  $currentRoles
);
RETURN 
HASH(
  "Current User", $currentUser,
  "User to Group Mapping", $userMapping
)

Column.ValidationFormula:
LET $whitelist := 
ARRAY(
  "https://player.vimeo.com/video/",
  "https://www.youtube.com/embed/"
); 

LET $match := 
ARRAY_LENGTH(ARRAY_WHERE(
  $whitelist, $(i, item) 
  EBMS_STARTSWITH($thisValue, $item)
)) = 1;

RETURN
IF( 
  AND(
	$thisValue <> NULL,
    $match <> TRUE 
  ),
  "Embed url doesn't match required format"
)

Binding.Expression:
LET $set := [Module Data];

RETURN
ARRAY_SELECT(
	$set,	  
  	$(i, item) 
	  LET $name := ARRAY_INDEX($item, 1); 
	  LET $eform := ARRAY_INDEX($item, 2); 

	  RETURN
	  HASH(
		  "value", 	$eform,
		  "text", 	IF($eform = "Global Settings", $name, $eform)//$name
	  )
)

