LET $pattern:= "(?:http[s]*\:\/\/)*(.*?)(?=\.)"; LET $url:= EBMS_GET_BASE_URL(); LET $instanceName := REGEX_MATCH($url, "([^\/]+$)"); LET $prefix:= REGEX_MATCH( $url, $pattern);
LET $gtmprefix := "https://"& IF( $prefix = "uat-nimblex", "uat-vp.", $prefix = "uat2-nimblex", "uat2-vp.", $prefix = "uat3-nimblex", "uat3-vp.", $prefix = "uat4-nimblex", "uat4-vp.", $prefix = "uat8-nimblex", "uat8-vp.", "" ); //gtm prefix is to make sure for uat environments the appropriate gtm url is used. this is irrelevant for prod environment
LET $defaults:= ARRAY( // ID prompt1 prompt 2 Locked, enabled, module dependent, module, nav, access group, url ARRAY( "def001", "start", "a new procurement process", TRUE, TRUE, TRUE, "Policy Guide", "Same window", NULL, "https://guide.vendorpanel.com.au"),
ARRAY( "def002", "find", "a supplier list(s)", TRUE, TRUE, TRUE, "Go To Market", "Same window", NULL, $gtmprefix & "vendorpanel.com.au/Members/?do=Panels:BuyerPanels&mode=all"), ARRAY( "def003", "find", "supplier(s) by name", TRUE, TRUE, TRUE, "Go To Market", "Same window", NULL, $gtmprefix & "vendorpanel.com.au/Members/?do=Search:SupplierSearch"), ARRAY( "def004", "find", "categories of suppliers", TRUE, TRUE, TRUE, "Go To Market", "Same window", NULL, $gtmprefix & "vendorpanel.com.au/Members/?do=BrowseCategories:ChooseSupplierListType"),
