ARRAY( "def008", "jump to", "a new procurement plan", TRUE, TRUE, TRUE, "Procurement Planning", "Same window", NULL, "records/Procurement/new "), ARRAY( "def009", "jump to", "a new sourcing process", TRUE, TRUE, TRUE, "Go To Market", "Same window", NULL, $gtmprefix & "vendorpanel.com.au/Members/?do=Wizards:RfxWizard&step=1")
);
LET $columns:= ARRAY( "Prompt ID", "Prompt 1", "Prompt 2", "Prompt Locked", "Prompt Enabled", "Prompt Module Dependent", "Prompt Module", "Prompt Navigation", "Prompt Access", "Prompt URL" ); RETURN INDEXES_TO_KEYS( ARRAY_SELECT( ARRAY_INDEX($defaults, $count), $(i,item) ARRAY($item) ), $columns )

