FieldEntry.Formula:
LET $start:= TODAY(target.[Announcement Date]);
LET $end:= TODAY(target.[End Date]);
LET $currentValue := target.[Remove from Dashboard];
RETURN
IF(
  	AND(
	  	TODAY() >= $start,
	  	$end >= TODAY()
	  ), 
  	NOT($currentValue), 
  	$currentValue
  )

