"<svg " & " style = ""width: 20px; height: 100%; """ & " xmlns=""http://www.w3.org/2000/svg"" aria-hidden=""true"" focusable=""false"" data-prefix=""fas"" data-icon=""chevron-right"" class=""svg-inline--fa fa-chevron-right fa-w-10"" role=""img"" viewBox=""0 0 16 10""><path fill=""" & $iconColour & """ d=""M15.6289 1.15625L14.9609 0.453125C14.7852 0.277344 14.5039 0.277344 14.3633 0.453125L8 6.81641L1.60156 0.453125C1.46094 0.277344 1.17969 0.277344 1.00391 0.453125L0.335938 1.15625C0.160156 1.29688 0.160156 1.57812 0.335938 1.75391L7.68359 9.10156C7.85938 9.27734 8.10547 9.27734 8.28125 9.10156L15.6289 1.75391C15.8047 1.57812 15.8047 1.29688 15.6289 1.15625Z""/></svg>";

LET $containedIcon := "<div data-clickid=""Action"" style=""cursor: pointer; text-align: center; background: " & $containerColour & "; width: 100%; height: 100%;"">" & $icon & "</div>";

RETURN $containedIcon

