FormulaDrivenCsvFile.CsvFileFormula:
LET $normalColumns := 
ARRAY_DISTINCT(
  ARRAY_SELECTMANY(
    [Temp], $(i, item)

    LET $normalColumns := 
	ARRAY_WHERE(
	  HASH_KEYS($item), $(i, item) 
	  AND(
	    $item <> "Questionnaire Data", 
	    $item <> "Vendor Contact Data", 
	    $item <> "Contract Manager Details" 
      ) 	  
	);

    RETURN $normalColumns
  )
);

LET $questionNames := 
ARRAY_DISTINCT(
  ARRAY_SELECTMANY(
    [Temp], $(i, item)

    LET $questionnaireData := ARRAY_FIRST(HASH_GET($item, "Questionnaire Data"));

    LET $questionNames := 
	ARRAY_WHERE(ARRAY_SELECT(
	  $questionnaireData, $(q, questionData) 
	  HASH_GET($questionData, "Question")
	), $(i, item) $item <> NULL); 

	RETURN $questionNames
  )
);

LET $questionCommentNames := 
ARRAY_SELECT(
  $questionNames, $(q, question) 
  $question & " - Comment"
);

LET $combinedColumns := 
ARRAY_SELECTMANY(
  ARRAY_CONCAT(
    $normalColumns, 
    ARRAY_TRANSPOSE(
      ARRAY(
  	  $questionNames, 
      $questionCommentNames
  	)
    )
  ), $(i, item) 
  $item
); 

LET $data := 
  ARRAY_SELECT(
    [Temp], $(i, item)

	LET $normalData := 
	ARRAY_SELECT(
	  $normalColumns, $(c, column) 
	  HASH_GET($item, $column)
	); 

    LET $questionnaireData := ARRAY_FIRST(HASH_GET($item, "Questionnaire Data"));

	LET $questionnaireResults := 
	ARRAY_SELECTMANY(
	  $questionNames, $(q, question) 
	  LET $match := ARRAY_FIRST(ARRAY_WHERE(
	    $questionnaireData, $(d, data) 
		HASH_GET($data, "Question") = $question
	  ));

	  RETURN 
	  ARRAY(
	    HASH_GET($match, "Answer"),
	    HASH_GET($match, "Comments")
	  )
	);

	RETURN 
	ARRAY_CONCAT(
	  $normalData, 
	  $questionnaireResults
	)
);

RETURN 
ARRAY_CONCAT(
  ARRAY($combinedColumns),
  $data
)

Binding.Expression:
LET $set := [Module Data];

RETURN
ARRAY_SELECT(
	$set,	  
  	$(i, item) 
	  LET $name := ARRAY_INDEX($item, 1); 
	  LET $eform := ARRAY_INDEX($item, 2); 

	  RETURN
	  HASH(
		  "value", 	$eform,
		  "text", 	IF($eform = "Global Settings", $name, $eform)//$name
	  )
)

LET $items := 
ARRAY(
  HASH("value", "Modules", "text", "Tiles"),
  HASH("value", "Integrated Modules", "text", "Modules")
);

LET $items := 
ARRAY_SELECT(
  $items, $(i, item) 
  "<div style='display: none;' data-tabname='" & HASH_GET($item, "value") & "' data-clickid='" & HASH_GET($item, "value") & "' data-state='selected' class='core-tabfile-item'>" & HASH_GET($item, "text") & "</div>" & 
  "<div style='display: none;' data-tabname='" & HASH_GET($item, "value") & "' data-clickid='" & HASH_GET($item, "value") & "' data-state='notselected' class='core-tabfile-item'>" & HASH_GET($item, "text") & "</div>"
);

RETURN "<div class='core-tabfile-bar'>" & ARRAY_JOIN($items, "") & "</div>"

LET $styles := 
ARRAY_SELECT(
  ARRAY(
    "Modules",
    "Integrated Modules"
  ), $(i, item) 

  LET $selected := 
  OR(
	AND($item = "Modules", [Module View Mode] = "Modules"), 
	AND($item = "Integrated Modules", [Module View Mode] = "Integrated Modules")
  ); 

  LET $css := 
  IF( 
	$selected = TRUE, 
	".core-tabfile-bar > [data-tabname='" & $item & "'][data-state='selected'] {display: flex !important;}",
	".core-tabfile-bar > [data-tabname='" & $item & "'][data-state='notselected'] {display: flex !important;}"
  );

  RETURN $css

); 

RETURN "<style>" & ARRAY_JOIN($styles) & "</style>"

"
<style>
.core-tabfile-item {
    padding: 8px 16px 8px 16px;
	cursor: pointer;
	margin: auto;
}
.core-tabfile-bar 
 {
    width: 100%;
    background: #FFFFFF;
    overflow: hidden;
    font-weight: 600;
	display: flex;
	font-size: 11pt;
	align-items: center;
	justify-content: center;
	text-align: center;
}
.core-tabfile-bar > [data-state='selected'] {
	background-color: #F8F8FF;
    border: 0px solid #F8F8FF;
    border-radius: 50px;
    color: #1E70B5;
}
.core-tabfile-item:hover {
	//color: steelblue;
	border: 0px solid #F8F8FF;
	border-radius: 50px;
	background-color: #F8F8FF;
}
</style>
"

TriggerCustomEvent.EventArgumentsFormula:
LET $min := 1; 
LET $max := ARRAY_LENGTH(EACH [Module Name]);

RETURN 
HASH(
  "swap", TRUE,
  "before", LEAST(GREATEST([LineNumber], $min), $max),
  "after", LEAST(GREATEST([Order Number], $min), $max)
)

FormulaCriterion.Formula:
LET $min := 1; 
LET $max := ARRAY_LENGTH(EACH [Module Name]);
LET $before := LEAST(GREATEST([LineNumber], $min), $max); 
LET $after := LEAST(GREATEST([Order Number], $min), $max);

RETURN $before <> $after

Binding.Expression:
"<div class='core-imageset-container'>" & 
ARRAY_JOIN(ARRAY_SELECT(
  ARRAY_SORT(HASH_KEYS([Svg Map]), $(item) $item), $(i, iconName)
  "<div class='core-iconpreview-container'>" & 
    "<div class='core-iconpreview-name'>" & $iconName & ".svg</div>" & 
    "<div class='core-iconpreview-image'>" & HASH_GET([Svg Map], $iconName) & "</div>" &
  "</div>"
)) & "</div>" & 
"<style>
.core-imageset-container{
    height: 300px;
    overflow: auto;
}
.core-iconpreview-image{
    height: 50px;
}
.core-iconpreview-name{
    padding-bottom: 10px;
    font-weight: bold;
}
.core-iconpreview-container{    
    width: 145px;
    float: left;
    border: 1px solid #e0e0e0;
    padding: 12px;
    text-align: center;
}
</style>"

LET $allSpecialFeatures := ARRAY( //Special functions i.e. not registers "Approvals", //Covers ANY kind of approval within the module "Custom Reminders", "Extensions", "Schedule of Rates", "Variations", "Vendor Insurance", "Vendor Contact", "Dashboard - Customise Report Tiles", "Dashboard - Detailed Reports", "Dashboard - Visual Reports", "Dashboard - Export Reports" );
LET $allRegisters := ARRAY( "Actions", "Clauses", "Contract Review", "Communication", "Defects Liability", "File notes", "Funding Sources", "Funding Recipients", "Issues", "Key Performance Indicators", "Lessons Learnt", "Novation", "Meetings", "Milestones", "Payment Certificates", "Purchase Orders", "Penalty Clauses", "Request for Information", "Risks", "Security", "Service Level Agreements", "Transition", "Warranties", "Work Orders", "Termination", "Reminders", "Manage Registers", "Contract Status", "Allocation History", "Approval Routes", "Vendor Access" );
LET $hashedSpecialFeatures := ARRAY_SELECT( $allSpecialFeatures, $(i, item) HASH("value", ARRAY("FEATURE", $item), "text", "FEATURE - " & $item) );
LET $hashedRegisters := ARRAY_SELECT( $allRegisters, $(i, item) HASH("value", ARRAY("REGISTER", $item), "text", "REGISTER TYPE - " & $item) );
RETURN ARRAY_CONCAT($hashedSpecialFeatures, $hashedRegisters)

Settings.LandingViewFormula:
LET $currentUser := HASH_GET([Session Data], "Current User");
LET $isSystemBuilder := HASH_GET(HASH_GET([Session Data], "User to Group Mapping"), "System Builders");
LET $navStripItem := COALESCE(HASH_GET($params, "manage"), "Announcements");
LET $view := IF($requestedView = "Client", "Client", $mode);
RETURN
IF(
  $isSystemBuilder, 
    COALESCE($requestedView, $navStripItem),
  "Client"
)

FieldEntry.Formula:
LET $currentUser := HASH_GET([Session Data], "Current User"); 
LET $userGroupMapping := HASH_GET([Session Data], "User to Group Mapping");

LET $modules := 
ARRAY_TRANSPOSE( 
  ARRAY(
    EACH [Module Name], 
    EACH [Module Settings EForm],
    EACH [Module Access]
  )
);

LET $modules:= 
ARRAY_WHERE(
  $modules, $(i,item) 
  AND(
    ARRAY_INDEX($item, 1) <> NULL,
    [Module Type]#$i = "Module"
  )
);

LET $data :=
ARRAY_SELECT(
	$modules, $(m, module)

	LET $moduleName := 	ARRAY_INDEX($module, 1); 
	LET $moduleSettings :=	ARRAY_INDEX($module, 2); 
	LET $settingPage :=	"records/" & URLENCODE($moduleSettings) & "&[RecorDID]=1"; 	
	LET $accessList := ARRAY_INDEX($module, 3); 

	LET $hasAccess :=
	ARRAY_LENGTH(
		ARRAY_WHERE(
			$accessList, $(i, item) 
			ARRAY_LENGTH(
				ARRAY_WHERE(
					$item, $(i, item) 
				  	HASH_GET($userGroupMapping, $item)= TRUE
				)
			) > 0
		)
	) > 0;

	RETURN
	IF( 
		$hasAccess == TRUE, 
		ARRAY(
			$moduleName,
			$moduleSettings
		)
	)
);


LET $modulesPlus:= 
ARRAY_CONCAT(
  $data, 
  IF([Global Settings in Navigation] = TRUE, ARRAY(ARRAY("Settings - Global", "Global Settings"))),
  IF([Automation in Navigation] = TRUE, ARRAY(ARRAY("Settings - Automation", "Settings - Automation")))
);


RETURN 
ARRAY_WHERE(
  $modulesPlus, $(i, item)
  $item <> NULL()
)

LET $svglist:= 
ARRAY_SELECT(
  ARRAY_SORT(HASH_KEYS([Svg Map]), $(item) $item), $(i,item) 
  	HASH( 
	  	"value", $item, 
	  	"text", $item & ".svg" 
  	)
  );

RETURN $svglist

//Used to identify kind of data this is in the recieving system 
LET $typeOfData := "Contracts"; 

//Used to find the 'Integration Event' record that list the last time this type of package was sent
LET $eventType := "Client Central Reporting - Contract"; 

//Pull settings related to this package
LET $targetForm := "Settings - Contract"; 

//Used to determine standard data mappings - note that a 'result' will be sent for each matching record
LET $primaryForm := "Contract";

//records of primary form that should be excluded
LET $primaryFormFilter := "[Contract Number] <> NULL";

LET $settingsColumns := 
ARRAY(
  "Central Reporting Fields"
);

LET $primaryFormColumns := DB_GETCOLUMNS($primaryForm); 

LET $settings := 
INDEXES_TO_KEYS(
  DB_LOOKUP(
    $settingsColumns,  
    $targetForm
  ), 
  $settingsColumns
); 

//Data to actually send
LET $toSend := HASH_GET($settings, "Central Reporting Fields"); 

//Getting data that is not literally stored in a column
LET $calculatedGets := 
ARRAY_WHERE(
  $toSend, $(i, item) 
  ARRAY_INDEXOF($primaryFormColumns, $item) = NULL
);

//Full list of supported of data to send
LET $possibleLookups := 
ARRAY_CONCAT(
  $primaryFormColumns,
  $calculatedGets //custom items
);

//Confirm that what is being sent is in the supported list
LET $lookupMap := 
ARRAY_WHERE(
  $toSend, $(i, item) 
  ARRAY_INDEXOF($possibleLookups, $item) > 0
);

LET $pushOnlyUpdated := 
COALESCE(
  HASH_GET($eventargs, "GetOnlyRecentlyUpdated"), 
  TRUE
);

LET $primaryFormColumns := DB_GETCOLUMNS($primaryForm); 

//Getting data that is literally stored in a column of the same name
LET $standardGets := ARRAY_WHERE($lookupMap, $(i, item) ARRAY_INDEXOF($primaryFormColumns, $item) <> NULL);

//Getting data that is not literally stored in a column
LET $calculatedGets := ARRAY_WHERE($lookupMap, $(i, item) ARRAY_INDEXOF($primaryFormColumns, $item) = NULL);

//Map the name sent to the column that it will import into in the target system
//If the outgoing name matches the target column then no mapping required
LET $standardGetsToHashkey := 
  ARRAY_SELECT(
  $standardGets, $(i, column) 
  IF( 
    $column = "EFormType", "Source EFormType", 
    $column = "RecordID", "Source RecordID",  
    $column = "Originator", "Created By",  
    $column = "DateCreated", "Created", 
    $column = "Existing Agreement Name", "Sourced Under Existing Agreement Name", 
    $column
  )
); 

//same as above but for custom components
LET $customGetsToHashkey := 
ARRAY_SELECT(
  $calculatedGets, $(i, column) 
  IF( 
    $column = "Test Value", "Example Test Value",
	$column
  )
); 

LET $customGetsToHashkey := 
ARRAY_WHERE(
  $customGetsToHashkey, $(i, column) 
  ARRAY_INDEXOF($toSend, $column) > 0
); 

LET $fromDate := 
DB_LOOKUP(
  "Datetime of Event", 
  "Integration Event", 
  "[Event Type] FAST_EQUAL_TO " & DB_ENCODECONSTANT($eventType),
  HASH("sort", "[Datetime of Event] DESC")
); 

LET $actualLookupFields := 
ARRAY_DISTINCT(
  ARRAY_CONCAT(
    ARRAY(
	  "RecordID", 
	  "Contract Manager", 
	  "Contract Manager Display Name", 
	  "Vendor RecordID"
	), 
	$standardGets
  )
);

//Get recently updated data for primary form 
LET $allData := 
DB_LOOKUPALL(
  $actualLookupFields,
  $primaryForm,
  $primaryFormFilter & " AND [LineNumber] FAST_EQUAL_TO 1" & 
  IF(
	//FALSE,
	AND($pushOnlyUpdated = TRUE, $fromDate <> NULL), 
	" AND [DateLastUpdated] > " & DB_ENCODECONSTANT(N($fromDate))
  )
); 

LET $contractRecordIDs := ARRAY_FIRST(ARRAY_TRANSPOSE($allData));

//One-time lookups here
//---Contract Manager Details----//
LET $getContractManagers :=
ARRAY_SELECT(
  $allData, $(i,item) 
  ARRAY(
	ARRAY_INDEX($item, ARRAY_INDEXOF($actualLookupFields, "RecordID")),
	ARRAY_INDEX($item, ARRAY_INDEXOF($actualLookupFields, "Contract Manager")),
	ARRAY_INDEX($item, ARRAY_INDEXOF($actualLookupFields, "Contract Manager Display Name"))
  )
);

LET $contractManagerDetails :=
HASH_MERGE(
  ARRAY_SELECTMANY(
	$getContractManagers, $(i, item)
	LET $recordID := ARRAY_INDEX($item, 1);
	LET $username := ARRAY_INDEX($item, 2);
	LET $name := ARRAY_INDEX($item, 3);
	LET $position := EBMS_GET_USER_POSITION($username);

	RETURN 
	HASH(
	  $recordID, 
	  ARRAY(
		HASH(
		 "Contract Manager Name", $name,
		 "Contract Manager Position", $position,
		 "Contract Manager Email", $username
		)
	  )
	)
  )
);

LET $contractManagerData :=
IF(
  ARRAY_INDEXOF($lookupMap, "Contract Manager Details") > 0,
	$contractManagerDetails,
  NULL
);

LET $getData := $(type)
	(
	  LET $data := 
	  IF(
		//Audit data
		AND(
		  ARRAY_LENGTH($contractRecordIDs) > 0,
		  $type = "Audit Data"
		),
        (
		  LET $data := 
          DB_LOOKUPALL(
            ARRAY(
              "RelatedID", 
          	  "ActualUsername", 
          	  "DateCreated" 
            ),
            "sys.Audit", 
            "[RelatedEForm] FAST_EQUAL_TO 'Contract'" &
            " AND [RelatedID] IN " & DB_ENCODECONSTANT($contractRecordIDs) & 
            " AND [ActualUsername] <> 'system'" & 
            " AND [Action] FAST_EQUAL_TO 'Updated eForm record'", 
            HASH("sort", "[DateCreated] DESC")
          );

          LET $hashed :=  
          ARRAY_TO_HASH_GROUPED(
            $data, 
            $(i, item) ARRAY_INDEX($item, 1),
            $(i, item) ARRAY_SKIP($item, 1) 
          );

          RETURN 
          ARRAY_SELECT(
            HASH_KEYS($hashed), $(i, item) 
            ARRAY_CONCAT(
              $item, 
          	ARRAY_FIRST(HASH_GET($hashed, $item))
            )
          )
		),	

		//Milestone data
		AND(
		  ARRAY_LENGTH($contractRecordIDs) > 0,
		  $type = "Milestone Data"
		),
		DB_LOOKUPALL(
		  ARRAY(
		  	"Contract RecordID",
		  	"Milestone",
		  	"Planned Date",
		  	"Date", //Actual Date
		    "Status"
		  ),
		  "Milestone",
		  "[LineNumber] FAST_EQUAL_TO 1 AND [Contract RecordID] IN " & DB_ENCODECONSTANT($contractRecordIDs)
		),

		//Variation data
		AND(
		  ARRAY_LENGTH($contractRecordIDs) > 0,
		  $type = "Variation Data",
		),
		DB_LOOKUPALL(
		  ARRAY(
		    "Contract RecordID",
		    "Type Description",
		    "Date Approved",
		    "Variation Value inc Tax",
		    "Status", 
			"End Date After Exercised" 
			//"Variation Value"
		  ),
		  "Variation",
		  "([Variation to Price] FAST_EQUAL_TO TRUE OR [Variation to Terms] FAST_EQUAL_TO TRUE) AND " & 
		  "([Status] FAST_EQUAL_TO 'Approved' OR [Status] FAST_EQUAL_TO 'Draft') AND " & 
		  "[LineNumber] FAST_EQUAL_TO 1 AND " & 
		  "[Contract RecordID] IN " & DB_ENCODECONSTANT($contractRecordIDs)
		),

		//Questionnaire data
		OR(
		  $type = "Contract Questionnaire Data",
		  $type = "Vendor Questionnaire Data",
		), 
		DB_LOOKUPALL(
		  ARRAY(
			"Context RecordID",
			"Question", 
			"Answer",
			"Comments"
		  ),
		  "Questionnaire",
		  "[Scoring Type] FAST_EQUAL_TO 'Supplementary Data' AND [Context Form] FAST_EQUAL_TO '" & IF($type = "Contract Questionnaire Data", "Contract", $type = "Vendor Questionnaire Data", "Vendor") & "' AND [Context RecordID] <> NULL"
		)
	  );

	  LET $groupedData := 
	  ARRAY_TO_HASH_GROUPED(
		$data, 
		$(k, key) ARRAY_FIRST($key),
		$(v, value) ARRAY_SKIP($value, 1)
	  );

	  LET $keys := HASH_KEYS($groupedData);

	  LET $dataHashed :=
		HASH_MERGE(
		  ARRAY_SELECTMANY(
			$keys, $(k, key) 
			  LET $keyRecordID := $key;
			  LET $data := HASH_GET($groupedData, $key);
			  LET $dataFormatted := 
			    ARRAY_SELECT(
			      $data, $(i, item)
			      IF( 
			        $type = "Audit Data",
					HASH(
					  "Modified By", 	 ARRAY_INDEX($item, 1),
					  "Modified",        ARRAY_INDEX($item, 2)
					),

					$type = "Milestone Data",
					HASH(
					  "Milestone", 	      ARRAY_INDEX($item, 1),
					  "Planned Date",     ARRAY_INDEX($item, 2),
					  "Actual Date", 	  ARRAY_INDEX($item, 3),
					  "Status", 		  ARRAY_INDEX($item, 4)
					),

					$type = "Variation Data",
			        HASH(
			          "Variation Type",   ARRAY_INDEX($item, 1),
			          "Date Approved",    ARRAY_INDEX($item, 2),
			          "Variation Value inc Tax",  ARRAY_INDEX($item, 3),
			          "Status", 		  ARRAY_INDEX($item, 4)
			        ),

			        OR( 
			          $type = "Vendor Questionnaire Data",
			          $type = "Contract Questionnaire Data"
			        ),
			        HASH(
			          "Question",   	  ARRAY_INDEX($item, 1),
			          "Answer",     	  ARRAY_INDEX($item, 2),
			          "Comments",   	  ARRAY_INDEX($item, 3)
			        )
				  )
			    );
			RETURN HASH($keyRecordID, ARRAY($dataFormatted))
		  )	
		); 

	   RETURN $dataHashed
	);

LET $auditData := 
IF(
  OR(
	ARRAY_INDEXOF($lookupMap, "Modified") > 0,
    ARRAY_INDEXOF($lookupMap, "Modified By") > 0
  ),
	CALL($getData, "Audit Data"),
  NULL
);

LET $milestoneData := 
IF(
  ARRAY_INDEXOF($lookupMap, "Milestone Data") > 0,
	CALL($getData, "Milestone Data"),
  NULL
);

LET $variationData := 
IF(
  ARRAY_INDEXOF($lookupMap, "Variation Data") > 0,
	CALL($getData, "Variation Data"),
  NULL
);

LET $vendorQuestionnaireData := 
IF(
  ARRAY_INDEXOF($lookupMap, "Vendor Questionnaire Data") > 0,
	CALL($getData, "Vendor Questionnaire Data"),
  NULL
);

LET $contractQuestionnaireData := 
IF(
  ARRAY_INDEXOF($lookupMap, "Questionnaire Data") > 0,
	CALL($getData, "Contract Questionnaire Data"),
  NULL
);

//Used to define custom data mappings
LET $customDefinitions := $(name, contractRecordID, vendorRecordID, data)(

	 LET $result := 
	  IF(
		//AND(
		//  HASH_GET($data, "Existing Agreement RecordID") <> NULL, 
		//  $name = "Sourced Under Existing Agreement Name"
		//), 
		//COALESCE(DB_LOOKUP("Contract Title and Number", "Contract", HASH("RecordID", HASH_GET($data, "Existing Agreement RecordID"))), ""),

		$name = "Modified By",
		COALESCE(HASH_GET(ARRAY_FIRST(ARRAY_FIRST(HASH_GET($auditData, T($contractRecordID)))), "Modified By"), ""),

		$name = "Modified",
		COALESCE(HASH_GET(ARRAY_FIRST(ARRAY_FIRST(HASH_GET($auditData, T($contractRecordID)))), "Modified"), ""),

		$name = "Milestone Data",
		COALESCE(HASH_GET($milestoneData, $contractRecordID), ARRAY()),

		$name = "Variation Data",
		COALESCE(HASH_GET($variationData, $contractRecordID), ARRAY()),

		$name = "Vendor Questionnaire Data",
		COALESCE(HASH_GET($vendorQuestionnaireData, $vendorRecordID), ARRAY()),

		$name = "Questionnaire Data",
		COALESCE(HASH_GET($contractQuestionnaireData, $contractRecordID), ARRAY()),

		$name = "Contract Manager Details", 
		COALESCE(HASH_GET($contractManagerData, $contractRecordID), ARRAY()),

	    $name = "Contract Has Variations", 
		COALESCE(HASH_GET($variationData, $contractRecordID) <> NULL, ARRAY()),

		$name = "Number of Employees",
		COALESCE(DB_LOOKUP("Number of Employees", "Vendor", HASH("RecordID", $vendorRecordID)), ""),

		$name = "Supplier Location",
		COALESCE(DB_LOOKUP("Full Address", "Vendor", HASH("RecordID", $vendorRecordID)), "")
	  );

	  RETURN 
	  COALESCE(
		$result, 
		"Custom lookup not defined (" & $name & ")"
	  )
	);  

LET $hashed := 
	ARRAY_SELECT(
	  $allData, $(i, data)
	  LET $hashedData := INDEXES_TO_KEYS($data, $actualLookupFields);
	  LET $contractRecordID := ARRAY_INDEX($data, ARRAY_INDEXOF($actualLookupFields, "RecordID"));
	  LET $vendorRecordID := ARRAY_INDEX($data, ARRAY_INDEXOF($actualLookupFields, "Vendor RecordID"));

	  //hash all data that is directly captured from a column on target form
	  LET $standardData := 
	  INDEXES_TO_KEYS(
		ARRAY_SELECT(
		  $standardGets, $(g, get) 
		  HASH_GET($hashedData, $get)
		), 
		$standardGetsToHashkey
	  );

	  //pulls the custom definitions into the set
	  LET $calculatedData := 
	  INDEXES_TO_KEYS(
		ARRAY_SELECT(
		  $calculatedGets, $(g, get)
		  CALL($customDefinitions, $get, $contractRecordID, $vendorRecordID, $hashedData)
		), 
		$customGetsToHashkey
	  );

	  RETURN 
	  HASH_MERGE(
		$standardData, 
		$calculatedData, 
		HASH(
		  "System Owner Name", [Customer Name],
		  "System Owner Address", [Full Address]
		)
	  )
	);

RETURN $hashed

HttpRequest.BodyTemplate:
{

//-----------------------------
//---- TAILOR 
//-----------------------------

//Used to identify kind of data this is in the recieving system 
LET $typeOfData := "Summary Data"; 

//Used to find the 'Integration Event' record that list the last time this type of package was sent
LET $eventType := "Client Central Reporting - Summary"; 

//Used to pull settings related to this package
LET $targetForm := "Global Settings"; 

//Used to determine standard data mappings
//Note that a 'result' will be sent for each matching record
LET $primaryForm := "Global Settings";

//records of primary form that should be excluded
LET $primaryFormFilter := "[RecordID] FAST_EQUAL_TO 1";

LET $customLookups := 
ARRAY(
  "Test Value", 
  "Number of Contracts", 
  "Total Value of Contracts"
);

//Used to define custom data mappings
LET $customDefinitions := $(name)(
  LET $result := 
  IF(
    $name = "Number of Contracts", 
    ARRAY_FIRST(ARRAY_FIRST(DB_REPORT("COUNT([RecordID])", "Contract", "[LineNumber] = 1"))),

	$name = "Total Value of Contracts", 
    ARRAY_FIRST(ARRAY_FIRST(DB_REPORT("SUM([Total Contract Value])", "Contract", "[LineNumber] = 1"))),

	$name = "Test Value", 
    "This is a test value"
  );

  RETURN 
  COALESCE(
    $result, 
    "Custom lookup not defined (" & $name & ")"
  )
); 

LET $pushOnlyUpdated := 
//TRUE;
FALSE;

//-----------------------------
//---- STANDARD
//-----------------------------

LET $primaryFormColumns := DB_GETCOLUMNS($primaryForm); 

LET $settingsColumns := 
ARRAY(
  //"Central Reporting Enabled", 
  //"Reporting System URL",
  "Client Central Reporting Fields"
);

LET $settings := 
INDEXES_TO_KEYS(
  DB_LOOKUP(
    $settingsColumns,  
    $targetForm
  ), 
  $settingsColumns
); 

//Full list of supported of data to send
LET $possibleLookups := 
ARRAY_CONCAT(
  $primaryFormColumns,
  $customLookups //custom items
);

//Data to actually send
LET $toSend := HASH_GET($settings, "Client Central Reporting Fields"); 

//Confirm that what is being sent is in the supported list
LET $lookupMap := 
ARRAY_WHERE(
  $toSend, $(i, item) 
  ARRAY_INDEXOF($possibleLookups, $item) > 0
);

//Getting data that is literally stored in a column of the same name
LET $standardGets := ARRAY_WHERE($lookupMap, $(i, item) ARRAY_INDEXOF($primaryFormColumns, $item) <> NULL);

//Getting data that is not literally stored in a column
LET $calculatedGets := ARRAY_WHERE($lookupMap, $(i, item) ARRAY_INDEXOF($primaryFormColumns, $item) = NULL);
LET $calculatedGets := ARRAY_WHERE($customLookups, $(i, item) ARRAY_INDEXOF($calculatedGets, $item) <> NULL);

//Map the name sent to the column that it will import into in the target system
//If the outgoing name matches the target column then no mapping required
LET $standardGetsToHashkey := 
  ARRAY_SELECT(
  $standardGets, $(i, column) 
  IF( 
    $column = "EFormType", "Source EFormType", 
    $column = "RecordID", "Source RecordID", 
    $column
  )
); 

//same as above but for custom components
LET $customGetsToHashkey := 
  ARRAY_SELECT(
  $customLookups, $(i, column) 
  IF( 
    $column = "Test Value", "Example Test Value",
    $column
  )
); 

LET $fromDate := 
DB_LOOKUP(
  "Datetime of Event", 
  "Integration Event", 
  "[Event Type] = " & DB_ENCODECONSTANT($eventType),
  HASH("sort", "[Datetime of Event] DESC")
); 

//Get recently updated data for primary form 
LET $allData := 
DB_LOOKUPALL(
  $primaryFormColumns, 
  $primaryForm,
  $primaryFormFilter & " AND [LineNumber] FAST_EQUAL_TO 1" & 
  IF(
    AND($pushOnlyUpdated = TRUE, $fromDate <> NULL), 
    " AND [DateLastUpdated] > " & DB_ENCODECONSTANT(N($fromDate))
  )
); 

LET $hashed := 
ARRAY_SELECT(
  $allData, $(i, data)
  LET $hashedData := INDEXES_TO_KEYS($data, $primaryFormColumns);

  //hash all data that is directly captured from a column on target form
  LET $standardData := 
  INDEXES_TO_KEYS(
    ARRAY_SELECT(
      $standardGets, $(g, get) 
      HASH_GET($hashedData, $get)
    ), 
    $standardGetsToHashkey
  );

  //pulls the custom definitions into the set
  LET $calculatedData := 
  INDEXES_TO_KEYS(
    ARRAY_SELECT(
      $calculatedGets, $(g, get)
	  CALL($customDefinitions, $get)
	), 
	$customGetsToHashkey
  );

  RETURN 
  HASH_MERGE(
    $standardData, 
    $calculatedData
  )
);

LET $result := 
JSONENCODE(
  HASH(
    "data", $hashed,
    "typeOfData", $typeOfData,
    "reportingSystem", EBMS_GET_BASE_URL()
  )
);

RETURN $result
}

{

//-----------------------------
//---- TAILOR 
//-----------------------------

//Used to identify kind of data this is in the recieving system 
LET $typeOfData := "Summary Data"; 

//Used to find the 'Integration Event' record that list the last time this type of package was sent
LET $eventType := "Central Reporting - Summary"; 

//Used to pull settings related to this package
LET $targetForm := "Global Settings"; 

//Used to determine standard data mappings
//Note that a 'result' will be sent for each matching record
LET $primaryForm := "Global Settings";

//records of primary form that should be excluded
LET $primaryFormFilter := "[RecordID] FAST_EQUAL_TO 1";

LET $customLookups := 
ARRAY(
    "System Version",
    "Product Type", 
    "Timezone", 

	//Contracts
	"Number of Active Contracts",
	"Total Value of Active Contracts",
	"Number of Contracts",
	"Total Value of Contracts",

	//Planning
	"Number of Completed Procurements", 
	"Estimated Value of Completed Procurements",	
	"Number of Active Procurements", 
	"Estimated Value of Active Procurements", 

	//Evaluations
	"Number of Completed Evaluations", 	
	"Total Value of Completed Evaluations", 
	"Number of Active Evaluations", 
	"Total Value of Active Evaluations", 

	//Approvals
	"Number of Submissions Approved", 	
	"Total Value of Submissions Approved", 

	//Users 
	"Users in Admin Group",
    "User Logins in Last Month",
    "Login Counts Per Day",  
    "Count of Distinct User Logins in Last 7 Days",  
    //"Count of Distinct User Logins in Last Month"//,

  	//Custom Field Success Metrics
	"Total Number of Contract Custom Fields",
	"Number of Custom Contract Fields Updated in Last 6 Months",
	"Number of Contracts with Custom Field Data",
	"Number of Contracts with all Custom Fields Complete",

	"Total Number of Vendor Custom Fields",
	"Number of Custom Vendor Fields Updated in Last 6 Months",
	"Number of Vendors with Custom Field Data",
	"Number of Vendors with all Custom Fields Complete"
	//"Test Value"
);

LET $getDisplayName := $(rolename) 
(
  DB_LOOKUP(
    "DisplayName", 
	"sys.Users", 
	"[Username] FAST_EQUAL_TO " & DB_ENCODECONSTANT($rolename)
  )
);

LET $removeSystemUsers := $(list)
(
  ARRAY_WHERE(
    $list, $(i,item) 
	LET $username := ARRAY_INDEX($item, 1); 
	RETURN 
	AND(
	  EBMS_CONTAINS($username, "vendorpanel") <> TRUE,
	  $username <> "system",
	  $username <> "external", 
	  $username <> "automatedTester",
	  $username <> "emailapproval",
	  EBMS_GET_USER_TYPE($username) <> "GROUP"
	)
  )
);

LET $loginCountsPerDay := 
DB_REPORT(
  ARRAY(
	//"[ActualUsername]", 
	"COUNT([ActualUsername])", 
	"TO_DAY([DateCreated])"
  ), 
  "sys.Audit", 
  "[Action] FAST_EQUAL_TO 'Login' AND [DateCreated] >= (CURRENT_DATE - 7) AND LEFT([ActualUsername], 4) <> 'api-' AND [ActualUsername] NOT IN ('automatedTester', 'system', 'external', 'emailapproval')"
);

//LET $distinctUserLoginsInTheLast7Days := ARRAY_LENGTH($loginCountsPerDay);

LET $userLoginsInTheLastMonth := 
DB_REPORT(
  ARRAY(
    "[ActualUsername]",
	"COUNT([ActualUsername])"
  ), 
  "sys.Audit", 
  "[Action] FAST_EQUAL_TO 'Login' AND [DateCreated] >= (CURRENT_DATE - 30)"
);

LET $userLoginsInTheLastMonth := CALL($removeSystemUsers, $userLoginsInTheLastMonth); 

LET $userLoginsInTheLastMonth := 
ARRAY_SELECT(
  $userLoginsInTheLastMonth, $(i, item) 
  ARRAY_CONCAT(
    $item,
	CALL($getDisplayName, ARRAY_FIRST($item))
  )
);

LET $totalDistinctLoginsInLastMonth := ARRAY_LENGTH($userLoginsInTheLastMonth);

//Users in Admin Group that aren't VP users
LET $usersInAdminGroup := EBMS_GET_DESCENDANT_ROLES("Administrators");

LET $usersInAdminGroup := 
ARRAY_SELECT(
  $usersInAdminGroup, $(i, item) 
  ARRAY_CONCAT(
    $item,
	CALL($getDisplayName, ARRAY_FIRST($item))
  )
);

LET $usersInAdminGroup := CALL($removeSystemUsers, $usersInAdminGroup); 

//Used to define custom data mappings
LET $customDefinitions := $(name)(
  LET $result := 
  IF(
	$name = "System Version", 
    DB_LOOKUP("Version", "System Version", "", HASH("sort", "[DateCreated] DESC")),

	$name = "Product Type", 
    IF(FIND("CompareCoOps", EBMS_GET_BASE_URL()) > 0, "CompareCoOps", "CORE"),

	$name = "Timezone", 
    (
      LET $UTC := D(ARRAY_FIRST(ARRAY_FIRST(DB_REPORT("CAST(CURRENT_DATE AS STRING)", "sys.Roles", HASH(), HASH("autoGroup", FALSE, "limit", 1)))));
      LET $local := NOW();
      LET $offset := ROUND(($local - $UTC) * 24, 4);
	  RETURN $offset
	),

	//Contracts
	$name = "Number of Active Contracts", 
    ARRAY_FIRST(ARRAY_FIRST(DB_REPORT("COUNT([RecordID])", "Contract", "[Status] FAST_EQUAL_TO 'Active' AND [LineNumber] FAST_EQUAL_TO 1"))),

	$name = "Total Value of Active Contracts", 
    ARRAY_FIRST(ARRAY_FIRST(DB_REPORT("SUM([Total Contract Value])", "Contract", "[Status] FAST_EQUAL_TO 'Active' AND [LineNumber] FAST_EQUAL_TO 1"))),

	$name = "Number of Contracts", 
    ARRAY_FIRST(ARRAY_FIRST(DB_REPORT("COUNT([RecordID])", "Contract", "[LineNumber] FAST_EQUAL_TO 1"))),

	$name = "Total Value of Contracts", 
    ARRAY_FIRST(ARRAY_FIRST(DB_REPORT("SUM([Total Contract Value])", "Contract", "[LineNumber] FAST_EQUAL_TO 1"))),

	//Planning
	$name = "Number of Completed Procurements", 
    ARRAY_FIRST(ARRAY_FIRST(DB_REPORT("COUNT([RecordID])", "Procurement", "([Status] FAST_EQUAL_TO 'Awarded') AND [LineNumber] FAST_EQUAL_TO 1"))),

	$name = "Estimated Value of Completed Procurements", 
    ARRAY_FIRST(ARRAY_FIRST(DB_REPORT("SUM([Estimated Value])", "Procurement", "([Status] FAST_EQUAL_TO 'Awarded') AND [LineNumber] FAST_EQUAL_TO 1"))),

	$name = "Number of Active Procurements", 
    ARRAY_FIRST(ARRAY_FIRST(DB_REPORT("COUNT([RecordID])", "Procurement", "[Status] IN ('Setup', 'Planning', 'Evaluating', 'Advertising', 'Awarding') AND [LineNumber] FAST_EQUAL_TO 1"))),

	$name = "Estimated Value of Active Procurements", 
    ARRAY_FIRST(ARRAY_FIRST(DB_REPORT("SUM([Estimated Value])", "Procurement", "[Status] IN ('Setup', 'Planning', 'Evaluating', 'Advertising', 'Awarding') AND [LineNumber] FAST_EQUAL_TO 1"))),

	//Evaluations
	$name = "Number of Completed Evaluations", 
    ARRAY_FIRST(ARRAY_FIRST(DB_REPORT("COUNT([RecordID])", "Evaluation", "[Status] IN ('Finished') AND [LineNumber] FAST_EQUAL_TO 1"))),

	$name = "Total Value of Completed Evaluations", 
    ARRAY_FIRST(ARRAY_FIRST(DB_REPORT("SUM([Budget])", "Evaluation", "[Status] IN ('Finished') AND [LineNumber] FAST_EQUAL_TO 1"))),

	$name = "Number of Active Evaluations", 
    ARRAY_FIRST(ARRAY_FIRST(DB_REPORT("COUNT([RecordID])", "Evaluation", "[Status] IN ('Started', 'Results', 'Prices', 'In Progress', 'Evaluators', 'Criteria', 'Consensus') AND [LineNumber] FAST_EQUAL_TO 1"))),

	$name = "Total Value of Active Evaluations", 
    ARRAY_FIRST(ARRAY_FIRST(DB_REPORT("SUM([Budget])", "Evaluation", "[Status] IN ('Started', 'Results', 'Prices', 'In Progress', 'Evaluators', 'Criteria', 'Consensus') AND [LineNumber] FAST_EQUAL_TO 1"))),

	//Approvals
	$name = "Number of Submissions Approved", 
    ARRAY_FIRST(ARRAY_FIRST(DB_REPORT("COUNT([RecordID])", "Submission", "[Status] FAST_EQUAL_TO 'Approved' AND [LineNumber] FAST_EQUAL_TO 1"))),

	$name = "Total Value of Submissions Approved", 
    ARRAY_FIRST(ARRAY_FIRST(DB_REPORT("SUM([Submission Value])", "Submission", "[Status] FAST_EQUAL_TO 'Approved' AND [LineNumber] FAST_EQUAL_TO 1"))),

	//Users 
	$name = "Users in Admin Group", 
    $usersInAdminGroup,

	$name = "User Logins in Last Month", 
    $userLoginsInTheLastMonth,

	$name = "Login Counts Per Day", 
    $loginCountsPerDay,

	//$name = "Count of Distinct User Logins in Last 7 Days", 
    //$distinctUserLoginsInTheLast7Days,

	$name = "Count of Distinct User Logins in Last Month", 
    $totalDistinctLoginsInLastMonth,

	//Contract Custom Fields
	$name = "Total Number of Contract Custom Fields",
    ARRAY_FIRST(ARRAY_FIRST(DB_REPORT("COUNT([RecordID])", "Standard Question", "[LineNumber] FAST_EQUAL_TO 1 AND [Linked Entity EFormType] FAST_EQUAL_TO 'Standard Questionnaire' AND [Questionnaire] FAST_EQUAL_TO 'Supplementary Contract Details'"))),

	$name = "Number of Custom Contract Fields Updated in Last 6 Months",
    ARRAY_FIRST(ARRAY_FIRST(DB_REPORT(
		"COUNT([RecordID])",
		"Standard Question",
		"[LineNumber] FAST_EQUAL_TO 1 AND [Linked Entity EFormType] FAST_EQUAL_TO 'Standard Questionnaire' AND [Questionnaire] FAST_EQUAL_TO 'Supplementary Contract Details' AND [DateLastUpdated] > " & DB_ENCODECONSTANT(DATEADD("month", -6, NOW()))
	))),

	$name = "Number of Contracts with Custom Field Data",
	(
		LET $data := DB_REPORT(ARRAY("[RecordID]","COUNT([Answer])"), "Questionnaire", "[Standard Questionnaire Name] FAST_EQUAL_TO 'Supplementary Contract Details' AND [Supplementary Data Type] FAST_EQUAL_TO 'Contract Data'");
		LET $answered := ARRAY_LENGTH(ARRAY_WHERE($data, $(i,item) ARRAY_INDEX($item,2) > 0));
		RETURN $answered
	),

	$name = "Number of Contracts with all Custom Fields Complete",
    (
	  LET $data := DB_REPORT(ARRAY("[RecordID]", "COUNT([Question])", "COUNT([Answer])"), "Questionnaire", "[Standard Questionnaire Name] FAST_EQUAL_TO 'Supplementary Contract Details' AND [Supplementary Data Type] FAST_EQUAL_TO 'Contract Data'");
	  LET $complete := ARRAY_LENGTH(ARRAY_WHERE($data, $(i,item) ARRAY_INDEX($item,2) = ARRAY_INDEX($item,3)));
	  RETURN $complete
	),

	//Vendor Custom Fields
	$name = "Total Number of Vendor Custom Fields",
    ARRAY_FIRST(ARRAY_FIRST(DB_REPORT("COUNT([RecordID])", "Standard Question", "[LineNumber] FAST_EQUAL_TO 1 AND [Linked Entity EFormType] FAST_EQUAL_TO 'Standard Questionnaire' AND [Questionnaire] FAST_EQUAL_TO 'Supplementary Supplier Details'"))),

	$name = "Number of Custom Vendor Fields Updated in Last 6 Months",
    ARRAY_FIRST(ARRAY_FIRST(DB_REPORT(
		"COUNT([RecordID])",
		"Standard Question",
		"[LineNumber] FAST_EQUAL_TO 1 AND [Linked Entity EFormType] FAST_EQUAL_TO 'Standard Questionnaire' AND [Questionnaire] FAST_EQUAL_TO 'Supplementary Supplier Details' AND [DateLastUpdated] > " & DB_ENCODECONSTANT(DATEADD("month", -6, NOW()))
	))),

	$name = "Number of Vendors with Custom Field Data",
	(
		LET $data := DB_REPORT(ARRAY("[RecordID]","COUNT([Answer])"), "Questionnaire", "[Standard Questionnaire Name] FAST_EQUAL_TO 'Supplementary Supplier Details' AND [Supplementary Data Type] FAST_EQUAL_TO 'Supplier Data'");
		LET $answered := ARRAY_LENGTH(ARRAY_WHERE($data, $(i,item) ARRAY_INDEX($item,2) > 0));
		RETURN $answered
	),

	$name = "Number of Vendors with all Custom Fields Complete",
    (
	  LET $data := DB_REPORT(ARRAY("[RecordID]", "COUNT([Question])", "COUNT([Answer])"), "Questionnaire", "[Standard Questionnaire Name] FAST_EQUAL_TO 'Supplementary Supplier Details' AND [Supplementary Data Type] FAST_EQUAL_TO 'Supplier Data'");
	  LET $complete := ARRAY_LENGTH(ARRAY_WHERE($data, $(i,item) ARRAY_INDEX($item,2) = ARRAY_INDEX($item,3)));
	  RETURN $complete
	)

	//$name = "Test Value", 
    //"This is a test value"
  );

  RETURN 
  COALESCE(
    $result, 
    0//"Custom lookup not defined (" & $name & ")"
  )
); 

LET $pushOnlyUpdated := 
//TRUE;
FALSE;

//-----------------------------
//---- STANDARD
//-----------------------------

LET $primaryFormColumns := DB_GETCOLUMNS($primaryForm); 

LET $settingsColumns := 
ARRAY(
  //"Central Reporting Enabled", 
  //"Reporting System URL",
  "Central Reporting Fields"
