  ARRAY_SELECT(
  $standardGets, $(i, column) 
  IF( 
    $column = "EFormType", "Source EFormType", 
    $column = "RecordID", "Source RecordID", 
    $column
  )
); 

//same as above but for custom components
LET $customGetsToHashkey := 
  ARRAY_SELECT(
  $customLookups, $(i, column) 
  IF( 
    $column = "Test Value", "Example Test Value",
    $column
  )
); 

LET $customGetsToHashkey := 
ARRAY_WHERE(
  $customGetsToHashkey, $(i, column) 
  ARRAY_INDEXOF($toSend, $column) > 0
); 

LET $fromDate := 
DB_LOOKUP(
  "Datetime of Event", 
  "Integration Event", 
  "[Event Type] FAST_EQUAL_TO " & DB_ENCODECONSTANT($eventType),
  HASH("sort", "[Datetime of Event] DESC")
); 

//Get recently updated data for primary form 
LET $allData := 
DB_LOOKUPALL(
  $primaryFormColumns, 
  $primaryForm,
  $primaryFormFilter & " AND [LineNumber] FAST_EQUAL_TO 1" & 
  IF(
    AND($pushOnlyUpdated = TRUE, $fromDate <> NULL), 
    " AND [DateLastUpdated] > " & DB_ENCODECONSTANT(N($fromDate))
  )
); 

LET $hashed := 
ARRAY_SELECT(
  $allData, $(i, data)
  LET $hashedData := INDEXES_TO_KEYS($data, $primaryFormColumns);

  //hash all data that is directly captured from a column on target form
  LET $standardData := 
  INDEXES_TO_KEYS(
