      $standardGets, $(g, get) 
      HASH_GET($hashedData, $get)
    ), 
    $standardGetsToHashkey
  );

  //pulls the custom definitions into the set
  LET $calculatedData := 
  INDEXES_TO_KEYS(
    ARRAY_SELECT(
      $calculatedGets, $(g, get)
	  CALL($customDefinitions, $get)
	), 
	$customGetsToHashkey
  );

  RETURN 
  HASH_MERGE(
    $standardData, 
    $calculatedData
  )
);

LET $result := 
JSONENCODE(
  HASH(
    "data", $hashed,
    "typeOfData", $typeOfData,
    "reportingSystem", EBMS_GET_BASE_URL()
  )
);

RETURN $result
}

FieldEntry.Formula:
LET $numberToGenerate := 5; 

LET $maxRecordID := ARRAY_FIRST(
	ARRAY_FIRST(
		DB_REPORT(
			"MAX([RecordID])", 
			"Vendor"
		)
	)
); 

RETURN
ARRAY_SELECT(
	RANGE(0, $numberToGenerate), $(i, item)		
	HASH(
		"Company Name", ARRAY_INDEX([List of Random Vendor Names], CEILING(RAND() * ARRAY_LENGTH([List of Random Vendor Names]))), 
		"BusinessNumber", NUMBERFORMAT(CEILING(RAND() * 100000000), "000000000")
	)
)

LET $prefix := 
[Contract Number Prefix] & 
IF( 
	[Use Current Year Prefix], YEAR(NOW()) & "-",
	[Use Current Financial Year Prefix], FINANCIALYEAR(NOW()) & "-"
);

LET $contractNumber := N(REPLACESUBSTRING($prefix, [Next Contract Number], ""));

LET $numberToGenerate := 5;

RETURN
ARRAY_SELECT(	
	RANGE(0, $numberToGenerate), $(i, item)		
	LET $contractTitle := 
	ARRAY_INDEX([Actions for Contract Generation], CEILING(RAND()* ARRAY_LENGTH([Actions for Contract Generation])))
	& " of a " 
	& ARRAY_INDEX([Adjectives for Contract Generation], CEILING(RAND()* ARRAY_LENGTH([Adjectives for Contract Generation])))
	& " " 
	& ARRAY_INDEX([Nouns for Contract Generation], CEILING(RAND()* ARRAY_LENGTH([Nouns for Contract Generation])));

	LET $vendorDetails := 
	ARRAY_INDEX(
		[Random Vendor Data], 
		CEILING(RAND() * ARRAY_LENGTH([Random Vendor Data]))
	);

	LET $vendorRecordID := ARRAY_INDEX($vendorDetails, 1);
	LET $vendorName := ARRAY_INDEX($vendorDetails, 2);
	LET $vendorBusinessNumber := ARRAY_INDEX($vendorDetails, 3);
	LET $startDate :=  DATE(YEAR(NOW() - FLOOR(RAND() * 5) + 2), MONTH(NOW()) - FLOOR(RAND() * 5) + 2, FLOOR(RAND() * 28));
	LET $originalEndDate := DATE(YEAR($startDate), MONTH($startDate) + FLOOR(RAND() * 6), FLOOR(RAND() * 28));

	LET $departmentsList := 
		DB_LOOKUPALL(
			"Name", 
			"sys.Departments"
		);
	LET $departmentsCount := ARRAY_LENGTH($departmentsList);

	LET $department := ARRAY_INDEX($departmentsList, 1 + FLOOR(RAND() * $departmentsCount));

	RETURN
	HASH(
		"Contract Title", $contractTitle, 
		"Initial Contract Value Incl. Tax", N(ROUND(RAND() * 100, 0) & "000"), 
		"Contract Number", [Contract Number Prefix] & $contractNumber + $i, 
		"Start Date", $startDate,
		"Original End Date",  $originalEndDate, 
		"Current End Date",  $originalEndDate, 
		"BusinessNumber", $vendorBusinessNumber, 
		"Vendor Name", $vendorName, 
		"Vendor RecordID", $vendorRecordID, 
		"Department", $department
	)
)

LET $contractRecordIDs := [Contract RecordIDs];
LET $numberToGenerate := 8;
LET $consequences := ARRAY( "Insignificant", "Minor", "Moderate", "Major", "Catastrophic" );
LET $likelihoods := ARRAY( "Rare", "Unlikely", "Possible", "Likely", "Almost Certain" );
LET $generatedData := ARRAY_SELECT( RANGE(0, $numberToGenerate), $(i, item)
LET $risk := "Large " & ARRAY_INDEX([Adjectives for Contract Generation], CEILING(RAND()* ARRAY_LENGTH([Adjectives for Contract Generation]))) & " " & ARRAY_INDEX([Nouns for Contract Generation], CEILING(RAND()* ARRAY_LENGTH([Nouns for Contract Generation]))) & " Affecting " & ARRAY_INDEX([Adjectives for Contract Generation], CEILING(RAND()* ARRAY_LENGTH([Adjectives for Contract Generation]))) & " " & ARRAY_INDEX([Nouns for Contract Generation], CEILING(RAND()* ARRAY_LENGTH([Nouns for Contract Generation])));
LET $consequence := ARRAY_INDEX( $consequences, CEILING(RAND() * ARRAY_LENGTH($consequences)) );
LET $likelihood := ARRAY_INDEX( $likelihoods, CEILING(RAND() * ARRAY_LENGTH($likelihoods)) );
RETURN HASH( "Risk", $risk, "Consequence", $consequence, "Likelihood", $likelihood ) );
RETURN $generatedData

LET $contractRecordIDs := [Contract RecordIDs];

LET $numberToGenerate := 5;

LET $generatedData := 
ARRAY_SELECT(	
	RANGE(0, $numberToGenerate), $(i, item)		

	LET $varyDate := FLOOR(RAND() * 2) = 1;	
	LET $varyPrice := 
	OR(
		FLOOR(RAND() * 2) = 1,
		$varyDate = FALSE
	);

	LET $newStart := 
	DATE(YEAR(NOW()) + FLOOR(RAND() * 3) - 1, MONTH(NOW()) + FLOOR(RAND() * 12));

	LET $newEnd := 
	DATE(YEAR($newStart) + FLOOR(RAND() * 5), MONTH($newStart) + FLOOR(RAND() * 12));

	LET $variationValue := FLOOR((FLOOR(RAND() * 100000) + 5000) / 100) * 50;

	RETURN
	HASH(
		"New Start Date", 		$newStart,
		"New End Date", 		$newEnd,
		"Variation Value", 		$variationValue,
		"Variation to Date", 	$varyDate,
		"Variation to Price", 	$varyPrice
	)
);

RETURN $generatedData

LET $contractRecordIDs := [Contract RecordIDs];

LET $numberToGenerate := 5;

LET $generatedData := 
ARRAY_SELECT(	
	RANGE(0, $numberToGenerate), $(i, item)		

	LET $years := FLOOR(RAND() * 5);	
	LET $months := FLOOR(RAND() * 12);


	LET $expiryDate := 
	DATE(YEAR(NOW()) + FLOOR(RAND() * 3), MONTH(NOW()) + FLOOR(RAND() * 12));

	RETURN
	HASH(
		"Extension Years", $years,
		"Extension Months", $months,
		"Expiry Date", $expiryDate
	)
);

RETURN $generatedData

