FieldEntry.Formula:
LET $fallback := ARRAY_INDEX( [Random Actions], CEILING(RAND() * ARRAY_LENGTH([Random Actions])) ) & " of a " &
ARRAY_INDEX( [Random Adjectives], CEILING(RAND() * ARRAY_LENGTH([Random Adjectives])))
& " " & ARRAY_INDEX( [Random Nouns], CEILING(RAND() * ARRAY_LENGTH([Random Nouns])) );
LET $getTitle:= $(key)(
LET $hash := HASH( "Contract", ARRAY( "IT Professional Services", "Tarmac Supply Services", "IT Hardware and Support", "Grass Seed and Fertilizer", "Grounds Maintenance", "Lawn Mowing and Landscaping", "Office Supplies", "Footpath and Road Civil Works", "Lighting Upgrade", "Self Contained Classroom Supply", "Waterproofing", "SAAS Software Agreement" ),
"Procurement", ARRAY( "Procurement of Concrete Footings", "Sourcing of a new Enterprise System", "Purchasing of Standard Equipment", "Acquisition of PPE", "Sourcing of Masks, RAT's and Face Shields", "Provision of IT Cyber Security Services", "Enhancement to existing infrastructure", "Establishment of a panel for Garden Maintenance Services", "Supply of Tarmac and Gravel Surfaces", "Sourcing of Engineering Services for Building Refurbishment", "Procurement for Hardware Refresh", "Installation of Traffic Signals", "Sourcing Consulting Services for Infrastructure" ),
"Project", ARRAY( "Construction of Sporting Club", "Resurfacing of North Oval", "Construction of New Office Buildings", "Resurfacing of Carpark in the shopping district", "Construction of new Toilet Blocks on the foreshore", "Community Building Upgrades", "Outdoor Gym Equipment", "Redesign of the Town Square", "Redevelopment of old school precinct" ) ); LET $set:= HASH_GET($hash, $key); RETURN ARRAY_INDEX( $set, CEILING(RAND() * ARRAY_LENGTH($set)) )
);
RETURN COALESCE( CALL($getTitle, HASH_GET($eventargs, "EformType")), $fallback )

SetReturnValue.ResultValueFormula:
LET $set := DB_LOOKUPALL("Username", "sys.Users", "[DisplayName] IS NOT EMPTY AND [IsSystem] <> TRUE AND [Active] = TRUE");
LET $len := ARRAY_LENGTH($set);
LET $randomNumber := CEILING(RAND() * $len);
RETURN ARRAY_INDEX($set, $randomNumber)

FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $direction := HASH_GET($eventargs, "direction");
LET $line := HASH_GET($eventargs, "line");
LET $swap := HASH_GET($eventargs, "swap");
LET $before := HASH_GET($eventargs, "before");
LET $after := HASH_GET($eventargs, "after");
LET $lowerChange := LEAST($before, $after); 
LET $higherChange := GREATEST($before, $after); 

LET $columns:=
ARRAY(
  "Module Name", 
  "Module Settings EForm", 
  "Module Type", 
  "Module Dashboard Name", 
  "Navigation Mode", 
  "Module Icon", 
  "Module Access", 
  "Module Home Page", 
  "Show on Main Entry"
);

LET $buildArray:= $(columnName)(
	LET $array	:= CURRENT_EACHVALUE($columnName);
	RETURN
		IF( 
		  $swap = TRUE, 
		    ARRAY_CONCAT(
			    ARRAY_TAKE($array, $lowerChange - 1), //before,
				CURRENT_VALUE($columnName, $higherChange),
			    ARRAY_TAKE(ARRAY_SKIP($array, $lowerChange), $higherChange - $lowerChange - 1), 
				CURRENT_VALUE($columnName, $lowerChange),
				ARRAY_SKIP($array, $higherChange) //after
			),

		  $direction = "up",
			ARRAY_CONCAT(
				ARRAY_TAKE($array, $line-2), //$before,
				CURRENT_VALUE($columnName, $line), 
			  	CURRENT_VALUE($columnName, $line-1),
				ARRAY_SKIP($array, $line) //$after
			),
			$direction = "down",
			ARRAY_CONCAT(
				ARRAY_TAKE($array, $line-1), //$before,
				CURRENT_VALUE($columnName, $line+1), 
			  	CURRENT_VALUE($columnName, $line),
				ARRAY_SKIP($array, $line+1) //$after
			),
			$array
		  )
);
RETURN 
HASH_MERGE(
  ARRAY_TO_HASH(
    $columns, 
    $(i, columnName) $columnName, 
    $(i, columnName) CALL($buildArray, $columnName)
  ), 
  HASH(
	"Swap IDs", 
	ARRAY(HASH(
	  //"before", COALESCE($line, $before),
	  "after", 
	    IF( 
		  $direction = "up", $line - 1, 
		  $direction = "down", $line + 1, 
		  $after
	    )
	  )
	)
  )
)

FieldEntry.Formula:
LET $data:=
	ARRAY(
	  EACH [Module Name],
	  EACH [Show on Main Entry] //2
	  );

LET $enabled:=
	ARRAY_WHERE(
	   ARRAY_TRANSPOSE($data), 
	  $(i,item) ARRAY_INDEX($item, 2) = TRUE
	  );

RETURN
ARRAY_FIRST(ARRAY_TRANSPOSE($enabled))

LET $fileNames := 
ARRAY_SELECT(
  EBMS_GET_ATTACHMENTS(
    EFormType, 
    RecordID, 
    "Home Images"
  ), $(i, item) 
  HASH_GET($item, "FileName")
);

LET $sortOrder := SPLIT([Image Order], " | "); 

RETURN IF(ARRAY_LENGTH($sortOrder) > 1, $sortOrder, $fileNames)

LET $fileNames := 
ARRAY_SELECT(
  EBMS_GET_ATTACHMENTS(
    EFormType, 
    RecordID, 
    "Banner Image"
  ), $(i, item) 
  HASH_GET($item, "FileName")
);

RETURN ARRAY_FIRST($fileNames)

LET $fileNames := 
ARRAY_SELECT(
  EBMS_GET_ATTACHMENTS(
    EFormType, 
    RecordID, 
    "Banner Logo"
  ), $(i, item) 
  HASH_GET($item, "FileName")
);

RETURN ARRAY_FIRST($fileNames)

ChangeView.ViewNameFormula:
LET $currentUser := HASH_GET([Session Data], "Current User");
LET $isSystemBuilder := HASH_GET(HASH_GET([Session Data], "User to Group Mapping"), "System Builders");
LET $navStripItem := COALESCE([Manage Mode], "Announcements");
LET $view := IF($requestedView = "Client", "Client", $mode);
RETURN
IF(
  $isSystemBuilder, 
    $navStripItem,
  "Client"
)
