Column.DefaultValueFormula:
LET $currentUser := EBMS_GET_CURRENT_USER(); 

LET $currentRoles := EBMS_GET_ANCESTOR_ROLES($currentUser);

LET $userMapping := 
INDEXES_TO_KEYS(
  ARRAY_SELECT($currentRoles, $(i, item) TRUE),
  $currentRoles
);
RETURN $userMapping

LET $currentUser := EBMS_GET_CURRENT_USER(); 

LET $currentRoles := EBMS_GET_ANCESTOR_ROLES($currentUser);

LET $userMapping := 
INDEXES_TO_KEYS(
  ARRAY_SELECT($currentRoles, $(i, item) TRUE),
  $currentRoles
);

RETURN IF(HASH_GET($userMapping, "Vendor Accounts") <> TRUE, FALSE, TRUE)

Binding.Expression:
LET $set:= HASH_GET([Action Options], [Action Prompt]);

RETURN 
ARRAY_SELECT( 
  	$set, 
  	$(i,item) HASH_GET($item, "Prompt 2") 
)

TriggerCustomEvent.EventArgumentsFormula:
LET $set:= HASH_GET([Action Options], [Action Prompt]);
LET $hash:= 
ARRAY_FIRST(ARRAY_WHERE( 
  	$set, 
  	$(i,item) HASH_GET($item, "Prompt 2") = [Action] 
));
RETURN 
HASH(
  "URL", HASH_GET( $hash, "URL"),
  "New Tab", IF(HASH_GET( $hash, "Navigation") = "New window", TRUE, FALSE)
  )

Binding.Expression:
"<div data-clickid='Action' class='core-home-imageheading'>" 
& 
  (
	LET $text := HASH_GET([Settings], "Image Heading");
    LET $split := SPLIT($text, " | "); 
    RETURN COALESCE(ARRAY_INDEX($split, [Image Selector]), ARRAY_FIRST($split))
  )
& "</div>"

LET $urls := HASH_GET([Settings], "Custom Image URLs");
LET $navigation := SPLIT(HASH_GET([Settings], "Image Navigation Links"), " | ");
LET $imageCount := ARRAY_LENGTH($urls);
LET $selectedImageNumber := [Image Selector]; 
LET $height := 40 * 10; 
LET $selectedImageURL := ARRAY_INDEX($urls, $selectedImageNumber);
LET $selectedImageNavLink := COALESCE(ARRAY_INDEX($navigation, $selectedImageNumber), ARRAY_FIRST($navigation));

LET $iconright :=  
"<svg  data-clickid='plus' class='core-home-imagebox-rightchevron' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 384 512'><path fill='#00557A' d='M342.6 233.4c12.5 12.5 12.5 32.8 0 45.3l-192 192c-12.5 12.5-32.8 12.5-45.3 0s-12.5-32.8 0-45.3L274.7 256 105.4 86.6c-12.5-12.5-12.5-32.8 0-45.3s32.8-12.5 45.3 0l192 192z'/></svg>";

LET $iconleft := 
"<svg  data-clickid='minus' class='core-home-imagebox-leftchevron' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 384 512'><path fill='#00557A' d='M41.4 233.4c-12.5 12.5-12.5 32.8 0 45.3l192 192c12.5 12.5 32.8 12.5 45.3 0s12.5-32.8 0-45.3L109.3 256 278.6 86.6c12.5-12.5 12.5-32.8 0-45.3s-32.8-12.5-45.3 0l-192 192z'/></svg>";

RETURN 
IF( 
  OR(
	HASH_GET([Settings], "Custom Image Visible") <> TRUE,
    ARRAY_LENGTH($urls) <= 0
  ), 
  NULL, 

  "<div style=""float: left; width: 3% ; height: " & $height & "px;  "">"& IF( $selectedImageNumber > 1, $iconleft) &"</div>"&

  IF($selectedImageNavLink <> NULL, "<a href='" & $selectedImageNavLink & "'>") & 
    "<div style=""border: 0; float: left; width: 76%; height: " & $height & "px; background-size: contain; background-image: url(" & "'files/eformrecords/Global%20Settings/1/Home%20Images/" & HTMLENCODE($selectedImageURL) & "'); background-repeat: no-repeat; background-position: center; ""></div>" & 
  IF($selectedImageNavLink <> NULL, "</a>") &

  "<div style=""border: 0; float: left; width: 94%; height: " & $height & "px; background: white; background-size: contain; background-image: url(" & "'files/eformrecords/Global%20Settings/1/Home%20Images/" & HTMLENCODE($selectedImageURL) & "'); background-repeat: no-repeat; background-position: center; ""></div>"&

  "<div style=""float: left; width: 3% ;  height: " & $height & "px;  "">"& IF( $selectedImageNumber < $imageCount, $iconright) &"</div>" 
)

LET $show := HASH_GET([Settings], "Show Announcement");
RETURN
AND(
  ARRAY_INDEXOF(HASH_GET([User to Group Mapping], "Current User Roles"), "Everyone") > 0,
  ARRAY_LENGTH([Announcement Data]) > 0,
  OR(
	$show = "Always", 
	$show = "Count Dependent"
  )
)

TriggerCustomEvent.EventArgumentsFormula:
LET $numberInRow := 2; 
LET $moduleData:= ARRAY_INDEX([Module Data], $numberInRow);
