RETURN 
HASH(
  "URL", HASH_GET( $moduleData, "URL"),
  "New Tab", IF($accessType = "External Link", TRUE, FALSE)
  )

Binding.Expression:
LET $critical:= HASH_GET([Action Data], "notificationPriority") = "Must";
LET $module:= HASH_GET([Action Data], "module");
LET $title:= HASH_GET([Action Data], "title");
LET $count:= HASH_GET([Action Data], "count");
LET $countitems:= $count&" "& IF($count >1, "items", "item");
LET $url:= HASH_GET([Action Data], "location");
RETURN  

"<a style= ""text-decoration: none; "" href="""&$url &""" target=""_self"" title = """& $module& " - "&$title &""" >"&
"<div style= ""line-height:25px; display: inline-block; padding:2px; margin-left: 6px"">"&

"<span style= ""margin-left:6px; font-size:12px; color:#3D77AF "">"&
"<strong>"& $module&"</strong>"&
" - "& $title &
"<span style= ""color:#5D5D5D; "">"&" | " &"</span>"&
"<strong>"& $countitems&
"</strong> </span>"&
IF(
  $critical = TRUE,
  "<span style= ""margin-left:6px; font-size:11px; color: white; background-color: #FF5D5D; border-radius: 4px; padding:4px"">Critical task</span>"
  )&

  " </div> </a>"

