//LET $title := HASH_GET([Action Data], "title");
LET $icon := HASH_GET([Action Data], "icon");
//LET $url:= HASH_GET([Action Data], "location");
RETURN
//"<a style= ""text-decoration: none; "" href="""&$url &""" target=""_self"" title = """& $module& " - "&$title &""" >"&

"<div style= "" border-radius: 50%; background-color: #eaeaea; padding: 3px; width: 100%; height: 100%"">" & $icon & "</div>"// </a>"

FormulaDrivenRaiseLinkedEForm.EFormTypeFormula:
LET $data := 
ARRAY_SELECT(
  SPLIT($clickid, "|"), $(i, item) 
  LET $split := SPLIT($item, "=");
  RETURN ARRAY(ARRAY_INDEX($split, 1), ARRAY_INDEX($split, 2))
);

LET $keys := ARRAY_SELECTMANY($data, $(i, item) ARRAY_INDEX($item, 1));
LET $values := ARRAY_SELECTMANY($data, $(i, item) ARRAY_INDEX($item, 2));

LET $hashed :=  INDEXES_TO_KEYS($values, $keys);

RETURN 
HASH_GET($hashed, "targetForm")

FormulaDrivenRaiseLinkedEForm.UIOpenModeFormula:
LET $data := 
ARRAY_SELECT(
  SPLIT($clickid, "|"), $(i, item) 
