LET $hashed :=  INDEXES_TO_KEYS($values, $keys);

RETURN 
HASH_GET($hashed, "navigationMode") = "Popup"

FormulaDrivenRaiseLinkedEForm.EFormTypeFormula:
LET $data := 
ARRAY_SELECT(
  SPLIT($clickid, "|"), $(i, item) 
  LET $split := SPLIT($item, "=");
  RETURN ARRAY(ARRAY_INDEX($split, 1), ARRAY_INDEX($split, 2))
);

LET $keys := ARRAY_SELECTMANY($data, $(i, item) ARRAY_INDEX($item, 1));
LET $values := ARRAY_SELECTMANY($data, $(i, item) ARRAY_INDEX($item, 2));

LET $hashed :=  INDEXES_TO_KEYS($values, $keys);

RETURN 
HASH_GET($hashed, "targetForm")

