IF( 
  OR(
	HASH_GET([Settings], "Video Tile Visible") <> TRUE,
    $embedlink = NULL
  ), 
  NULL, 

  "<div allow=""autoplay; fullscreen; picture-in-picture"" allowfullscreen style=""padding-top: 15px; border: 0; width: 100%; height: 100%; background: white;""><iframe style=""display: block; border: 0; margin: 0 auto;"" width=""" & 800 & """ height=""" & 450 & """ src=""" & HTMLENCODE($embedlink) & """ allowfullscreen></iframe></div>"
)

