LET $name:= HASH_GET($link, "Name");
LET $url:= HASH_GET( $link, "URL");
LET $target:= IF(HASH_GET( $link, "Navigation") = "New window", "_blank", "_self");
RETURN
"<a style= ""text-decoration: none; "" href="""&$url &""" target="""& $target&""" title = """& $name &""" >"&
"<span style= ""color:#3D77AF ; font-size:12px"">"&$name& "</span> </a>"

