FieldEntry.Formula:
LET $currentUser := EBMS_GET_CURRENT_USER(); 
LET $currentRoles := EBMS_GET_ANCESTOR_ROLES($currentUser);
RETURN 
HASH(
  "Current User", 
    $currentUser, 
  "Current User Roles",
    $currentRoles
)

LET $headerColumns:= 
ARRAY(
  "Full Width", 
  "Icon Size", 
  "Show Announcement", 
  "Show Action",
  "Logo Size", 
  "Show Action Prompt",
  "Video Tile Visible", 
  "Video Embed URL",
  "Welcome Text", 
  "Video Heading",
  "Show Action Summary", 
  "Custom Image Visible", 
  "Image Navigation Links",
  "Custom Image URLs",
  "Image Heading",
  "Available Categories Tile Visible",
  "Category Custom Text", 
  "Banner Mode",
  "Banner Colour",
  "Banner Image URL", 
  "Banner Text Colour",
  "Banner Logo URL",
  "Banner Padding", 
  "Logo Bottom Margin"
);

LET $multiColumns := 
ARRAY(
  "Module Name", 
  "Module Settings EForm",
  "Module Home Page", 
  "Module Type",
  "Module Access",
  "Module Icon",
  "Navigation Mode",
  "Show on Main Entry",
  "Custom Link Text", 
  "Custom Link URL", 
  "Custom Link Navigation",
  "Custom Link Visibility"
);

LET $lookup :=
DB_LOOKUPALL(
  ARRAY_CONCAT($headerColumns, $multiColumns),
  "Global Settings", 
  "[RecordID] FAST_EQUAL_TO 1"
);

LET $settings:=  
INDEXES_TO_KEYS(
  ARRAY_TAKE(ARRAY_FIRST($lookup), ARRAY_LENGTH($headerColumns)),
  $headerColumns
);

LET $data:= 
INDEXES_TO_KEYS(
  ARRAY_SKIP(ARRAY_TRANSPOSE( $lookup), ARRAY_LENGTH($headerColumns)),
  $multiColumns
);

LET $columns :=
ARRAY(
  "Module Name", 
  "Module Settings EForm",
  "Module Home Page", 
  "Module Type",
  "Module View",
  "Module Access",
  "Module Icon",
  "Navigation Mode",
  "Show on Main Entry"
);

LET $moduleData := 
ARRAY_TRANSPOSE(
  ARRAY_SELECT(
	$columns, $(c, col) 
	HASH_GET($data, $col)
  )
);

LET $modules:= 
ARRAY_WHERE(
  $moduleData, $(m,module)
  LET $moduleName 			:= 	ARRAY_INDEX($module, ARRAY_INDEXOF($columns, "Module Name")); 
  LET $show 				:= 	ARRAY_INDEX($module, ARRAY_INDEXOF($columns, "Show on Main Entry")); 
  RETURN 
  AND( $moduleName <> NULL, $show=TRUE)
);

LET $moduleData:=
ARRAY_SELECT(
	$modules, $(m, module)

	LET $moduleName 		:= 	ARRAY_INDEX($module, ARRAY_INDEXOF($columns, "Module Name")); 
	LET $moduleSettings 	:=	ARRAY_INDEX($module, ARRAY_INDEXOF($columns, "Module Settings EForm")); 
	LET $EFormName 			:=	ARRAY_INDEX($module, ARRAY_INDEXOF($columns, "Module Home Page")); 
	LET $accessType 		:=	ARRAY_INDEX($module, ARRAY_INDEXOF($columns, "Module Type")); 
	LET $view   			:= 	ARRAY_INDEX($module, ARRAY_INDEXOF($columns, "Module View"));
	LET $access   			:= 	ARRAY_INDEX($module, ARRAY_INDEXOF($columns, "Module Access"));
	LET $icon				:=  ARRAY_INDEX($module, ARRAY_INDEXOF($columns, "Module Icon"));
	LET $navigationMode		:=  ARRAY_INDEX($module, ARRAY_INDEXOF($columns, "Navigation Mode"));
	LET $show				:=  ARRAY_INDEX($module, ARRAY_INDEXOF($columns, "Show on Main Entry"));

	LET $moduleHomePage 	:=	
	IF( 
		$accessType = "Module", 
		"records/" & URLENCODE($EFormName) & "/new?view=" & URLENCODE($view),
		$EFormName
	); 

  	LET $hasAccess :=
	ARRAY_LENGTH(
		ARRAY_WHERE(
			$access, $(i, item) 
			ARRAY_LENGTH(
				ARRAY_WHERE(
					$item, $(i, item) 
                    ARRAY_INDEXOF(
	                  HASH_GET([User to Group Mapping], "Current User Roles"), 
	                  $item
                    ) > 0			
			    )
			) > 0
		)
	) > 0;

	RETURN
	IF( 
		$hasAccess == TRUE, 
		HASH(
			"moduleName", $moduleName, 	
			"moduleSettings", $moduleSettings,
			"EFormName", $EFormName, 		
			"accessType", $accessType, 	
			"primaryColour", "#e2e2e2", 
			"view", $view,   		
			"access", $access,   		
			"icon", $icon,			
			"navigationMode", $navigationMode,
		    "moduleHomePage", $moduleHomePage,
			"show",	$show
		)
	)
);

LET $moduleTileData:= ARRAY_WHERE($moduleData, $(i, item) AND( $item <> NULL,  HASH_GET($item, "show") = TRUE) );

LET $moduleListforAlert:= 
ARRAY_SELECTMANY(
  $moduleData, $(i, item)
  IF( 
	$item <> NULL(),
	HASH_GET($item, "moduleName")
  )
);

LET $moduleHash:=
HASH(
  	"Module Data",				ARRAY_WHERE( $moduleData, $(i, item) $item <> NULL ),
	"Module Tile Data",			$moduleTileData,
  	"Module List for Alert",	$moduleListforAlert
);
// end of new stuff
RETURN HASH_MERGE($settings, $data, $moduleHash)

IF(
  //force full width mode for vendors
  ARRAY_INDEXOF(
	HASH_GET([User to Group Mapping], "Current User Roles"), 
	"Vendor Accounts"
  ) > 0,
	TRUE,

  COALESCE(
    EBMS_GET_PREFERENCE("Full Width"), 
    HASH_GET([Settings], "Full Width")
  )
)

LET $userIsVendor := 
ARRAY_INDEXOF(
	HASH_GET([User to Group Mapping], "Current User Roles"), 
	"Vendor Accounts"
  ) > 0; 

RETURN 
IF( 
  $userIsVendor == TRUE, 
  (
	LET $vendorUsername := HASH_GET([User to Group Mapping], "Current User");

    LET $vendorID := 
    DB_LOOKUP(
      "RecordID", 
      "Vendor", 
      "[Username] FAST_EQUAL_TO " & DB_ENCODECONSTANT($vendorUsername)
    );

    LET $companyID:= 
    IF(
     	ISBLANK($vendorID), 
      	DB_LOOKUP(
     	 	"RecordID", 
     	 	"Vendor", 
     		"STEP TO CHILD [Contact] " &
     	 	"ON [To].[Username] FAST_EQUAL_TO " & DB_ENCODECONSTANT($vendorUsername) & 
     	 	" GET COUNT(0) >= 1"
      	),
    	$vendorID
    );

    RETURN $companyID
  )
)

LET $data := HASH_GET([Settings], "Module Tile Data");

LET $iconSize := [Icon Size];

LET $fullWidth := [Full Width];

LET $perLine := 
IF( 
  $isMobile = TRUE, 1,
  $fullWidth  = TRUE, 1,
  $iconSize = 1, 4, 
  $iconSize = 2, 3, 
  $iconSize = 3, 2, 
  2
); 

LET $singleColour := IF( HASH_GET([Settings], "Use Single Colour") = TRUE, HASH_GET([Settings], "Single Colour"));

LET $rowColours := 
IF( HASH_GET([Settings], "Use Row Colours") = TRUE,
	ARRAY_WHERE(HASH_GET([Settings], "Row Colour"), $(i,item) $item <> NULL )
  );


LET $result :=  
ARRAY_SELECT( 	
	$data, $(i, item) 

  	LET $moduleName 		:= 	HASH_GET($item, "moduleName");
	LET $moduleSettings 	:=	HASH_GET($item, "moduleSettings");
	LET $EFormName 			:=	HASH_GET($item, "EFormName");
	LET $accessType 		:=	HASH_GET($item, "accessType");
	LET $primaryColour   	:= 	HASH_GET($item, "primaryColour");
	LET $view   			:= 	HASH_GET($item, "view");
	LET $access   			:= 	HASH_GET($item, "access");
  	LET $messageEnabled		:=	HASH_GET($item, "messageEnabled");
	LET $icon				:=  HASH_GET($item, "icon");
	LET $navigationMode		:=  HASH_GET($item, "navigationMode");
	LET $moduleHomePage		:=  HASH_GET($item, "moduleHomePage");


	LET $primaryColour :=
    IF(
  	  AND($rowColours <> NULL(), $rowColours <> ARRAY()),

	  ( 
		LET $lenColours := ARRAY_LENGTH($rowColours);
		LET $rowNumber := CEILING($i / $perLine);
		LET $colourIndex := MOD($rowNumber - 1, $lenColours) + 1;
		LET $rowColour := ARRAY_INDEX($rowColours, $colourIndex);
	    RETURN $rowColour
   	  ),

      COALESCE($singleColour, $primaryColour)
	); 

    LET $functionEnabled :=  
    IF( 
      AND(
		$moduleName = "Contracts",
		[Vendor RecordID] <> NULL
	  ), 
	  ARRAY_INDEXOF(
		DB_LOOKUP(
		  "Profile Supported Functions", 
		  "Settings - Contract"
		), 
		"Vendor Access"
	  ) > 0, 

	  TRUE
	);


	RETURN
    IF( 
	  $functionEnabled = TRUE, 
	  HASH(
		"Name", 			$moduleName,
		"URL", 				$moduleHomePage, 
		"Primary Color", 	$primaryColour,
	  	"Icon", 			$icon,
	  	"Access Type", 		$accessType,
	  	"Alert Enabled", 	TRUE,
	    "Navigation Mode",  $navigationMode,
	    "Target Form",  	$EFormName
	  )	
	)
);

LET $result := ARRAY_WHERE($result, $(i, item) $item <> NULL); 

LET $result :=  
ARRAY_SELECT(
  RANGE(0, CEILING(ARRAY_LENGTH($data) / $perLine)), $(i, item) 
  LET $skip := ARRAY_SKIP($result, $perLine * ($i - 1)); 
  RETURN ARRAY_TAKE($skip, $perLine)
);

RETURN 
ARRAY_WHERE($result, $(i, item) $item <> ARRAY())

Plugins.VendorPanel.ProcurementHomeRecor.RunOnlyWhenFormula:
LET $showAlerts := 
HASH_GET([Settings], "Show Action"); 

LET $moduleListforAlert:= 
HASH_GET([Settings], "Module List for Alert");

LET $isGTMUser := 
ARRAY_INDEXOF(
  HASH_GET([User to Group Mapping], "Current User Roles"), 
  "GTM SSO Users"
) > 0;

RETURN 
AND(
  $showAlerts <> "Never",
  ARRAY_INDEXOF($moduleListforAlert, "Go To Market") > 0,
  $isGTMUser == TRUE 
)

FieldEntry.Formula:
//internal module alerts
LET $showAlerts := HASH_GET([Settings], "Show Action"); 

LET $moduleDataforAlert :=
ARRAY_WHERE(
  ARRAY_SELECTMANY(
    EACH [Module Data], $(i,item) 
    $item
  ), 
  $(i, item) 
  HASH_GET($item, "Alert Enabled") = TRUE
);

LET $moduleListforAlert :=
ARRAY_SELECT(
  $moduleDataforAlert, $(i, item) 
  HASH_GET($item, "Name")
);

LET $getSvg:=  $(iconName, backColour)
(
  LET $svg := HASH_GET([Svg Map], $iconName);
  LET $coloredSvg:= REPLACESUBSTRING("currentColor", $svg, "#5D5D5D" );
  RETURN  $coloredSvg
);

LET $iconHash:= 
ARRAY_TO_HASH(
  HASH_GET( [Settings], "Module Data"), 
  $(i,item) HASH_GET($item, "moduleName"),
  $(i,item) CALL($getSvg, HASH_GET($item, "icon"))
);

LET $gtmAlerts:= 
ARRAY_SELECT(
  ARRAY_WHERE( 
	HASH_GET([Alert Data], "values"), $(i,item) HASH_GET($item, "count") > 0
	), $(i,item) HASH_MERGE(HASH("module", "Go To Market"), $item)
);

LET $columnMap := 
HASH(
  "Procurement", 
  ARRAY("Status", "RecordID"), 

  "Contract", 
  ARRAY("Status", "RecordID"), 

  "Submission", 
  ARRAY("Status", "RecordID", "Type"), 

  "Meeting", 
  ARRAY("Status", "RecordID"), 

  "Action", 
  ARRAY("Status", "RecordID"), 

  "Questionnaire", 
  ARRAY(
	"Status", 
	"RecordID", 
	"Panel Member Username", 
	"Invitation Sent",
	"Linked Entity RecordID", 
	"Assessor", 
	"Participant"
  ), 

  "Evaluation", 
  ARRAY(
	"Status", 
	"RecordID", 
	"Username in Role", 
	"Individual Scoring Complete", 
	"Panel Member Username", 
	"Panel Member Name", 
	"Invitation Sent", 
	"Consensus Scoring Complete", 
	"Final Decision Complete"
  )
);

LET $lookupAll := $(eform, filterString)(
	LET $columns := HASH_GET($columnMap, $eform);
	LET $data := 
     IF(
	   $showAlerts <> "Never",
	   DB_LOOKUPALL( 
         $columns, 
		 $eform, 
         $filterString
	   ), 
	   ARRAY()
     );
	RETURN //function to get all columns an all single line Data for an eform
	ARRAY_SELECT( $data, $(i, item) INDEXES_TO_KEYS($item, $columns))
);


LET $isNormalUser := 
ARRAY_INDEXOF(
  HASH_GET([User to Group Mapping], "Current User Roles"), 
  "Everyone"
) > 0;

LET $lookupHash := 
IF(
  $isNormalUser == TRUE,

  HASH(
    "Procurement",  	
    	IF(
  	  ARRAY_INDEXOF($moduleListforAlert, "Procurement Planning")>0,

  	  CALL($lookupAll, "Procurement", "[LineNumber] FAST_EQUAL_TO 1 AND ( (CURRENT_USER IN ROLE [Originator] AND [Procurement Officer] IS NULL) OR CURRENT_USER IN ROLE [Procurement Officer])")
  	  ),
    "Contract",  	 	
    	IF(
  	  ARRAY_INDEXOF($moduleListforAlert, "Contracts")>0,
  	  CALL($lookupAll, "Contract", "[LineNumber] FAST_EQUAL_TO 1 AND ( (CURRENT_USER IN ROLE [Originator] AND [Contract Manager] IS NULL) OR CURRENT_USER IN ROLE [Contract Manager])")
    	),
    "Submission",  	 	
    	IF(
  	  OR(
  			ARRAY_INDEXOF($moduleListforAlert, "Advanced Evaluations")>0,
  			ARRAY_INDEXOF($moduleListforAlert, "Procurement Planning")>0,
  			ARRAY_INDEXOF($moduleListforAlert, "Contracts")>0,
  			ARRAY_INDEXOF($moduleListforAlert, "Vendors")>0,
  			ARRAY_INDEXOF($moduleListforAlert, "Approvals")>0
  		),
  	  CALL($lookupAll, "Submission", "CURRENT_USER IN ROLE [Approver] AND [Approver Decision] FAST_EQUAL_TO 'In Progress' AND [Status] <> 'Voided'")
  	), 
    "Meeting",  		 	
    	IF(
  	  ARRAY_INDEXOF($moduleListforAlert, "Meetings and Actions")>0,
  	  CALL($lookupAll, "Meeting", "CURRENT_USER FAST_EQUAL_TO [Invitee] AND [Flattened Meeting Date] FAST_EQUAL_TO TO_DAY(CURRENT_DATE) AND [Status] <> 'Complete' AND [Status] <> 'Cancelled'")
  	 ),

    "Action",  		 	
    	IF(
  	  ARRAY_INDEXOF($moduleListforAlert, "Meetings and Actions")>0,
  	  CALL($lookupAll, "Action", "CURRENT_USER FAST_EQUAL_TO [Responsible - Multiline] AND [Status] <> 'Complete' AND [Status] <> 'Cancelled'")
  	 ),

    "Questionnaire",	 
    	IF(
  		 	ARRAY_INDEXOF($moduleListforAlert, "Questionnaires")>0,
  	  CALL($lookupAll, "Questionnaire", "[LineNumber] FAST_EQUAL_TO 1 AND [Status] <> 'Deleted' AND [Linked Entity EFormType] <> 'Evaluation'")
  	  ),

     "Evaluation",	 	
    	IF(
  	  ARRAY_INDEXOF($moduleListforAlert, "Advanced Evaluations")>0,
  	  (
  	  	LET $allEvals:=	CALL($lookupAll, "Evaluation", "[Status] <> 'Finished' AND [Status] <> 'Not Started' AND [Status] <> 'Cancelled'");
  		LET $allQs:= CALL($lookupAll, "Questionnaire", "[LineNumber] FAST_EQUAL_TO 1 AND [Status] <> 'Deleted' AND [Linked Entity EFormType] FAST_EQUAL_TO 'Evaluation'");
  		RETURN 
  		ARRAY_SELECT(
  			$allEvals, $(i,item)
    			LET $recordID:= HASH_GET($item, "RecordID");
  			LET $evaluator:= HASH_GET($item, "Panel Member Username");
  			LET $invitationSent:= HASH_GET($item, "Invitation Sent");
    			LET $qdata:= IF( $invitationSent = TRUE,
  							ARRAY_SELECT(
  								  ARRAY_WHERE(
  									$allQs, $(j,jitem)
  										AND(
  											HASH_GET($jitem, "Linked Entity RecordID") == $recordID,
  											HASH_GET($jitem, "Assessor") = $evaluator
  										)
  									),
  							  	$(k,kitem) HASH_GET($kitem, "Status")
  							 )
  					  );
  			  LET $scoringStatus:= IF( 
  									ARRAY_DISTINCT($qdata) = ARRAY("Complete"), "Complete",
  									ARRAY_DISTINCT($qdata) = ARRAY("Not Started"), "Not Started",
  									ARRAY_DISTINCT($qdata) = ARRAY(), "Not Started", "In Progress"
  									);
  				RETURN 
  				HASH_MERGE( 
  					$item,
  					HASH("Scoring Status", $scoringStatus)
  				)
  			)

  		)
  	)  
  )
);

//array of approval alerts grouped by submission type.
LET $submissionAlerts:= 
(
  LET $targetForm := "Submission";
  LET $rawData := HASH_GET($lookupHash, $targetForm);
  LET $submissionByType := ARRAY_GROUP( $rawData, $(item) HASH_GET($item, "Type"));
  RETURN 
  ARRAY_SELECT(
  	$submissionByType, $(i,item)
  		(
  		  LET $type := ARRAY_FIRST($item);
  		  LET $filtered:= ARRAY_INDEX($item,2);
  		  LET $records := ARRAY_SELECT($filtered, $(i,item) HASH_GET($item, "RecordID"));
  		  LET $directURL:= NULL;
  		  LET $profile := "Approvals";
  		  LET $filter := "[RecordID] IN "& DB_ENCODECONSTANT($records);
  		  LET $url := "tabularreport/"& $targetForm & "?profile=" & $profile & "&where=" & URLENCODE($filter);
  		  LET $count := ARRAY_LENGTH($records);
  		  RETURN
  		  	IF($count>0,
  				HASH(
  					"title", "Review approval for " & LOWER(ARRAY_FIRST($item)),
  					"module",	"Approvals",
  					"count", $count,
  					"location", COALESCE($directURL, $url)
  				  	)
  			  )

  	 	)

  )
);

