//modules with alert
LET $mods:= 
ARRAY_SELECT(
  EACH [Action Data], $(i,item) 
  HASH_GET($item, "module")
);

RETURN
ARRAY_SELECT( 
  EACH [Module Data], $(l,line) 
    ARRAY_SELECT(
      $line, $(t,tile) 
      ARRAY_INDEXOF(
        $mods,
        HASH_GET($tile, "Name")
      ) > 0
    )
