LET $titlePadding:=    IF( [Icon Size] = 1, "10px 10px 10px 70px;", [Icon Size] = 2, "15px 15px 15px 90px;", [Icon Size] = 3, "20px 20px 20px 130px;" );
LET $titleFontSize :=  IF( [Icon Size] = 1, "10pt;", [Icon Size] = 2, "14pt;", [Icon Size] = 3, "16pt;" );

LET $dotWidth :=      IF( [Icon Size] = 1, "10px;", [Icon Size] = 2, "12px;", [Icon Size] = 3, "14px;" );
LET $dotPadding :=    IF( [Icon Size] = 1, "8px;", [Icon Size] = 2, "10px;", [Icon Size] = 3, "12px;" );
LET $popoutWidth :=   IF( [Icon Size] = 1, "12px;", [Icon Size] = 2, "14px;", [Icon Size] = 3, "16px;" );

LET $otherWidth:=     IF( [Full Width] <> TRUE, "5%; ", "2%; ");

LET $svgMap:= [Svg Map];

RETURN
ARRAY_SELECT(
	EACH [Module Data], $(l,line) 
	 ARRAY_SELECT(
			$line, $(t,tile) 
			(	
				LET $data := 			$tile;
				LET $name :=			HASH_GET($data, "Name");
				LET $url := 			HASH_GET($data, "URL");
				LET $navigationMode := 	HASH_GET($data, "Navigation Mode");
				LET $targetForm :=		HASH_GET($data, "Target Form");
			  	LET $accessType := 		HASH_GET($data, "Access Type");
			  	LET $iconName := 		HASH_GET($data, "Icon");
			  	LET $moduleColour := 	HASH_GET($data, "Primary Color");  //ARRAY_INDEX($module, 3);
				LET $backColour :=		COALESCE($moduleColour, "white");
			  	LET $hasAlert := 		ARRAY_INDEX([Module Alert Data]#$l, $t);

				LET $title := 
			    "<div class='core-tile-textarea' style='padding: " & $titlePadding & "; font-size:"& $titleFontSize & "'>" & 
			      $name & 
			    "</div>";

				LET $dot:= 
			    "<svg style= 'width: " & $dotWidth & " padding: " & $dotPadding & " ' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 512 512'>" & 
			      "<path fill= '#FF5D5D' d='M512 256C512 397.4 397.4 512 256 512C114.6 512 0 397.4 0 256C0 114.6 114.6 0 256 0C397.4 0 512 114.6 512 256z'/>" & 
			    "</svg>";


				LET $popout:=
				"<svg style='width: " & $popoutWidth & " padding: " & $dotPadding & " ' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 512 512'><path fill='#3D77AF' d='M384 320c-17.67 0-32 14.33-32 32v96H64V160h96c17.67 0 32-14.32 32-32s-14.33-32-32-32L64 96c-35.35 0-64 28.65-64 64V448c0 35.34 28.65 64 64 64h288c35.35 0 64-28.66 64-64v-96C416 334.3 401.7 320 384 320zM488 0H352c-12.94 0-24.62 7.797-29.56 19.75c-4.969 11.97-2.219 25.72 6.938 34.88L370.8 96L169.4 297.4c-12.5 12.5-12.5 32.75 0 45.25C175.6 348.9 183.8 352 192 352s16.38-3.125 22.62-9.375L416 141.3l41.38 41.38c9.156 9.141 22.88 11.84 34.88 6.938C504.2 184.6 512 172.9 512 160V24C512 10.74 501.3 0 488 0z'/></svg>";

				LET $target:= IF($accessType = "External Link", "_blank", "_self");

				LET $other:= 
				"<div class='core-tile-notificationarea' style= 'vertical-align: " & IF($accessType = "External Link", "bottom", $hasAlert = TRUE, "top", "middle") & 
			      ";  width: " & $otherWidth & "'>" & 
			      IF( $accessType = "External Link", $popout, $hasAlert = TRUE, $dot) & 
			    "</div>";

				LET $hyperlink := 
				IF(
					$accessType = "Form", 
				  	"style= 'text-decoration: none; cursor: pointer;' data-clickid='targetForm=" & HTMLENCODE($targetForm) & "|navigationMode=" & $navigationMode & "'", 
				  	"style= 'text-decoration: none;' href='" & $url & "' target='" & $target & "'"
				); 

				LET $tile:= 
				"<a " & $hyperlink & " title='" & $name & "'>"&
				  "<div class='core-tile' style='border: solid 1px #E0E0E0;'>" & 
				    $title & 
			        $other &
				  "</div>
				</a>";

				LET $svg := HASH_GET($svgMap, $iconName);

			    LET $icon:= 
			  	"<a " & $hyperlink & " title = '"& $name & "' >" &
				  "<div style= 'width: 100%; height: 100%;'>" & 
			        $svg & 
			      "</div>" &
				"</a>";

			  RETURN 
			  HASH(
				"tile", $tile, 
				"icon", $iconName, 
				"iconbackcolour", $backColour
			  )
		  )
	   )
  )

LET $showPerLine := 3;

LET $columns:= 
ARRAY(
		"Custom Link Text", 
		"Custom Link URL", 
	  	"Custom Link Navigation",
		"Custom Link Visibility"
	);

LET $linkData := 
ARRAY_TRANSPOSE(
  ARRAY_SELECT( $columns, $(c, col) HASH_GET([Settings], $col) )
  );

LET $links:= 
ARRAY_WHERE(
  $linkData, $(i,item)
  LET $link := 	ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Custom Link Text")); 
  RETURN 
  $link <> NULL
 );



LET $filtered := 
ARRAY_WHERE(
	$links, $(i, item) 
	ARRAY_LENGTH(
		ARRAY_WHERE(
			ARRAY_INDEX($item, 4), $(i, item)
		  	ARRAY_INDEXOF(
              HASH_GET([User to Group Mapping], "Current User Roles"), 
              $item
            ) > 0
		)
	) > 0
);

LET $set := 
ARRAY_SELECT(
	$filtered, $(i, item) 
	ARRAY_TAKE($item, 3)
);

LET $lines := CEILING(ARRAY_LENGTH($set) / $showPerLine );

LET $filtered := 
ARRAY_SELECT(
	RANGE(0, $lines), $(l, line)
	ARRAY_TAKE(ARRAY_SKIP($set, $showPerLine * ($l - 1)), $showPerLine)
);
RETURN
ARRAY_SELECT( 
  $filtered, $(i, item)
	ARRAY_SELECT( $item, $(j,jitem) 
	  	INDEXES_TO_KEYS($jitem, ARRAY("Name", "URL", "Navigation"))
	  )
)

LET $filter := 
"[Subject] IS NOT EMPTY AND TO_DAY([Announcement Date]) <= " & DB_ENCODECONSTANT(TODAY()) & " AND " &
"TO_DAY([End Date]) >= " & DB_ENCODECONSTANT(TODAY()) & " AND " &
"[Remove from Dashboard] <> TRUE";

LET $columns:=
ARRAY(
  	"RecordID",
   	"Subject", 
   	"Announcement Date",
  	"Body"
  );

LET $isNormalUser := 
ARRAY_INDEXOF(
  HASH_GET([User to Group Mapping], "Current User Roles"), 
  "Everyone"
) > 0;

LET $show:= HASH_GET([Settings], "Show Announcement");
LET $show:= OR( $show = "Always", $show = "Count Dependent");
RETURN
IF( 
  AND(
	$show = TRUE,
  	$isNormalUser == TRUE
  ),
  LET $result :=
  DB_LOOKUPALL(
    $columns,
    "Announcement", 
    $filter
  );

  RETURN
	ARRAY_SELECT(
		$result, 
		$(i,item) 
		LET $recordID:= ARRAY_FIRST($item);
		LET $attachmentCount := EBMS_GET_EFORMRECORD_ATTACHMENT_COUNT("Announcement", $recordID, "/");

		RETURN HASH_MERGE(
				INDEXES_TO_KEYS($item, $columns),
				HASH("Attachment", $attachmentCount > 0)
		  )
	  )
  )

LET $show:= HASH_GET([Settings], "Show Action Prompt")= "Yes";

LET $isNormalUser := 
ARRAY_INDEXOF(
  HASH_GET([User to Group Mapping], "Current User Roles"), 
  "Everyone"
) > 0;

RETURN
IF(
  AND(
  	$show = TRUE,
  	$isNormalUser == TRUE
  ),
  LET $set:=
  DB_LOOKUPALL(
	  ARRAY(
		  "Prompt 1", 
		  "Prompt 2", 
		  "Prompt URL",
		  "Prompt Navigation",
		  "Prompt Access", //5
		  "Prompt Module Dependent",
		  "Prompt Module"
	  ),
	  "Home - Sentence Builder", 
	  "[LineNumber] FAST_EQUAL_TO 1 AND [Prompt Enabled] FAST_EQUAL_TO TRUE AND [Prompt 1] IS NOT NULL"
  );

  LET $moduleList:= 
  ARRAY_SELECTMANY(
	EACH [Module Data], $(l,line)
	ARRAY_SELECTMANY($line, $(i,item) IF( HASH_GET($item, "Access Type") <> "External Link", HASH_GET($item, "Name")))
	);
  //filter by prompt access
  LET $filtered := 
  ARRAY_WHERE(
	  $set, $(i, item) 
	LET $moduleDependent:=  ARRAY_INDEX($item, 6);
	LET $promptModule:=  ARRAY_INDEX($item, 7);
	LET $access:= ARRAY_INDEX($item, 5);
	RETURN
	IF( 
	  $moduleDependent = TRUE, 
	  ARRAY_INDEXOF($moduleList, $promptModule)>0, 

		ARRAY_LENGTH(
			ARRAY_WHERE(
				$access, $(i, item)
			  	ARRAY_INDEXOF(
                  HASH_GET([User to Group Mapping], "Current User Roles"), 
                  $item
                ) > 0
		    )
		) > 0
	  )
  );

  LET $data := 
  ARRAY_SELECT(
	  $filtered, $(i, item) 
	  ARRAY_TAKE($item, 4)
  );
  RETURN ARRAY_TO_HASH_GROUPED(
	  $data, $(i,item) ARRAY_INDEX($item, 1),
	  $(i,item) INDEXES_TO_KEYS( ARRAY_SKIP($item, 1), ARRAY( "Prompt 2", "URL", "Navigation"))
	)

)

LET $columns := ARRAY("RecordID", "Category Name", "Status"); 

LET $data := 
DB_LOOKUPALL(
  $columns,
  "GTM Category", 
  "[LineNumber] FAST_EQUAL_TO 1", 
  HASH("sort", "[Category Name] ASC")
); 

LET $result := 
ARRAY_SELECT($data, $(i, item) INDEXES_TO_KEYS($item, $columns));

RETURN $result

LET $result := 
ARRAY_WHERE(
  [GTM Categories - All], $(i, item) 
  HASH_GET($item, "Status") = "Limited"
);

LET $maxToShow := 10; 
