LET $exceededMax := $length > $maxToShow; 

RETURN 
IF( 
  $exceededMax = TRUE, 
  ARRAY_CONCAT(ARRAY_TAKE($result, $maxToShow), "SHOW MORE"),
  ARRAY_CONCAT(ARRAY_TAKE($result, $maxToShow), "")
)

LET $limitedLength := ARRAY_LENGTH(EACH [GTM Category - Limited]);

LET $result :=  
ARRAY_WHERE(
  [GTM Categories - All], $(i, item) 
  HASH_GET($item, "Status") = "Ready"
);

LET $baseLength := ARRAY_LENGTH($result);

LET $result :=  
ARRAY_SELECT(
  ARRAY_PAD($result, GREATEST(ARRAY_LENGTH($result), $limitedLength)), $(i,item) 
  COALESCE($item, "")
);

LET $maxToShow := 10; 
LET $length := ARRAY_LENGTH($result); 
LET $exceededMax := $baseLength > $maxToShow; 

RETURN 
IF( 
  $exceededMax = TRUE, 
  ARRAY_CONCAT(ARRAY_TAKE($result, $maxToShow), "SHOW MORE"),
  ARRAY_CONCAT(ARRAY_TAKE($result, $maxToShow), "")
)

LET $limitedLength := ARRAY_LENGTH(EACH [GTM Category - Limited]);
LET $readyLength := ARRAY_LENGTH(EACH [GTM Category - Ready]);

LET $result := 
ARRAY_WHERE(
  [GTM Categories - All], $(i, item) 
  HASH_GET($item, "Status") = "Emerging"
);

LET $baseLength := ARRAY_LENGTH($result);

LET $result := 
ARRAY_SELECT(
  ARRAY_PAD($result, GREATEST($readyLength, $limitedLength)), $(i,item) 
  COALESCE($item, "")
);

LET $maxToShow := 10; 
LET $length := ARRAY_LENGTH($result); 
LET $exceededMax := $baseLength > $maxToShow; 

RETURN 
IF( 
  $exceededMax = TRUE, 
  ARRAY_CONCAT(ARRAY_TAKE($result, $maxToShow), "SHOW MORE"),
  ARRAY_CONCAT(ARRAY_TAKE($result, $maxToShow), "")
)
