FormulaCriterion.Formula:
LET $clickID := 
SPLIT(
  $clickid, 
  "]|["
 ); 

LET $result := N(ARRAY_INDEX($clickID, 2)); 

RETURN 
AND(
  ISNUMBER($result), 
  $result <> NULL
)

Binding.Expression:
LET $data :=
DB_LOOKUPALL(
  ARRAY(
	"Linked Records", 
	"RecordID"
  ),
  "Linked Records", 
  "[Status] <> 'Deleted' AND [Linked Record EFormType] = " & DB_ENCODECONSTANT([EFormType]) & " AND " & 
  "[Linked Record RecordID] = " & DB_ENCODECONSTANT([RecordID]) 
);

LET $data :=  
ARRAY_SELECT(
  $data, $(i, item) 
  LET $recordData := ARRAY_INDEX($item, 1);
  LET $linkRecordID := ARRAY_INDEX($item, 2);
  RETURN 
  ARRAY_SELECT(
	$recordData, $(i, item) 
	ARRAY_CONCAT(
	  $item, 
	  $linkRecordID
	)
  )
);

LET $matches := 
ARRAY_WHERE(
  ARRAY_SELECTMANY($data, $(i, item) $item),
  $(i, item)
  ARRAY(
	ARRAY_INDEX($item, 1), 
	ARRAY_INDEX($item, 2)) <> ARRAY([EFormType], [RecordID]
  )
); 

LET $headings := 
ARRAY(
	HASH("heading", "Linked record", "width", 800),
	HASH("heading", "", "width", 100)
);

LET $rows := 
ARRAY_SELECT(
  $matches, $(i, item) 
  LET $eFormType := ARRAY_INDEX($item, 1);
  LET $recordID := ARRAY_INDEX($item, 2);

  LET $description  := 
  //ARRAY_INDEX($item, 3); //point in time
  DB_LOOKUP("Description", $eFormType, HASH("RecordID", $recordID)); //live  

  LET $linkRecordID := ARRAY_INDEX($item, 4);
  RETURN 
  ARRAY(
	HASH(
	  "url", EBMS_GET_EFORMRECORD_URL($eFormType, $recordID), 
	  "urlTarget", "_blank", 
	  "value", COALESCE($description, $eFormType & " - " & $recordID)
    ), 
	HASH(
	  "value", "Remove link",
	  "clickid", "Delete]|[" & $linkRecordID
	)
  )
);

RETURN
HASH(
	"columns", 	$headings, 
	"rows", 	$rows
)

FieldEntry.Formula:
LET $numberInRow := $eventargs;
LET $process := IF( $numberInRow > ARRAY_LENGTH([This Stage Process List]), "Tracking", //fallback process
$numberInRow = NULL, ARRAY_LAST([This Stage Process List]),
ISNUMBER($numberInRow) = TRUE, ARRAY_INDEX([This Stage Process List], $numberInRow),
$eventargs );
LET $result := IF( ARRAY_INDEXOF([Visible Steps], $process) > 0, ARRAY_WHERE([Visible Steps], $(i, item) $item <> $process), ARRAY_CONCAT([Visible Steps], $process) );
RETURN IF( [Support Multiple Visible] = TRUE, ARRAY_WHERE( ARRAY_DISTINCT($result), $(i, item) $item <> NULL ), ARRAY($process) )
