FieldEntry.Formula:
LET $data := 
ARRAY_CONCAT(
	[Notes],
	ARRAY(
		ARRAY(
			EBMS_GET_CURRENT_USER(), 
			NOW(),
			"<p>" & HTMLENCODE([New Message]) & "</p>"
		)
	)
);

RETURN 
ARRAY_WHERE(
	$data, $(i,item) 
	AND(
		$item <> ARRAY(),
		$item <> NULL()
	)
)

