Column.Formula:
LET $deposit := AND(
		[Deposit Required?] == TRUE,
		OR(
			[Deposit Collected?] = "Awaiting Inv No",
			[Deposit Collected?] = "Awaiting Access Payment"
		));

LET $accessChargeBalance := AND(
		[Deposit Required?] == TRUE,
		OR([Deposit Collected?] = "Paid", [Deposit Collected?] = "Waived"),
		OR([Fee Collected?] = "Awaiting Inv No",[Fee Collected?] = "Awaiting Access Payment")
		);

LET $accessCharge := AND(
	[Deposit Required?] <> TRUE,
		OR(
			[Fee Collected?] = "Awaiting Inv No",
			[Fee Collected?] = "Awaiting Access Payment"
			)
		);

RETURN IF(
			$deposit = TRUE, "_PLACEHOLDER_",
		    $accessChargeBalance, "_PLACEHOLDER_",
			$accessCharge = TRUE, "_PLACEHOLDER_",
			NULL()
)

LET $accessChargeBalance := [Access Charge] - [Deposit Amount Received] - [Access Charge Reduced]; 

RETURN
IF(
    OR(
		[Fee Collected?] == "Paid",
		[Fee Collected?] == "Waived"
	),
	0,
  	$accessChargeBalance
)

//Opens all processes
LET $keys := HASH_KEYS([Process Data]);

RETURN 

INDEXES_TO_KEYS(
  ARRAY_SELECT($keys, $(i, item) TRUE), 
  $keys
)

LET $set := 
HASH_MERGE(
  HASH(
	"Details", 
    HASH(
	  "Process", "Details", 
	  "Process Display Name", "Details"
    )
  ), 

  /*HASH(
	"Requested by", 
    HASH(
	  "Process", "Requested by", 
	  "Process Display Name", "Requested by"
    )
  ),*/

  IF( 
    [Process Required?] = "Yes", 
    HASH(
	  "Application Fee", 
      HASH(
    	"Process", "Application Fee", 
    	"Process Display Name", "Application Fee"
      )
	)
  ),

  IF( 
    [Process Required?] = "Yes",
    HASH(
	  "Assessment", 
      HASH(
    	"Process", "Assessment", 
    	"Process Display Name", "Assessment"
      )
	)
  ),

  IF( 
    [Process Required?] = "Yes", 
    HASH(
	  "Access Charge", 
      HASH(
    	"Process", "Access Charge", 
    	"Process Display Name", "Access Charge"
      )
	)
  ),

    IF( 
    [Process Required?] = "Yes",
    HASH(
	  "Outcome", 
      HASH(
    	"Process", "Outcome", 
    	"Process Display Name", "Outcome"
      )
	)
  )
); 

RETURN $set

//Never does a tick
LET $keys := HASH_KEYS([Process Data]);

RETURN 

INDEXES_TO_KEYS(
  ARRAY_SELECT($keys, $(i, item) FALSE), 
  $keys
)

