Column.ValidationFormula:
LET $alreadyExists := DB_LOOKUP( "RecordID", "Change of Role", "[LineNumber] = 1 AND [Role] = " & DB_ENCODECONSTANT([Role]) & " AND [Status] = 'Draft'", HASH( "parent", ARRAY(EFormType, RecordID), "sort", "[DateCreated] DESC" ) ) <> NULL();
LET $text := IF( ISBLANK([Individual Display Name in Role]), "allocated", "reallocated" );
RETURN IF( ISBLANK([Role]), "Role cannot be empty",
$alreadyExists = TRUE, "This role is already being " & $text & " for this record", TRUE )

Column.Formula:
LET $roleChanges := 
HASH_GET([Change of Role Data], [Role]); 

LET $matches := 
ARRAY_WHERE(
  $roleChanges, $(i, item) 
  HASH_GET($item, "Status") = "Draft"
);

RETURN 
HASH_GET(ARRAY_FIRST($matches), "Status")

LET $roleChanges := 
HASH_GET([Change of Role Data], [Role]); 

LET $matches := 
ARRAY_WHERE(
  $roleChanges, $(i, item) 
  HASH_GET($item, "Status") = "Draft"
);

RETURN 
HASH_GET(ARRAY_FIRST($matches), "RecordID")

LET $columns := ARRAY("RecordID", "Status", "New User in Role", "New Person in Role", "DateCreated", "Role");

LET $data := 
IF( 
  [Change of Role Data] = NULL, 
  DB_LOOKUPALL(
	$columns, 
	"Change of Role", 
    "[LineNumber] = 1",
    HASH(
    	"parent", ARRAY(EFormType, RecordID),
    	"sort", "[DateCreated] DESC"
    )	
   ), 
);

LET $data := IFERROR($data, NULL()); 

LET $hashed :=  
ARRAY_SELECT(
  $data, $(i, item)
  INDEXES_TO_KEYS(
    $item, 
    $columns
  )
);

LET $result := 
ARRAY_TO_HASH_GROUPED(
  $hashed, 
  $(i, item) HASH_GET($item, "Role"),
  $(i, item) $item
);

RETURN 
IF( 
  [Change of Role Data] = NULL, 
  $result, 
  [Change of Role Data]
)

Column.ValidationFormula:
LET $data := IF( $thisValue <> NULL, DB_LOOKUPALL( "Linked Records", "Linked Records", "[Status] <> 'Deleted' AND [Linked Record EFormType] = " & DB_ENCODECONSTANT([EFormType]) & " AND " & "[Linked Record RecordID] = " & DB_ENCODECONSTANT([RecordID]) ) );
LET $matches := ARRAY_WHERE( ARRAY_SELECTMANY($data, $(i, item) $item), $(i, item) ARRAY(ARRAY_INDEX($item, 1), ARRAY_INDEX($item, 2)) <> ARRAY([EFormType], [RecordID]) );
LET $recordIDs := ARRAY_SELECT($matches, $(i, item) ARRAY_INDEX($item, 2));
RETURN IF( $thisValue = NULL, "A record must be selected.",
ARRAY_INDEXOF( $recordIDs, $thisValue ) > 0, "Record is already listed as related." )

