  "Name of EDRMS",
  "Document Upload"
);

LET $data := 
DB_LOOKUP(
  $columns, 
  "Global Settings",
  "[RecordID] = 1"
);

RETURN INDEXES_TO_KEYS($data, $columns)

LET $columns := ARRAY( "Default Processing Days", "Processing Period Filter", "Default Appeal Days", "Appeal Period Filter", "Name of Primary Regulator", "Shortname of Primary Regulator", "Correspondence Tracking", "Outgoing Emails", "Department Visibility", "Region Visibility", "Tax Inv No Visibility", "Receipt No Visibility", "Time Spent Visibility", "Location Address Visibility", "Section Transfer", "Assessment Reference Required", "Consultation Required" );
LET $data := DB_LOOKUP( $columns, "Settings - Information Requests", "[RecordID] = 1" );
RETURN INDEXES_TO_KEYS($data, $columns)

