Column.Formula:
LET $visible := TRUE;//ARRAY_INDEXOF(EACH [Mail Merge Visible], TRUE) > 0;
LET $references := EACH [Mail Merge Purpose]; 

LET $uniqueReferences := ARRAY_SELECT($references, $(i, item) DB_ENCODECONSTANT($item));

LET $filter := "([Reference] = " & ARRAY_JOIN($uniqueReferences, " OR [Reference] = ") & ")";

LET $recordData := 
IF( 
  [All Mail Merge Options] = NULL,
  DB_LOOKUPALL(
    ARRAY("RecordID", "Reference"), 
    "Mail Merge Template", 
    "[LineNumber] = 1" & 
    " AND [Context EFormType] = " & DB_ENCODECONSTANT([EFormType]) & 
    " AND " & $filter
  )
); 

LET $custom := 
ARRAY_SELECT(
  $recordData, $(i, data)
  LET $recordID := ARRAY_INDEX($data, 1); 
  LET $reference := ARRAY_INDEX($data, 2); 
  LET $files := 
  (
	LET $files := 
	ARRAY_SELECT(
	  EBMS_GET_ATTACHMENTS("Mail Merge Template", $recordID, "Mailmerge"), $(i, item) 
	  HASH(
		"FileName", 
		(
		  LET $fileName := HASH_GET($item, "FileName");
		  RETURN $fileName
		  //RETURN IF(RIGHT($fileName, 5) = ".docx", LEFT($fileName, LEN($fileName) - 5), $fileName)
		),
		"RecordID", $recordID, 
		"MimeType", HASH_GET($item, "MimeType"),
		"Type", "Custom"
	  )
	); 

	RETURN 
	ARRAY_WHERE(
	  $files, $(i, item) 
	  OR(
		HASH_GET($item, "MimeType") = "application/msword",
		HASH_GET($item, "MimeType") = "application/vnd.openxmlformats-officedocument.wordprocessingml.document"
	  )
	)
  );

  RETURN ARRAY($reference, $files)
); 

LET $result := 
ARRAY_TO_HASH(
  $custom, 
  $(i, item) ARRAY_INDEX($item, 1),
  $(i, item) ARRAY_INDEX($item, 2) 
);

RETURN 
IF( 
  [All Mail Merge Options] <> NULL,
    [All Mail Merge Options],
  $result
)

LET $enabled :=OR(
  HASH_GET([Information Request Settings], "Correspondence Tracking") = "Automatic Email Capture Enabled", 
  HASH_GET([Information Request Settings], "Outgoing Emails") = "Email Sending Enabled"
); 

RETURN
IF( 
  NOT($enabled), 
    FALSE, 
  [Show Emails]
)

LET $cleanData := 
ARRAY_WHERE(
	[Notes], $(i, item) 
	ARRAY_INDEX($item, 3) <> NULL()
);

LET $formatted := 
ARRAY_SELECT(
	$cleanData, $(i, item) 
	LET $user := ARRAY_INDEX($item, 1); 
	LET $datePosted := ARRAY_INDEX($item, 2); 
	LET $message := IFERROR(ARRAY_INDEX($item, 3), "");
	LET $attachments := ARRAY_INDEX($item, 4);
	LET $isSystem := ROLE("system") == $user;
	LET $isCurrentUser := EBMS_GET_CURRENT_USER() == $user;
	LET $userDisplayName := EBMS_GET_ROLE_DISPLAYNAME($user); 
	LET $datePostedFormatted := DATEFORMAT($datePosted, "dd-MMM-yyyy hh:mm tt");
	LET $class := IF($isCurrentUser, "right-top", "left-top");
	LET $colour := IF($isCurrentUser, "#00497b" , "#347016");
	LET $classes := 
    IF( 
	  $isSystem = TRUE, 
      "filenotes-system-message",
      "talk-bubble tri-right " & $class
	);

    //----
    LET $username := "<span style=""font-weight: 600; color: " & $colour & """>" & HTMLENCODE($userDisplayName) & "</span>";
    LET $date := "<span style=""" & IF($isSystem = TRUE, "float: right; padding-right: 15px;", /*"font-style: italic;"*/) & """> " & $datePostedFormatted & " </span>";
    LET $message := "<span style=""text-align: left; word-wrap: break-word;"">" & $message & "</span>";
    //----
    RETURN 
	IF( 
	  //System message
	  $isSystem = TRUE, 
	  "<div class=""" & $classes & """>" & $message & $date & "</div>",

	  //User message
      "<div class=""" & $classes & """>" & $username & $date & $message & "</div>"
	)
);

RETURN 
IFERROR(
	ARRAY_JOIN(
		$formatted, ""
	), 
	NULL()
)

LET $plaintext := 
IFERROR(
  STRIPXML([Scope of Request]), 
  [Scope of Request]
);

//makes the stripped look a little nicer by injecting spaces in a couple places
LET $cleaned := 
REGEX_REPLACE($plaintext, "[\?\.\!][A-z]", $(item) LEFT($item, 1) & " " & RIGHT($item, 1)); 

RETURN $cleaned

LET $text := [Plain Text Scope of Request];

LET $trimmed := ARRAY_JOIN(ARRAY_TAKE(SPLIT($text, " "), 25), " "); 

RETURN 
IF(
  $trimmed = $text, $text, 
  $trimmed & "...<text trimmed>"
)

LET $current := ARRAY([EFormType], [RecordID]);
LET $descriptionColumns := 
HASH(
	"Contract",  					"Contract Number", 
	"Vendor", 						"Company Name",
	"Procurement", 					"Procurement Number",
	"Project", 						"Project Number",
    "Evaluation", 		    		"Description",
  	"Information Request",  		"File No.", 
	"Subpoena", 					"File No.",
	"Privacy Incident", 			"File No."
); 

LET $altFormName:= HASH(
  	"Insurance",	"Compliance"
  );

LET $getLabelText := $(eFormType, recordID)
(
  	LET $targetColumn := HASH_GET($descriptionColumns, $eFormType);
  	RETURN 
  	IF( $targetColumn = NULL, NULL,
		LET $isthisRecord := ARRAY($eFormType, $recordID) = $current;
		LET $title:= 
		IF( $isthisRecord = TRUE, 
			CURRENT_VALUE($targetColumn),
			ARRAY_FIRST(DB_LOOKUP(
				ARRAY($targetColumn),
				$eFormType, 
				"[RecordID] FAST_EQUAL_TO " & $recordID
			))
		  );
		//shortern the descriptive label/ title
		LET $charCount := LEN($title);
		LET $titleArray := SPLIT($title, " ");
		LET $wordCount := ARRAY_LENGTH($titleArray);

		LET $charLimit := 30; 
		LET $wordLimit := ARRAY_LENGTH( SPLIT( LEFT($title, $charLimit), " ") );

	  RETURN //ARRAY_TAKE($titleArray, $wordLimit)
		IF(
			$wordCount <= $wordLimit, $title,
			ARRAY_JOIN(
				ARRAY_TAKE($titleArray, $wordLimit), " "
			)& " ..."
		)
	  )

); 

LET $getReadAccess := $(eFormType, recordID)
( EBMS_GET_EFORMRECORD_PERMISSION($eFormType, $recordID, "read"));

LET $currentDes := CALL($getLabelText, [EFormType], [RecordID]);
LET $currentAccess := FALSE;

RETURN
IF(
	[Parent Structure] <> NULL, [Parent Structure],
  	(

	  LET $parent := 
	  IF( 
		[RecordID] > 0, 
		(
		  LET $record := EBMS_GET_PARENT_RECORD(ARRAY_FIRST($current), ARRAY_INDEX($current, 2));
		  RETURN 
			  IF(  ARRAY_FIRST($record) == NULL, NULL,
				ARRAY_CONCAT( 
				  $record, 
				  CALL($getLabelText, ARRAY_FIRST($record), ARRAY_INDEX($record, 2)),
				  CALL($getReadAccess, ARRAY_FIRST($record), ARRAY_INDEX($record, 2))
				  )

			  )
	  )); 

	  LET $gParent := 
	  IF( 
		 $parent <> NULL, 
		(
		  LET $record := EBMS_GET_PARENT_RECORD(ARRAY_FIRST($parent), ARRAY_INDEX($parent, 2));
		  RETURN 
			  IF(  ARRAY_FIRST($record) == NULL, NULL,
				ARRAY_CONCAT( 
				  $record, 
				  CALL($getLabelText, ARRAY_FIRST($record), ARRAY_INDEX($record, 2)),
				  CALL($getReadAccess, ARRAY_FIRST($record), ARRAY_INDEX($record, 2))
			  )
		  )
	  )); 

	  LET $hash :=  
	  HASH_MERGE(
		  IF( $parent <> NULL, HASH("parent", 	$parent)),
		  IF( $gParent <> NULL, HASH("gparent", 	$gParent))
	  );

	  LET $parentStructure := 	//2nd instance
	  IF( 
		  ARRAY_LENGTH(HASH_KEYS($hash)) >0, $hash, NULL
	  );

	  LET $parentData:= 
	  ARRAY_WHERE(
		ARRAY(
			$gParent, 
			$parent, 
			ARRAY_CONCAT($current, $currentDes, $currentAccess),
		), $(i,item) $item <> NULL
	   );


	  LET $parentChain:=
		ARRAY_SELECT(
			  $parentData, $(i, item)
			  ARRAY_TAKE($item, 2)
	   );


	  LET $parentLinks :=
	  ARRAY_SELECT(
		  $parentData, $(i, item) 
			  LET $eFormType 		:= ARRAY_FIRST($item);
			  LET $recordID 		:= ARRAY_INDEX($item, 2);
			  LET $descriptiveLabel := ARRAY_INDEX($item, 3);
			  LET $hasReadAccess	:= ARRAY_INDEX($item, 4);
			  LET $URL	:= "records/" & URLENCODE($eFormType) & "/" & $recordID ;
			  LET $showEFormType := $eFormType <> "Vendor";

			  LET $labelColour := 
				  IF(
				   AND($eFormType = [EFormType], $recordID = [RecordID]),
					  "background: #ffffff; color: #5D5D5D;",
					 $hasReadAccess = TRUE,
					  "background: #ffffff; color: #4D87B7;",
					  "background: #ffffff; color: #5D5D5D;"

				  );



			  LET $labeltext:= 
					  IF( $showEFormType == TRUE, " " & COALESCE(HASH_GET($altFormName, $eFormType), $eFormType) ) &
					  IF(NOT(ISBLANK($descriptiveLabel)), " " & $descriptiveLabel);

			  LET $label :=  
				  "<span style=""margin-left: 5px;" & $labelColour & """>" &
				  HTMLENCODE($labeltext) & 
				  "</span>";

			  RETURN 
			  IF( 
				  $hasReadAccess == TRUE, 
				  "<a style=""text-decoration-line: none;"" href=" & HTMLENCODE($URL) & ">" & $label & "</a>",
				  $label 
			  )
	  );

	   LET $separator :=   "<svg style=""vertical-align: bottom; margin-left: 5px; height: 18px;"" xmlns=""http://www.w3.org/2000/svg"" viewBox=""0 0 256 512""><path fill=""#5D5D5D"" d=""M246.6 278.6c12.5-12.5 12.5-32.8 0-45.3l-128-128c-9.2-9.2-22.9-11.9-34.9-6.9s-19.8 16.6-19.8 29.6l0 256c0 12.9 7.8 24.6 19.8 29.6s25.7 2.2 34.9-6.9l128-128z""/></svg>";

	  LET $breadcrumb := "<div style=""height: 23px; padding-left: 5px; font-size: 10pt; color: #5D5D5D; "">You are here:" & ARRAY_JOIN($parentLinks, $separator) & "</div>";

	  RETURN 
	  HASH(
		  "Parent Structure",	$parentStructure,
		  "Parent Data", 		$parentData,
		  "Parent Chain",		$parentChain,
		  "Breadcrumb",		$breadcrumb
		)
	 )
)

MergeField.Formula:
LET $data := 
	ARRAY_TRANSPOSE(
		ARRAY(
			EACH [Assessment Reference Name],
			EACH [Assessment Reference Location],
			EACH [Assessment Reference Notes]
		)
	);

RETURN $data

LET $exemptions := EACH [Exemptions Applied];
LET $exemptionNotes := EACH [Exemption Notes];

LET $data := 
	DB_LOOKUPALL(
		ARRAY(
	  		"Exemption Code", 
	  		"Exemption Title"
  		),
		"Freedom of Information Exemptions",
		"[LineNumber] =  1 AND [Exemption Code] IN " & DB_ENCODECONSTANT($exemptions)
	);

LET $cleanedData := ARRAY_SELECT($data, $(i, item) ARRAY_CONCAT($item, ARRAY_INDEX($exemptionNotes, $i)));

RETURN $cleanedData

LET $applicantAddress := [Applicant Street Address] & ", "
& [Applicant Suburb] & " "
& [Applicant State] & " "
& [Applicant Postcode];

LET $applicantDetails := ARRAY([Applicant Name], [Applicant Organisation], $applicantAddress);

LET $cleaned := ARRAY_WHERE($applicantDetails, $(i, item) $item <> "");

 RETURN ARRAY_JOIN($cleaned, NEWLINE())

Binding.Expression:
