LET $start := [Date Received];
LET $end := NOW();
LET $processingPeriodFilter := HASH_GET([Information Request Settings], "Processing Period Filter"); 

LET $dayDiff := 
IF( 
  $processingPeriodFilter = "Excluding Weekends",
  WORKDAYSBETWEEN($start, $end),
  TODAY($end) - TODAY($start)
);

LET $flooredDays := FLOOR($dayDiff); 
LET $flooredDayString := $flooredDays & " day" & IF(OR($flooredDays ==0, $flooredDays > 1), "s");
LET $result := $flooredDayString;

RETURN 
IF( 
  [Date Received] <> NULL, 
  "Since received: " & $result & "*"
)

LET $start := NOW();
LET $end := [Due Date];
LET $processingPeriodFilter := HASH_GET([Information Request Settings], "Processing Period Filter"); 

LET $dayDiff := 
IF( 
  TODAY($end) = TODAY($start),
  	0,  

  $processingPeriodFilter = "Excluding Weekends",
    WORKDAYSBETWEEN($start, $end),

  TODAY($end) - TODAY($start)
); 

LET $flooredDays := FLOOR($dayDiff); 
LET $flooredDayString := $flooredDays & " day" & IF(OR($flooredDays ==0, $flooredDays > 1), "s");
LET $result := $flooredDayString;

RETURN 
IF( 
  [Due Date] <> NULL, 
  "Until due: " & $result & "*"
)

