FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $requestedFields := ARRAY( 
	"Applicant Organisation",
	"Applicant Email",
	"Applicant Contact Phone Number",
	"Applicant State",
	"Applicant Suburb",
	"Applicant Postcode",
	"Applicant Street Address"
);

LET $data := DB_LOOKUP(
	$requestedFields, 
	"Information Request",
	HASH("Applicant Name", [Applicant Name]), 
	HASH("sort", "[RecordID] DESC")
);

return ARRAY_TO_HASH(
	$data,
	$(i, item)
	(
		ARRAY_INDEX($requestedFields, $i)
	),
	$(i, item)
	(
		$item
	)
)

