FieldEntry.Formula:
LET $data := 
ARRAY_CONCAT(
	[Notes],
	ARRAY(
		ARRAY(
			EBMS_GET_CURRENT_USER(), 
			NOW(),
			[New Message], 
			[Attachment Names]
		)
	)
);

RETURN 
ARRAY_WHERE(
	$data, $(i,item) 
	AND(
		$item <> ARRAY(),
		$item <> NULL()
	)
)

FormulaCriterion.Formula:
LET $clickID := 
SPLIT(
  $clickid, 
  "]|["
 ); 

LET $result := N(ARRAY_INDEX($clickID, 2)); 

RETURN 
AND(
  ISNUMBER($result), 
  $result <> NULL
)

Binding.Expression:
LET $data :=
DB_LOOKUPALL(
  ARRAY(
	"Linked Records", 
	"RecordID"
  ),
  "Linked Records", 
  "[Status] <> 'Deleted' AND [Linked Record EFormType] = " & DB_ENCODECONSTANT([EFormType]) & " AND " & 
  "[Linked Record RecordID] = " & DB_ENCODECONSTANT([RecordID]) 
);

LET $data :=  
ARRAY_SELECT(
  $data, $(i, item) 
  LET $recordData := ARRAY_INDEX($item, 1);
  LET $linkRecordID := ARRAY_INDEX($item, 2);
  RETURN 
  ARRAY_SELECT(
	$recordData, $(i, item) 
	ARRAY_CONCAT(
	  $item, 
	  $linkRecordID
	)
  )
);

LET $matches := 
ARRAY_WHERE(
  ARRAY_SELECTMANY($data, $(i, item) $item),
  $(i, item)
  ARRAY(
	ARRAY_INDEX($item, 1), 
	ARRAY_INDEX($item, 2)) <> ARRAY([EFormType], [RecordID]
  )
); 

LET $headings := 
ARRAY(
	HASH("heading", "Linked record", "width", 800),
	HASH("heading", "", "width", 100)
);

LET $rows := 
ARRAY_SELECT(
  $matches, $(i, item) 
  LET $eFormType := ARRAY_INDEX($item, 1);
  LET $recordID := ARRAY_INDEX($item, 2);

  LET $description  := 
  //ARRAY_INDEX($item, 3); //point in time
  DB_LOOKUP("Description", $eFormType, HASH("RecordID", $recordID)); //live  

  LET $linkRecordID := ARRAY_INDEX($item, 4);
  RETURN 
  ARRAY(
	HASH(
	  "url", EBMS_GET_EFORMRECORD_URL($eFormType, $recordID), 
	  "urlTarget", "_blank", 
	  "value", COALESCE($description, $eFormType & " - " & $recordID)
    ), 
	HASH(
	  "value", "Remove link",
	  "clickid", "Delete]|[" & $linkRecordID
	)
  )
);

RETURN
HASH(
	"columns", 	$headings, 
	"rows", 	$rows
)

MailMerge.ResultFileNameTemplate:
{LET $name:= HASH_GET([Mail Merge Options - Document Selection]#7, "FileName");

LET $cleanName:= REGEX_REPLACE($name,  "[\*\\\:\<\>\?\/\|\""]", $(i, item) "");

LET $cleanName := LEFT($cleanName, FIND(".", $cleanName)-1);


RETURN DATEFORMAT(NOW(), "yyyy-MMM-dd") & " - " & $cleanName & ".docx"}

{LET $name:= [Mail Merge Purpose];
LET $cleanName:= REGEX_REPLACE($name,  "[\*\\\:\<\>\?\/\|\""]", $(i, item) "");
RETURN DATEFORMAT(NOW(), "yyyy-MMM-dd") & " - " & $cleanName & ".docx"}

Binding.Expression:
NOT(
  AND(
    [Mail Merge Options - Document Selection] <> NULL, 
    HASH_GET([Mail Merge Options - Document Selection], "FileName") <> [Mail Merge Purpose]  
  )
)

LET $results := [Mail Merge Options];

LET $hashed :=  
ARRAY_SELECT(
  $results, $(i, item) 
  HASH(
	"text", HASH_GET($item, "FileName"),
	"value", $item
  )
);

RETURN $hashed

