FieldEntry.Formula:
LET $days := HASH_GET([Information Request Settings], "Default Appeal Days"); 
LET $filter := HASH_GET([Information Request Settings], "Appeal Period Filter"); 

RETURN 
IF(
	$filter = "Excluding Weekends", 
	WORKDAYSBETWEEN(
		[Date of Decision], 
		[Date of Decision] + $days
	) + 1,
	$filter = "Including Weekends", 
	[Date of Decision] + $days,
	NULL
)

LET $days := HASH_GET([Information Request Settings], "Default Processing Days"); 
LET $filter := HASH_GET([Information Request Settings], "Processing Period Filter"); 

LET $dateDepositLetterSent := [Date Deposit Request Letter Sent]; 
LET $dateDepositReceived := [Deposit Date Received]; 

LET $includingWeekends := $filter = "Excluding Weekends"; 

RETURN
IF( 
  //date valid must be entered
  [Date Valid] = NULL, 
    NULL, 

  //if deposit recieved then calculate end date
  AND(
    [Deposit Date Received] <> NULL, 
    $filter = "Excluding Weekends"
  ), 
    WORKDAY([Date Valid], $days),


  AND(
    [Deposit Date Received] <> NULL, 
    $filter = "Including Weekends"
  ), 
    DATEADD("day", $days, [Date Valid]),

  //if letter has been sent but no deposit provided then due date is null
  AND(
	[Deposit Date Received] = NULL, 
    [Date Deposit Request Letter Sent] <> NULL
  ), 
    NULL, 

  $filter = "Excluding Weekends", 
    WORKDAY([Date Valid], $days),


  $filter = "Including Weekends", 
    DATEADD("day", $days, [Date Valid]), 


  NULL()
)

LET $numberInRow := $eventargs;
LET $process := IF( $numberInRow > ARRAY_LENGTH([This Stage Process List]), "Tracking", //fallback process
$numberInRow = NULL, ARRAY_LAST([This Stage Process List]),
ISNUMBER($numberInRow) = TRUE, ARRAY_INDEX([This Stage Process List], $numberInRow),
$eventargs );
LET $result := IF( ARRAY_INDEXOF([Visible Steps], $process) > 0, ARRAY_WHERE([Visible Steps], $(i, item) $item <> $process), ARRAY_CONCAT([Visible Steps], $process) );
RETURN IF( [Support Multiple Visible] = TRUE, ARRAY_WHERE( ARRAY_DISTINCT($result), $(i, item) $item <> NULL ), ARRAY($process) )
