Column.Formula:
LET $current := ARRAY([EFormType], [RecordID]);
LET $descriptionColumns := 
HASH(
	"Contract",  					"Contract Number", 
	"Vendor", 						"Company Name",
	"Procurement", 					"Procurement Number",
	"Project", 						"Project Number",
    "Evaluation", 		    		"Description",
  	"Information Request",  		"File No.", 
	"Subpoena", 					"File No.",
	"Privacy Incident", 			"File No."
); 

LET $altFormName :=
HASH(
	"Insurance",	"Compliance"
);

LET $getLabelText := $(eFormType, recordID)
(
  	LET $targetColumn := HASH_GET($descriptionColumns, $eFormType);
  	RETURN 
  	IF(
		$targetColumn = NULL, NULL,
		LET $isthisRecord := ARRAY($eFormType, $recordID) = $current;
		LET $title := 
		IF( $isthisRecord = TRUE, 
			CURRENT_VALUE($targetColumn),
			ARRAY_FIRST(DB_LOOKUP(
				ARRAY($targetColumn),
				$eFormType, 
				"[RecordID] FAST_EQUAL_TO " & $recordID
			))
		  );
		//shortern the descriptive label/ title
		LET $charCount := LEN($title);
		LET $titleArray := SPLIT($title, " ");
		LET $wordCount := ARRAY_LENGTH($titleArray);

		LET $charLimit := 30; 
		LET $wordLimit := ARRAY_LENGTH( SPLIT( LEFT($title, $charLimit), " ") );

	  RETURN //ARRAY_TAKE($titleArray, $wordLimit)
		IF(
			$wordCount <= $wordLimit, $title,
			ARRAY_JOIN(
				ARRAY_TAKE($titleArray, $wordLimit), " "
			)& " ..."
		)
	  )

); 

LET $getReadAccess := $(eFormType, recordID) (EBMS_GET_EFORMRECORD_PERMISSION($eFormType, $recordID, "read"));

LET $currentDes := CALL($getLabelText, [EFormType], [RecordID]);
LET $currentAccess := FALSE;

RETURN
IF(
	[Parent Structure] <> NULL, [Parent Structure],
  	(

		LET $parent := 
		IF( 
			[RecordID] > 0, 
			(
			LET $record := EBMS_GET_PARENT_RECORD(ARRAY_FIRST($current), ARRAY_INDEX($current, 2));
			RETURN

			IF(
				ARRAY_FIRST($record) == NULL,
				NULL,
				ARRAY_CONCAT( 
					$record, 
					CALL($getLabelText, ARRAY_FIRST($record), ARRAY_INDEX($record, 2)),
					CALL($getReadAccess, ARRAY_FIRST($record), ARRAY_INDEX($record, 2))
				)
			))
		);

		LET $gParent := 
		IF( 
			$parent <> NULL, 
			(
				LET $record := EBMS_GET_PARENT_RECORD(ARRAY_FIRST($parent), ARRAY_INDEX($parent, 2));
				RETURN

				IF( 
					ARRAY_FIRST($record) == NULL, NULL,
					ARRAY_CONCAT( 
						$record, 
						CALL($getLabelText, ARRAY_FIRST($record), ARRAY_INDEX($record, 2)),
						CALL($getReadAccess, ARRAY_FIRST($record), ARRAY_INDEX($record, 2))
					)
				)
			)
		); 

		LET $hash :=  
		HASH_MERGE(
			IF( $parent <> NULL, HASH("parent", 	$parent)),
			IF( $gParent <> NULL, HASH("gparent", 	$gParent))
		);

		LET $parentStructure := 	//2nd instance
		IF( 
			ARRAY_LENGTH(HASH_KEYS($hash)) >0, $hash, NULL
		);

		LET $parentData := 
		ARRAY_WHERE(
			ARRAY(
				$gParent, 
				$parent, 
				ARRAY_CONCAT($current, $currentDes, $currentAccess),
			), $(i,item) $item <> NULL
		);

		LET $parentChain :=
			ARRAY_SELECT(
				$parentData, $(i, item)
				ARRAY_TAKE($item, 2)
		);


		LET $parentLinks :=
		ARRAY_CONCAT(
			ARRAY(
			//Append Home to the front of the breadcrumb chain
			"<a style=""text-decoration-line: none;"" href=" & HTMLENCODE(EBMS_GET_BASE_URL()) & ">" &
				"<span style=""margin-left: 5px; background: #ffffff; color: #4D87B7;"">" & HTMLENCODE("Home") & "</span>" &
			"</a>",
			//gets the active Contract profile to use as the display name (e.g. Advanced, Lite)
			"<a style=""text-decoration-line: none;"" href=" & HTMLENCODE(EBMS_GET_BASE_URL()&"/records/Dashboard%20-%20Contract/new?view=Default") & ">" &
				"<span style=""margin-left: 5px; background: #ffffff; color: #4D87B7;"">" & HTMLENCODE("Contracts") & "</span>" & 
			"</a>"
			),

			ARRAY_SELECT(
				ARRAY_WHERE($parentData, $(p, parent) ARRAY_FIRST($parent) <> "Procurement"), $(i,item)
					LET $eFormType 		:= ARRAY_FIRST($item);
					LET $recordID 		:= ARRAY_INDEX($item, 2);
					LET $descriptiveLabel := ARRAY_INDEX($item, 3);
					LET $hasReadAccess	:= ARRAY_INDEX($item, 4);
					LET $URL	:= "records/" & URLENCODE($eFormType) & "/" & $recordID ;
					LET $showEFormType := $eFormType <> "Vendor";

					LET $labelColour := 
					IF(
						AND($eFormType = [EFormType], $recordID = [RecordID]),
						"background: #ffffff; color: #5D5D5D;",

						$hasReadAccess = TRUE,
						"background: #ffffff; color: #4D87B7;",

						"background: #ffffff; color: #5D5D5D;"
					);

					LET $labeltext := 
					IF(
						$showEFormType == TRUE,
						" " & COALESCE(HASH_GET($altFormName, $eFormType), $eFormType)
					) &
					IF(NOT(ISBLANK($descriptiveLabel)),
					" " & $descriptiveLabel);

					LET $label :=  
					"<span style=""margin-left: 5px;" & $labelColour & """>" &
					HTMLENCODE($labeltext) & 
					"</span>";

				RETURN
				IF( 
					$hasReadAccess == TRUE, 
					"<a style=""text-decoration-line: none;"" href=" & HTMLENCODE($URL) & ">" & $label & "</a>",
					$label 
				)
			)
		);

		LET $separator :=   "<svg style=""vertical-align: bottom; margin-left: 5px; height: 16px;"" xmlns=""http://www.w3.org/2000/svg"" viewBox=""0 0 256 512""><path fill=""#5D5D5D"" d=""M246.6 278.6c12.5-12.5 12.5-32.8 0-45.3l-128-128c-9.2-9.2-22.9-11.9-34.9-6.9s-19.8 16.6-19.8 29.6l0 256c0 12.9 7.8 24.6 19.8 29.6s25.7 2.2 34.9-6.9l128-128z""/></svg>";
		LET $breadcrumb := "<div style=""height: 23px; padding-left: 5px; font-size: 10pt; color: #5D5D5D; "">You are here:" & ARRAY_JOIN($parentLinks, $separator) & "</div>";

		RETURN 

		HASH(
			"Parent Structure",	$parentStructure,
			"Parent Data",		$parentData,
			"Parent Chain",		$parentChain,
			"Breadcrumb",		$breadcrumb
		)
	)
)

LET $cleanData := 
ARRAY_REVERSE(
  ARRAY_WHERE(
    [Notes], $(i, item) 
    ARRAY_INDEX($item, 3) <> NULL()
  )
);

LET $formatted := 
ARRAY_SELECT(
	$cleanData, $(i, item) 
	LET $user := ARRAY_INDEX($item, 1); 
	LET $datePosted := ARRAY_INDEX($item, 2); 
	LET $message := IFERROR(ARRAY_INDEX($item, 3), "");
	LET $attachments := ARRAY_INDEX($item, 4);
	LET $isSystem := ROLE("system") == $user;
	LET $isCurrentUser := EBMS_GET_CURRENT_USER() == $user;
	LET $userDisplayName := EBMS_GET_ROLE_DISPLAYNAME($user); 
	LET $datePostedFormatted := DATEFORMAT($datePosted, "dd-MMM-yyyy hh:mm tt");
	//LET $class := IF($isCurrentUser, "right-top", "left-top");
	LET $colour := IF($isCurrentUser, "#CBE8FF" , "#E2E2E2");
	LET $classes := 
    IF( 
	  $isSystem = TRUE, 
      "filenotes-system-message",
      "talk-bubble"
	);

    //----
    LET $username := "<span style=""font-weight: 600;"">" & HTMLENCODE($userDisplayName) & "</span>";
    LET $date := "<span style=""" & IF($isSystem = TRUE, "float: right; padding-right: 15px;", /*"font-style: italic;"*/) & """> " & $datePostedFormatted & " </span>";
    LET $message := "<span style=""white-space: break-spaces; display: block; padding-bottom: 10px; text-align: left; word-wrap: break-word;"">" & $message & "</span>";
    LET $noteAdded := "<span style='background-color: " & $colour & ";' class='filenotes-noteadded'>" & IF($isSystem = TRUE, "SYSTEM MESSAGE", "NOTE ADDED") & "</span>";
  	LET $delete := IF($isCurrentUser, "<span style='cursor: pointer; color: #404040;' data-clickid='" & $i & "'> (Delete) </span>");
    //----
    RETURN 
	IF( 
	  //System message
	  $isSystem = TRUE, 
	  "<div class=""" & $classes & """>" & $message & $noteAdded & $date & "</div>",

	  //User message
      "<div class=""" & $classes & """>" & $message & $noteAdded & $username & $date & $delete & "</div>"
	)
);

RETURN
IF(
  	[Navigation] == "File notes",
	IFERROR(
		ARRAY_JOIN($formatted, ""), 
		NULL()
	) & "<span class='endtag'></span>"
)

Binding.Expression:
LET $enabledRegisters := HASH_GET([Contract Module Settings], "Profile Supported Functions"); 

LET $forms := HASH_KEYS([Contract Event Hash]); 

LET $formToFunctionMap := 
HASH(
  "Action", "Actions", 
  "Communication Event", "Communication", 
  "Contract Review", "Contract Review", 
  "Defects Liability", "Defects Liability",
  "Defects Entry", "Defects Entry", 
  "KPI Performance", "Key Performance Indicators",
  "KPI Target", "Key Performance Indicators", 
  "Meeting", "Meetings", 
  "Milestone", "Milestones", 
  "Novation", "Novation", 
  "Payment Certificate", "Payment Certificates", 
  "Penalty Clause", "Penalty Clauses", 
  "Security", "Security", 
  "Service Level Agreement", "Service Level Agreements", 
  "Transition", "Transition",
  "Warranty", "Warranties", 
  "Extension", "Extensions", 
  "Variation", "Variations"  
); 

LET $forms := 
ARRAY_WHERE(
  $forms, $(i, form)
  LET $conversion := HASH_GET($formToFunctionMap, $form); 
  RETURN ARRAY_INDEXOF($enabledRegisters, $conversion) > 0
); 

LET $data := 
ARRAY_SELECT(
  $forms, 
  $(i, form) 
  ARRAY($form, HASH_GET([Contract Event Hash], $form))
);

LET $transformed := 
ARRAY_SELECT(
  $data, $(i, item)
  LET $form := ARRAY_INDEX($item, 1); 
  LET $dates := ARRAY_JOIN(ARRAY_INDEX($item, 2), ", ");
  RETURN 
  $form & ": " & $dates
);

LET $result := ARRAY_JOIN($transformed, NEWLINE());

RETURN $result

FieldEntry.Formula:
//Notes are displayed in reverse order, so we need to reverse it again to map the correct index
LET $displayValues := ARRAY_REVERSE([Notes]);
LET $noteToDelete := N($clickid);

//return the array of all notes except the one to be deleted
LET $newValues :=
ARRAY_WHERE(
  	$displayValues, $(i,item)
  	$i <> $noteToDelete
);

RETURN
//if there are no notes remaining, return NULL, otherwise re-reverse & return ARRAY(ARRAY(note1, note2...))
IF(
  	ARRAY_LENGTH($newValues) > 0,
  	ARRAY(ARRAY_REVERSE($newValues)),
  	NULL
)

Binding.Expression:
"<div style=""
    background: white;
	height: 100%;
    padding-left: 10px;
    overflow-x: hidden;
    overflow-y: auto;
	border: 1px solid #f4f4f4;
"">" & [Notes Processor] & "</div>"

FieldEntry.Formula:
LET $data := 
ARRAY_CONCAT(
	[Notes],
	ARRAY(
		ARRAY(
			EBMS_GET_CURRENT_USER(), 
			NOW(),
			"<p>" & HTMLENCODE([New Message]) & "</p>"
		)
	)
);

RETURN 
ARRAY_WHERE(
	$data, $(i,item) 
	AND(
		$item <> ARRAY(),
		$item <> NULL()
	)
)

