Column.DefaultValueFormula:
LET $modules := 
DB_LOOKUPALL(
	ARRAY(
		"Module Name", 
		"Module Settings EForm",
		"Module Home Page", 
		"Module Type",
		"Front Page Colour",
	    "Module View",
	  	"Module Access", //7
		"Message Enabled",
		"Message EFormType",
		"Message Aggregate Filter",
		"Message Text"
	), 
	"Global Settings", 
	"[Show on Main Entry] = TRUE AND [RecordID] = 1 AND [Module Name] IS NOT EMPTY AND [Module Name] <> 'Module Settings'"
);

LET $singleColour := 
DB_LOOKUP(
  "Single Colour", 
  "Global Settings", 
  "[RecordID] = 1 AND [Use Single Colour] = TRUE"
);

LET $rowColours := 
DB_LOOKUPALL(
  "Row Colour", 
  "Global Settings", 
  "[RecordID] = 1 AND [Use Row Colours] = TRUE AND [Row Colour] IS NOT EMPTY"
);

LET $data :=
ARRAY_SELECT(
	$modules, $(m, module)

	LET $moduleName 		:= 	ARRAY_INDEX($module, 1); 
	LET $moduleSettings 	:=	ARRAY_INDEX($module, 2); 
	LET $EFormName 			:=	ARRAY_INDEX($module, 3); 
	LET $accessType 		:=	ARRAY_INDEX($module, 4); 
	LET $primaryColour   	:= 	ARRAY_INDEX($module, 5);
	LET $view   			:= 	ARRAY_INDEX($module, 6);

	LET $moduleHomePage 	:=	
	IF( 
		$accessType = "Module", 
		"records/" & URLENCODE($EFormName) & "/new?view=" & URLENCODE($view),
		ARRAY_INDEX($module, 3)
	); 

	LET $accessList := ARRAY_INDEX($module, 7);

	LET $hasAccess :=
	ARRAY_LENGTH(
		ARRAY_WHERE(
			$accessList, $(i, item) 
			ARRAY_LENGTH(
				ARRAY_WHERE(
					$item, $(i, item) 
					EBMS_IS_USER_IN_ROLE(
						EBMS_GET_CURRENT_USER(), 
						$item
					)
				)
			) > 0
		)
	) > 0;

	LET $messageEnabled		:= ARRAY_INDEX($module, 8);
	LET $messageEform		:= ARRAY_INDEX($module, 9);
	LET $messageFilter		:= ARRAY_INDEX($module, 10);
	LET $messageText		:= ARRAY_INDEX($module, 11);

	LET $getMessage			:= $(form, filter, text)
	(
		LET $count:= 
		IFERROR(
			ARRAY_FIRST( ARRAY_FIRST( 
				DB_REPORT(
					"COUNT([RecordID])",
					$form,
					$filter
				)
			)), 0
		);
		RETURN IF( $count > 0, 	$count& " "& $text, NULL)
	);

	RETURN
	IF( 
		OR(
			$hasAccess == TRUE, 
			//AND(
			//  $accessType == "External",
			//  EBMS_IS_USER_IN_ROLE(EBMS_GET_CURRENT_USER(), "Everyone")
			//),
			AND(
				$EFormName == "Global Settings",
				EBMS_IS_USER_IN_ROLE(EBMS_GET_CURRENT_USER(), "Administrators")
			)			
		),
		ARRAY(
			$moduleName,
			$moduleHomePage,
			$moduleSettings,
			$primaryColour,
			//$messageEnabled,
			IF( 
				$messageEnabled <> TRUE, NULL,
				CALL($getMessage, $messageEform, $messageFilter, $messageText)
			)
		)
	)
);



LET $iconSize := 
IF( 
  EBMS_IS_USER_IN_ROLE(EBMS_GET_CURRENT_USER(), "Vendor Accounts"),
  3,

  COALESCE(
	EBMS_GET_PREFERENCE("Dashboard Icon Size"), 
	2
  )
); 

LET $perLine := 
IF( 
  $isMobile = TRUE, 
  1,

  OR(
    EBMS_IS_USER_IN_ROLE(EBMS_GET_CURRENT_USER(), "Vendor Accounts"),
	EBMS_GET_PREFERENCE("Full Width") = TRUE 
  ), 1,

  $iconSize = 1, 4, 

  $iconSize = 2, 3, 

  $iconSize = 3, 2, 

  2
); 

LET $result :=  
ARRAY_SELECT( 	
	ARRAY_WHERE(
		$data, $(i, item)
		$item <> NULL()
	), $(i, item) 
	LET $moduleName 	:= 	ARRAY_INDEX($item, 1); 
	LET $moduleHomepage 	:=	ARRAY_INDEX($item, 2); 
	LET $moduleSettings 	:=	ARRAY_INDEX($item, 3); 
	LET $message			:= ARRAY_INDEX($item, 5 );
	LET $primaryColour :=
    IF(
  	  AND($rowColours <> NULL(), $rowColours <> ARRAY()),

	  ( 
		LET $lenColours := ARRAY_LENGTH($rowColours);
		LET $rowNumber := CEILING($i / $perLine);
		LET $colourIndex := MOD($rowNumber - 1, $lenColours) + 1;
		LET $rowColour := ARRAY_INDEX($rowColours, $colourIndex);
	    RETURN $rowColour
   	  ),

      COALESCE($singleColour, ARRAY_INDEX($item, 4))
	); 

	RETURN
	ARRAY(
		$moduleName,
		$moduleHomepage, 
		IF(
		  EBMS_GET_PREFERENCE("Dashboard Dark Mode") = TRUE, "white", IFERROR($primaryColour, NULL())
	    ),
		$message
	)	
);

LET $result :=  
ARRAY_SELECT(
  RANGE(0, CEILING(ARRAY_LENGTH($data) / $perLine)), $(i, item) 
  LET $skip := ARRAY_SKIP($result, $perLine * ($i - 1)); 
  RETURN ARRAY_TAKE($skip, $perLine)
);

RETURN 
ARRAY_WHERE($result, $(i, item) $item <> ARRAY())

