  $data, $(i, item) 
  ARRAY(
    ARRAY_INDEX($item, 1), 
		HASH(
			"icon", REPLACESUBSTRING(".png", ARRAY_INDEX($item, 2), ""),
			"type", IF( RIGHT(ARRAY_INDEX($item, 2), 4) = ".png", "png", "svg")
		)	
  )
);

RETURN
ARRAY_TO_HASH(
	$data, 
	$(i, item) ARRAY_FIRST(ARRAY_TAKE($item, 1)),
	$(i, item) ARRAY_FIRST(ARRAY_SKIP($item, 1))
)

Column.DefaultValueFormula:
LET $showPerLine := 3;

LET $set := 
DB_LOOKUPALL(
	ARRAY(
		"Custom Link Text", 
		"Custom Link URL", 
		"Custom Link Visibility"
	), 
	"Global Settings", 
	"[RecordID] = 1 AND [Custom Link Text] IS NOT NULL"
);

LET $filtered := 
ARRAY_WHERE(
	$set, $(i, item) 
	ARRAY_LENGTH(
		ARRAY_WHERE(
			ARRAY_INDEX($item, 3), $(i, item)
			EBMS_IS_USER_IN_ROLE(
				EBMS_GET_CURRENT_USER(),
				$item
			)	
		)
	) > 0
);

LET $set := 
ARRAY_SELECT(
	$filtered, $(i, item) 
	ARRAY_TAKE($item, 2)
);

LET $lines := CEILING(ARRAY_LENGTH($set) / $showPerLine );

RETURN 
ARRAY_SELECT(
	RANGE(0, $lines), $(l, line)
	ARRAY_TAKE(ARRAY_SKIP($set, $showPerLine * ($l - 1)), $showPerLine)
)

LET $day := DAYOFWEEK(NOW());

LET $set := 
DB_LOOKUPALL(
  ARRAY("Name", "Birthday"),
  "Birthday",
  "[LineNumber] FAST_EQUAL_TO 1 AND [Disable] <> TRUE", 
  HASH("sort", "[Name] ASC")
);

LET $today := DATE(YEAR(NOW()), MONTH(NOW()), DAY(NOW())); 

LET $matches := 
ARRAY_WHERE(
	$set, $(i, item) 
	LET $date := ARRAY_INDEX($item, 2);
    	LET $date := DATE(YEAR(NOW()), MONTH($date), DAY($date));

	RETURN 
    	IF( 
		  $day > 2, /*After Monday - matches today*/ 
		  $date = $today,

		  $day = 2, /*Monday - matches today, or yesterday or day before (capturing weekend birthdays)*/
		  OR(
			$date = $today,
			$date = DATEADD("day", -1, $today),
			$date = DATEADD("day", -2, $today)
		  ),

		  $day = 1, /*Sunday - matches today, or yesterday (capturing Sunday with Saturday)*/
		  OR(
			$date = $today,
			$date = DATEADD("day", -1, $today)
		  )
		)
);

RETURN ARRAY_LENGTH($matches) > 0

LET $filter := "[Subject] IS NOT EMPTY AND [Announcement Date] <= " & DB_ENCODECONSTANT(NOW()) & " AND " & "[End Date] >= " & DB_ENCODECONSTANT(NOW()) & " AND " & "[Remove from Dashboard] <> TRUE";
LET $result := DB_LOOKUPALL( ARRAY( "Subject", "Announcement Date", "RecordID" ), "Announcement", $filter );
RETURN $result

