Column.DefaultValueFormula:
LET $form := "Birthday";
LET $name := "Name";
LET $date := "Birthday";

LET $set := 
IF( 
  AND(
	$form <> NULL(),
	$name <> NULL(),
	$date <> NULL(),
  ), 	
  DB_LOOKUPALL(
	ARRAY($name, $date),
	$form,
	"[LineNumber] FAST_EQUAL_TO 1 AND [Disable] <> TRUE", 
	HASH("sort", "[Name] ASC")
  )
);


LET $day := DAYOFWEEK(NOW());
LET $today := DATE(YEAR(NOW()), MONTH(NOW()), DAY(NOW()));

LET $matches := 
ARRAY_WHERE(
	$set, $(i, item) 
	LET $date := ARRAY_INDEX($item, 2);
    	LET $date := DATE(YEAR(NOW()), MONTH($date), DAY($date));

	RETURN 
    	IF( 
		  $day > 2, /*After Monday - matches today*/ 
		  $date = $today,

		  $day = 2, /*Monday - matches today, or yesterday or day before (capturing weekend birthdays)*/
		  OR(
			$date = $today,
			$date = DATEADD("day", -1, $today),
			$date = DATEADD("day", -2, $today)
		  ),

		  $day = 1, /*Sunday - matches today, or yesterday (capturing Sunday with Saturday)*/
		  OR(
			$date = $today,
			$date = DATEADD("day", -1, $today)
		  )
		)
);

LET $names := ARRAY_SELECT(
	$matches, $(i, item)
	ARRAY_INDEX($item, 1)
    //First Name Only -- ARRAY_FIRST(SPLIT(ARRAY_INDEX($item, 1), " "))
);

LET $hashed := 
ARRAY_TO_HASH_GROUPED(
  $matches, 
  $(i, item) ARRAY_INDEX($item, 2), 
  $(i, item) ARRAY_INDEX($item, 1)
);

LET $result := 
ARRAY_SELECT(
	HASH_KEYS($hashed), $(k, key)
	ARRAY(
	  $key, 
	  HASH_GET($hashed, $key)
	)
);

RETURN $result

