Column.Formula:
LET $userEmailAddresses := 
ARRAY_JOIN(ARRAY_SELECT(ARRAY_WHERE(
  ARRAY_TRANSPOSE(
  ARRAY(
    EACH [Invitee Email Address],
    EACH [Invitee is System User]
  )
), $(i, item) ARRAY_INDEX($item, 2) = TRUE), $(i, item) ARRAY_INDEX($item, 1)), ", ");

LET $internalOnly := 
IF( 
	[Notify Internal Users] = TRUE,
	$userEmailAddresses
);

LET $inviteeRoleEmailAddresses := 
IF( 
	[Notify all Invited Users] = TRUE,
	EACH [Invitee Email Address], 
);

LET $additionalRoleEmailAddresses := 
ARRAY_SELECT(
	[Minutes Notify Additional Users], $(i,item) 
	EBMS_GET_ROLE_EMAIL_ADDRESSES($item)
);

LET $custom := ARRAY_SELECT(SPLIT([Custom Email Addresses], ","), $(i, item) TRIM($item));

LET $combined := 
ARRAY_CONCAT(
	$internalOnly, 
	$inviteeRoleEmailAddresses,
	$additionalRoleEmailAddresses, 
	$custom
);


LET $cleaned := 
ARRAY_WHERE(
  $combined, $(i, item) 
  NOT(ISBLANK($item))
); 

RETURN 
ARRAY_JOIN(ARRAY_DISTINCT($cleaned), ", ")

LET $result := 
ARRAY_SELECT(
  	[Parent Chain], $(i, item) 

	LET $trim := $i < $len - 2;

	LET $EFormType := ARRAY_FIRST($item);
	LET $RecordID := ARRAY_LAST($item);

	LET $id := 
	IF(
	  	$EFormType = "Project", 
	  	$RecordID
	);

	RETURN $id
  );
RETURN 
ARRAY_FIRST(ARRAY_WHERE($result, $(i, item) $item <> NULL()))

