LET $period := [Frequency]; 

LET $period := 
IF( 
  [Frequency Measure] = "week", 
  $period * 7, 
  $period
); 

LET $measure := 
IF( 
  [Frequency Measure] = "week", 
  "day", 
  [Frequency Measure]
); 

LET $dailyWeekly :=
IF( 
  [Daily Weekly Repeat] = TRUE, 
  (
	LET $daysHash := 
	HASH(
		1, ARRAY([Monday Time], [Monday End Time], [Recurring Meeting - Monday]), 
		2, ARRAY([Tuesday Time], [Tuesday End Time], [Recurring Meeting - Tuesday]),  
		3, ARRAY([Wednesday Time], [Wednesday End Time], [Recurring Meeting - Wednesday]),  
		4, ARRAY([Thursday Time], [Thursday End Time], [Recurring Meeting - Thursday]), 
		5, ARRAY([Friday Time], [Friday End Time], [Recurring Meeting - Friday]), 
		6, ARRAY([Saturday Time], [Saturday End Time], [Recurring Meeting - Saturday]),  
		0, ARRAY([Sunday Time], [Sunday End Time], [Recurring Meeting - Sunday])
	);

	LET $today := TODAY(NOW());
	LET $dayOfWeek := DAYOFWEEK($today);

	LET $targetDays := 
	ARRAY_WHERE(
		ARRAY_SELECT(
			HASH_KEYS($daysHash), $(i, item) 
			LET $set := HASH_GET($daysHash, $item);
			RETURN 
			IF(
				ARRAY_INDEX($set, 3) = TRUE, 
				ARRAY(
					$item, 
					ARRAY_INDEX($set, 1), // start time
					ARRAY_INDEX($set, 2) // end time
				)
			)				
		), $(i, item) $item <> NULL()
	);

	LET $getTime := $(targetDay, targetTime) 
	(
		DATE(YEAR($targetDay), MONTH($targetDay), DAY($targetDay), HOUR($targetTime), MINUTE($targetTime))
	); 

	LET $getDate := $(targetDay, targetTime)(
		LET $date1 := CALL($getTime, DATEADD("day", $targetDay - $dayOfWeek + 1, $today), $targetTime); 
		LET $date2 := CALL($getTime, DATEADD("day", $targetDay - $dayOfWeek + 1 + 7, $today), $targetTime); 

		LET $date := IF(OR($date1 < NOW(), $date1 <= [Meeting Date]), $date2, $date1); 
		RETURN $date
	);



	RETURN 
	LEAST(
	  ARRAY_SELECT(
		$targetDays, $(i, item) 
		CALL($getDate, ARRAY_INDEX($item, 1), ARRAY_INDEX($item, 2))
	  )
	)
  )
);

RETURN 
IF( 
  [Recurring Meeting Created] = TRUE, 
  	[Frequency - Next Meeting Date],

  [Daily Weekly Repeat] = TRUE,
  	$dailyWeekly,

  [Frequency Type] = "Follow-up Meeting", 
  	[Frequency - Next Meeting Date],

  DATEADD($measure, $period, [Meeting Date])
)

ARRAY_JOIN(
  ARRAY_WHERE(
	SPLIT([Notify Email Addresses], ", "), $(i, item)
	FIND("ebms.com.au", $item) > 0
  ), ", "
)

