  IF( 
	$hours > 1, 
		$hours & "hrs",
	$hours = 1,	 
		$hours & "hr"
  ),
  IF( 
	$minutes >= 1, 
		$minutes & "m"
  )
);

LET $joined := 
ARRAY_JOIN(ARRAY_WHERE($string, $(i, item) $item <> NULL()), " ");

RETURN COALESCE($joined, "Not specified")

LET $hash := HASH( "Meeting Title", [Meeting Title], "Meeting Objective", [Meeting Objective], "Meeting Date", [Meeting Date], "Meeting Date End", [Meeting Date End], "Location", [Location], "Invitees", EACH [Invitee Name], "Invitees Email Addresses", [Invitee Email Address], "Meeting App", [Online Meeting] );
LET $changed := ARRAY_SELECT( HASH_KEYS($hash), $(i, key) LET $newVal := HASH_GET($hash, $key); LET $storedVal := HASH_GET([Calendar Details Captured], $key);
RETURN IF( AND( $newVal != $storedVal, OR($key = "Meeting Date", $key = "Meeting Date End") ), $key & NEWLINE() & " Old value '" & DATEFORMAT($storedVal, "dddd hh:mm tt MMM dd yyyy") & "'" & NEWLINE() & " New value '" & DATEFORMAT($newVal, "dddd hh:mm tt MMM dd yyyy") & "'",
$newVal != $storedVal, $key & NEWLINE() & " Old value '" & $storedVal & "'" & NEWLINE() & " New value '" & $newVal & "'" ) );
RETURN IF( [Calendar Details Captured] <> NULL(), ARRAY_JOIN(ARRAY_WHERE($changed, $(i, item) $item <> NULL()), NEWLINE() & NEWLINE()) )

LET $hash := 
HASH(
	"Meeting Title", [Meeting Title],
	"Meeting Objective", [Meeting Objective],
	"Meeting Date", [Meeting Date],
	"Meeting Date End", [Meeting Date End],
	"Location", [Location],
	"Invitees", ARRAY_WHERE(EACH [Invitee Name], $(i, item) [Calendar Invite]#$i = TRUE),
	"Invitees Email Addresses", ARRAY_WHERE(EACH [Invitee Email Address], $(i, item) [Calendar Invite]#$i = TRUE)
);

LET $translation := 
HASH(
  "Meeting Date", "Start time", 
  "Meeting Date End", "End time"
);

RETURN 
ARRAY_JOIN(
  ARRAY_SELECT(
	HASH_KEYS($hash), $(i, key) 
	LET $translatedName := COALESCE(HASH_GET($translation, $key), $key); 
	LET $value := HASH_GET($hash, $key);
	LET $formattedValue := 
	IF( 
		OR($translatedName = "Start time", $translatedName = "End time"),   
		DATEFORMAT($value, "dddd hh:mm tt MMM dd yyyy"), 
	    $value
	); 

	RETURN 

	$translatedName & " - " & ARRAY_JOIN($formattedValue, ", ") & NEWLINE()
  ), 
  ""
)

