LET $custom := ARRAY_SELECTMANY( $recordIDs, $(i, recordID) ( LET $files := ARRAY_SELECT( EBMS_GET_ATTACHMENTS("Mail Merge Template", $recordID, "Mailmerge"), $(i, item) HASH( "FileName", HASH_GET($item, "FileName"), "RecordID", $recordID, "Type", "Custom" ) );
RETURN $files ) );
