HttpRequest.UrlTemplate:
{
	IF(ISBLANK([Calendar Event ID]),
	  "https://graph.microsoft.com/v1.0/users/" & [Meeting Called By Email] & "/calendar/events",
	  "https://graph.microsoft.com/v1.0/users/" & [Meeting Called By Email] & "/calendar/events/" & [Calendar Event ID]
	)
}

HttpRequest.BodyTemplate:
{
LET $demoMode := DB_LOOKUP("Demo Mode", "Global Settings", "[RecordID] = 1");

LET $attendees :=
IF(
  $demoMode = TRUE, 
	ARRAY(
	  ARRAY(
	    EBMS_GET_ROLE_DISPLAYNAME(EBMS_GET_CURRENT_USER()),
	    EBMS_GET_ROLE_EMAIL_ADDRESSES(EBMS_GET_CURRENT_USER())
      )
	), 

 ARRAY_WHERE(
   ARRAY_TRANSPOSE(
     ARRAY(
  	   EACH [Invitee Name], 
  	   EACH [Invitee Email Address],
  	   EACH [Calendar Invite]
  	 )
    ), $(i, item) 
    ARRAY_INDEX($item, 3) = TRUE
  )
);

LET $result :=  
JSONENCODE(
	LET $required := HASH(
		"subject", [Meeting Title],
		"body", HASH(
			"contentType", "HTML",
			"content", "<p>" & [Meeting Objective] & "</p><p>" & IF([Online Meeting] = "zoom", "Join Zoom Meeting: " & [Zoom Meeting URL]) & "</p>"
		),
		"start", HASH("dateTime", [Meeting Date], "timeZone", "Etc/GMT+12"),
		"end", HASH("dateTime", [Meeting Date End], "timeZone", "Etc/GMT+12"),
		"location", HASH(
			"displayName", [Location]
		),
		"attendees", 
	  	ARRAY_SELECT($attendees, $(i, item)
			HASH(
				"emailAddress", 
			    HASH(
					"address", ARRAY_INDEX($item, 2),
					"name", ARRAY_INDEX($item, 1)
				),
				"type", "required"
			)
		)
	);

	LET $onlineMeeting := IF([Online Meeting] <> "None",
		IF([Online Meeting] = "teamsForBusiness",
			HASH(
				"isOnlineMeeting", TRUE,
				"onlineMeetingProvider", [Online Meeting]
			)
	  	),
		NULL()
	);

	RETURN HASH_MERGE($required, $onlineMeeting)
);

RETURN $result
}

FormulaDrivenRaiseLinkedEForm.FieldValuesFormula:
LET $columns := ARRAY( "Access", "Agenda - Duration / Time", "Agenda - Speaker", "Agenda - Topic", "Agenda Total Hours", "Agenda Total Minutes", "Agenda Item - Hours", "Agenda Item - Minutes", "Agenda Item Start Time", "Attendee", "Comments", "Conference Call Number", "External Invitee Name", "Frequency", "Frequency - Next Meeting Date", "Frequency - Next Meeting Date Flattened", "Frequency Measure", "Frequency Type", "Invited Count", "Invitee", "Invitee Email Address", "Invitee is System User", "Invitee Name", "Invitees", "Location", "Meeting Called By", "Meeting Date", "Meeting Duration - Hours", "Meeting Duration - Minutes", "Meeting Invitees - Manual", "Meeting Invitees - Users", "Meeting Objective", "Meeting Title", "Optional Invitees", "Type of Meeting",
"Daily Weekly Repeat", "Recurring Meeting - Monday", "Recurring Meeting - Tuesday", "Recurring Meeting - Wednesday", "Recurring Meeting - Thursday", "Recurring Meeting - Friday", "Recurring Meeting - Saturday", "Recurring Meeting - Sunday", "Monday Time", "Monday End Time", "Tuesday Time", "Tuesday End Time", "Wednesday Time", "Wednesday End Time", "Thursday Time", "Thursday End Time", "Friday Time", "Friday End Time", "Saturday Time", "Saturday End Time", "Sunday Time", "Sunday End Time" );
LET $values := ARRAY_SELECT( $columns, $(i, column) IF( $column = "Frequency - Next Meeting Date", GREATEST([Frequency - Next Meeting Date], DATEADD("day", 1, NOW())),
CURRENT_EACHVALUE($column) ) );
LET $previousMeetings := IF( [Previous Meetings] = NULL(), ARRAY(ARRAY([RecordID])), ARRAY(ARRAY_CONCAT([Previous Meetings], [RecordID])) );
LET $newValues := HASH( "Root Meeting RecordID", COALESCE([Root Meeting RecordID], [RecordID]), "Previous Meeting", [RecordID], "Previous Meetings", $previousMeetings, "Meeting Date", ARRAY([Frequency - Next Meeting Date]), "Is Follow-up Meeting", ARRAY(TRUE), "Send Agenda on Meeting Date", ARRAY(TRUE) );
RETURN HASH_MERGE( INDEXES_TO_KEYS( $values, $columns ), $newValues )

